interface IMasterBELT {
    struct UserInfo {
        uint256 shares;
        uint256 rewardDebt;
    }

    struct PoolInfo {
        IERC20 want;
        uint256 allocPoint;
        uint256 lastRewardBlock;
        uint256 accBELTPerShare;
        address strat;
    }
    
    function deposit(uint256 _pid, uint256 _wantAmt) external;
    function withdraw(uint256 _pid, uint256 _wantAmt) external;
    function pendingBELT(uint256 _pid, address user) external view returns (uint256);
    function poolInfo(uint256 idx) external view returns (PoolInfo memory);
    function userInfo(uint256 idx, address user) external view returns (UserInfo memory);
    function totalAllocPoint() external view returns (uint256);
    function BELT() external view returns (address);
