contract CompensationPool is ERC20, Ownable, ReentrancyGuard {
    
    address public token4Belt;
    address public remedy4Belt;
    address public constant masterBELT = 0xD4BbC80b9B102b77B21A06cb77E954049605E6c1;
    
    uint public lastMined;
    uint public miningIndex;
    
    uint public pid4Belt = 3;
    uint public pidRemedy;
    uint public totalStaked;
    uint public totalReward4BELT;
    uint public teamAllocationFunds;

    mapping(address => uint) public userLastIndex;
    mapping(address => uint) public userRewardSum;
    mapping(address => uint) public staked;
    
    event AddFund(uint256 amount);
    event UpdateMiningIndex(uint lastMined, uint miningIndex);
    event GiveReward(address user, uint amount, uint lastIndex, uint rewardSum);

    constructor(string memory name_, string memory symbol_, address _token4BELT, address _tokenr4BELT) ERC20(name_, symbol_) public {
        token4Belt =  _token4BELT;
        remedy4Belt = _tokenr4BELT;

        _mint(address(this), 1 ether);
    }
    
    function init(uint256 _pid) public onlyOwner {
        require(pidRemedy == 0, "require: already initialized");
        
        IMasterBELT.PoolInfo memory poolInfo = IMasterBELT(masterBELT).poolInfo(_pid);
        require(address(poolInfo.want) == address(this));

        pidRemedy = _pid;
        
        _approve(address(this), masterBELT, 1 ether);
        IMasterBELT(masterBELT).deposit(pidRemedy, 1 ether);
        
        updateMiningIndex();
    }
    
    function deposit(uint256 amount) public nonReentrant {
        require(IERC20(token4Belt).transferFrom(msg.sender, address(this), amount), "require: 4Belt TransferFrom Failed");
        require(IERC20(remedy4Belt).transferFrom(msg.sender, address(this), amount), "require: r4Belt TransferFrom Failed");
        
        stake4BELT(amount);
        
        giveReward(msg.sender);
        
        totalStaked = totalStaked.add(amount);
        
        staked[msg.sender] = staked[msg.sender].add(amount);
    }
    
    function withdraw(uint256 amount) public nonReentrant {
        require(staked[msg.sender] >= amount, "require: Insufficient Staked Amount");
        
        unstake4BELT(amount);
        
        giveReward(msg.sender);
        
        totalStaked = totalStaked.sub(amount);
        
        staked[msg.sender] = staked[msg.sender].sub(amount);
        
        require(IERC20(token4Belt).transfer(msg.sender, amount), "require: 4Belt Transfer Failed");
        require(IERC20(remedy4Belt).transfer(msg.sender, amount), "require: r4Belt Transfer Failed");
    }

    receive() payable external { revert(); }
    
    function minedBELT() private view returns (uint) {
        IMasterBELT masterBelt = IMasterBELT(masterBELT);
        // 아직 안 받은 BELT + 이제까지 받아간 BELT
        return masterBelt.pendingBELT(pid4Belt, address(this))
                .add(totalReward4BELT)
                .add(masterBelt.pendingBELT(pidRemedy, address(this)))
                .add(masterBelt.userInfo(pidRemedy, address(this)).rewardDebt)
                .add(teamAllocationFunds);
    }
    
    function addFund(uint256 amount) public nonReentrant {
        require(amount > 0, "require: funds must be bigger than 0");
        
        IERC20 BELT = IERC20(IMasterBELT(masterBELT).BELT());
        
        uint256 beforeBal = BELT.balanceOf(address(this));
        
        require(BELT.transferFrom(msg.sender, address(this), amount), "require: BELT TransferFrom Failed");
        
        uint256 afterBal = BELT.balanceOf(address(this));
        
        require(afterBal.sub(beforeBal) == amount, "require: insufficient sender BELT Balance");
        
        teamAllocationFunds = teamAllocationFunds.add(amount);
        
        updateMiningIndex();
        
        emit AddFund(amount);
    }

    function getMiningIndex() private view returns (uint) {
        uint mined = minedBELT();

        if (mined > lastMined) {
            uint thisMined = mined.sub(lastMined);
            
            if (thisMined != 0 && totalStaked != 0) {
                return miningIndex.add(thisMined.mul(10 ** 18).div(totalStaked));
            }
        }

        return miningIndex;
    }

    function updateMiningIndex() public {
        uint mined = minedBELT();

        if (mined > lastMined) {
            uint thisMined = mined.sub(lastMined);

            lastMined = mined;
            
            if (thisMined != 0 && totalStaked != 0) {
                miningIndex = miningIndex.add(thisMined.mul(10 ** 18).div(totalStaked));
            }

            emit UpdateMiningIndex(lastMined, miningIndex);
        }
    }
    
    function stake4BELT(uint256 amount) private {
        IMasterBELT masterBelt = IMasterBELT(masterBELT);
        IERC20 BELT = IERC20(masterBelt.BELT());

        uint beforeBal = BELT.balanceOf(address(this));
        
        IERC20(token4Belt).approve(masterBELT, amount);
        masterBelt.deposit(pid4Belt, amount);
        
        uint afterBal = BELT.balanceOf(address(this));
        
        require(afterBal >= beforeBal);
        
        totalReward4BELT = totalReward4BELT.add(afterBal).sub(beforeBal);
        
        updateMiningIndex();
    }
    
    function unstake4BELT(uint256 amount) private {
        IMasterBELT masterBelt = IMasterBELT(masterBELT);
        IERC20 BELT = IERC20(masterBelt.BELT());

        uint beforeBal = BELT.balanceOf(address(this));
        
        masterBelt.withdraw(pid4Belt, amount);
        
        uint afterBal = BELT.balanceOf(address(this));
        
        require(afterBal >= beforeBal);
        
        totalReward4BELT = totalReward4BELT.add(afterBal).sub(beforeBal);
        
        updateMiningIndex();
    }
    
    function giveReward(address user) private {
        uint lastIndex = userLastIndex[user];
        uint currentIndex = getMiningIndex();

        uint have = staked[user];

        if (currentIndex > lastIndex) {
            userLastIndex[user] = currentIndex;

            if (have != 0) {
                uint amount = have.mul(currentIndex - lastIndex).div(10 ** 18);
                
                IMasterBELT(masterBELT).withdraw(pidRemedy, 0);
           
                require(IERC20(IMasterBELT(masterBELT).BELT()).transfer(user, amount));

                userRewardSum[user] = userRewardSum[user].add(amount);
                emit GiveReward(user, amount, currentIndex, userRewardSum[user]);
            }
        }
    }
    
    function pendingBELT(address user) public view returns (uint) {
        return staked[user].mul(getMiningIndex().sub(userLastIndex[user])).div(10 ** 18);
    }

    function harvest() public nonReentrant {
        unstake4BELT(0);
        giveReward(msg.sender);
    }
}
