        return (holderBonus[_index], mintedDocCoinEarning[_index]);
    }

    function getUserClaimReward(uint _index) public view returns (uint256,uint256) {
        return (mintedDocMaticBonus[_index], mintedDocCoinEarning[_index]);
    }

