        return (refererBonus[addr], refererClaimed[addr]);
    }
    

  // public functions

    function mintDocument(DocMinting memory _mintDoc, uint amount) external {
    uint256 supply = totalSupply();
    uint256 amountSend = amount;
    uint256 costOfMint = 0;
    uint256 mintBonus=0;
    
    if(amountSend==cost){
        costOfMint = cost;
        mintBonus = type1MintBonus;
    }
    else{
        costOfMint = cost1;
        mintBonus = type2MintBonus;
    }
    uint256 id = supply + 1;
    require(!paused, "Minting stopped!");
    require(amountSend >= costOfMint, "amount send is low, You can't mint!");

    _safeMint(msg.sender, id);
    BUSD.transferFrom(msg.sender, address(this), costOfMint);
    count++;
    countDividend++;
    address referral = _mintDoc.referral;
    address userAddress = _mintDoc.userAddress;
    string memory fileURL = _mintDoc.fileURL;
    uint gradeType = 1;
    uint256 validityPeriod = _mintDoc.validityPeriod;
    uint256 dateRegister = _mintDoc.dateRegister;
    bool approved = false;
    uint visibility = _mintDoc.visibility;
    mintedDocCoinEarning[id - 1] = 0;
    mintedDocMaticBonus[id - 1] = 0;
    //mintedDocCoinEarning
    mintType[id - 1] = mintBonus;
    allHolders.push(userAddress);
    numberOfNFT[userAddress]++;
    claimNum[id - 1] = countDividend;
          uint getStakerShare = (stakerShare * costOfMint) / 100;
          
          uint getMarketerShare = (marketerShare * costOfMint) / 100;
          holdersPoolReward += getMarketerShare; 
          if(numberOfNFT[userAddress] <= 2){
          refererBonus[referral] += getStakerShare;
          }
          allPartners.push(referral);
          adminBalance += costOfMint -  (getStakerShare); //((marketerShare + stakerShare) * msg.value) / 100;
        mintDocuments.push(DocMinting(id, fileURL, userAddress, referral, gradeType, validityPeriod, dateRegister, approved, visibility));    
        totalDistribute += getStakerShare;
        lastTokenDistributed = getStakerShare;
        lastDistributedTime = dateRegister;
        if(countDividend <= 1){
            dividendNum[id] = getStakerShare / (countDividend);
        }
        else{
            dividendNum[id] = getStakerShare / (countDividend - 1);
        }
        
        // dividendSellValue[countDividend] = totalForSell;
        
    emit MintDocEvent(id, fileURL, userAddress, gradeType, validityPeriod, dateRegister, approved, visibility);
    
    }
  // Update from the mintDoc objects list
    function updateVisibility(uint256 _index, uint _visibility) external {
            mintDocuments[_index].visibility = _visibility;
            require(mintDocuments[_index].userAddress != address(0) && mintDocuments[_index].userAddress == msg.sender,"You are qualify for this");
            uint256 ctime = block.timestamp;
            
            emit updateMintedDocEvent(_index, _visibility, ctime);
    }

    // Update from the mintDoc objects list
    function payForUpgrade(uint256 _index, uint _amount) external {
            require(_amount >= gradeACharges,"Insufficient payment");
            BUSD.transferFrom(msg.sender, address(this), _amount);
            gradeAMinting[_index] = 1;
            emit payForGradeAEvent(_index, _amount, block.timestamp);
    }

    // Update from the mintDoc objects list
    function updateDOCType(uint256 _index, uint _type) external onlyOwner{
            mintDocuments[_index].gradeType = _type;
            emit updateMintedDocEvent(_index, _type, block.timestamp);
    }

  // Approve user profile for verification
    function approveMintDoc(uint256 _index) public verificationPartnerOnly {
        DocMinting storage _mintDoc = mintDocuments[_index];
        _mintDoc.approved = true;
    }

    // Unhide to enable displaying of user profile
    function unhideMintDoc(uint256 _index, uint _action) public {
        require(verificationPartner[msg.sender]==true || msg.sender== mintDocuments[_index].userAddress, "Only admin and profile owner can unhide the profile");
        mintDocuments[_index].visibility = _action;
    }

    // function displaying of user profile details
    function displaymintDoc(uint256 _index) public view returns( string memory, address, uint256, uint256 , uint256, bool, uint256){
        DocMinting storage _mintDoc = mintDocuments[_index];
        uint isvisibility = _mintDoc.visibility;
        //keccak256(abi.encodePacked(msg.sender)) == keccak256(abi.encodePacked(_mintDoc.userAddress));
        require(isvisibility==1 || msg.sender== _mintDoc.userAddress, "The user profile protected can't be viewed by public!");
        return (_mintDoc.fileURL, _mintDoc.userAddress, _mintDoc.gradeType, _mintDoc.validityPeriod, _mintDoc.dateRegister, _mintDoc.approved, _mintDoc.visibility);
    }

  function walletOfOwner(address _owner)
    public
    view
    returns (uint256[] memory)
  {
    uint256 ownerTokenCount = balanceOf(_owner);
    uint256[] memory tokenIds = new uint256[](ownerTokenCount);
    for (uint256 i; i < ownerTokenCount; i++) {
      tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
    }
    return tokenIds;
  }

  function tokenURI(uint256 tokenId)
    public
    view
    virtual
    override
    returns (string memory)
  {
    require(
      _exists(tokenId),
      "ERC721Metadata: URI query for nonexistent token"
    );
    
    if(revealed == false) {
        return notRevealedUri;
    }

    string memory currentBaseURI = _baseURI();
    return bytes(currentBaseURI).length > 0
        ? string(abi.encodePacked(currentBaseURI, tokenId.toString(), baseExtension))
        : "";
  }

