function getTodayProfit(uint _nid) public  view returns (uint, uint){
        uint lastNumClaim = claimNum[_nid];
        uint dividentAmount = 0;
        uint result =0;
        if(lastNumClaim < totalSupply()){
            
            uint lastDividendCount= 0;
            uint countC = 0;
            for(uint i= (lastNumClaim+1); i <= totalSupply(); i++){
             dividentAmount = dividendNum[i]; // - dividendNum[lastNumClaim];
             if(dividentAmount > 0){
             result += dividentAmount;
             lastDividendCount = i;
             countC++;
             if(countC >= 100){
                 break;
             }
             }
            }
            return (result, lastDividendCount);
           
         }
           
         else{
             return (result,totalSupply());
         }
        
    }
  function claimMatic(uint256 _index) public {
    // This will pay HashLips 5% of the initial sale.
    // You can remove this if you want, or keep it in to support HashLips and his channel.
    // =============================================================================
    (uint amt, uint lastSupply) = getTodayProfit(_index);
    require(mintType[_index] >= (mintedDocMaticBonus[_index]), "you have reached your claim limit");
    require(mintDocuments[_index].userAddress != address(0)  && mintDocuments[_index].userAddress == msg.sender,"You are not qualify for this transaction!");
    address rewardAddress = mintDocuments[_index].userAddress;
    BUSD.transfer(rewardAddress, amt);
    mintedDocMaticBonus[_index] += amt;
    totalBusdClaimed += amt;
    claimNum[_index] = lastSupply;
    // =============================================================================
  }

  function claimRegiz(uint256 _index, uint256 amt) public {
    // This will pay HashLips 5% of the initial sale.
    // You can remove this if you want, or keep it in to support HashLips and his channel.
    // =============================================================================
    require(mintType2[_index] > (mintedDocCoinEarning[_index] + mintedDocCoinClaimed[_index]), "You have reached your claim limit");
    require(mintDocuments[_index].userAddress != address(0) && mintDocuments[_index].userAddress == msg.sender,"You are qualify for this");
    token.transfer(mintDocuments[_index].userAddress,amt);
    mintedDocCoinEarning[_index] -= amt;
    mintedDocCoinClaimed[_index] +=amt;
    
    // =============================================================================
  }

