contract Ownable {
    address private _owner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    /**
     * @dev The Ownable constructor sets the original `owner` of the contract to the sender
     * account.
     */
    constructor() {
        _owner = msg.sender;
        emit OwnershipTransferred(address(0), _owner);
    }

    /**
     * @return the address of the owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(isOwner());
        _;
    }

    /**
     * @return true if `msg.sender` is the owner of the contract.
     */
    function isOwner() public view returns (bool) {
        return msg.sender == _owner;
    }

    /**
     * @dev Allows the current owner to relinquish control of the contract.
     * @notice Renouncing to ownership will leave the contract without an owner.
     * It will not be possible to call the functions with the `onlyOwner`
     * modifier anymore.
     */
    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    /**
     * @dev Allows the current owner to transfer control of the contract to a newOwner.
     * @param newOwner The address to transfer ownership to.
     */
    function transferOwnership(address newOwner) public onlyOwner {
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers control of the contract to a newOwner.
     * @param newOwner The address to transfer ownership to.
     */
    function _transferOwnership(address newOwner) internal {
        require(newOwner != address(0));
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

interface ITokenData {
    function pair() external view returns (address);

    function token() external view returns (address);

    function usdt() external view returns (address);

    function wbnb() external view returns (address);

    function router() external view returns (address);

    function pledge() external view returns (address);

    function mining() external view returns (address);

    function market() external view returns (address);

    function users(address user) external view returns (uint8);

    function openBuy() external view returns (bool);

    function openSell() external view returns (bool);

    function buyFeeRate() external view returns (uint256);

    function sellFeeRate() external view returns (uint256);

    function createPair(address token) external;

    function sellFeeReceiver() external view returns (address);

    function buyFeeReceiver() external view returns (address);

    function directTransfer(
        address from,
        address to
    ) external view returns (bool);

    function validBuy(address user, uint256 amount) external;

    function validBind(
        address from,
        address to,
        uint256 amount
    ) external view returns (bool);

    function minRewardFdAmount() external view returns (uint256);

    function minRewardLpAmount() external view returns (uint256);
}

interface IFeeReceiver {
    function handleNext(address user) external;
}

interface IPledge {
    function getRecvInfo(
        address user
    )
        external
        view
        returns (
            uint256 canRecv,
            uint256 release,
            uint256 noRelease,
            uint256 recv
        );

    function pledgeByFD(address user, uint256 amount) external;

    function goGet(address user) external;
}

interface IPair {
    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
        external
        view
        returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

contract Token is ERC20, Ownable {
    address public constant dead = address(0xdead);
    address public constant pool = address(0x1);
    ITokenData public data;
    mapping(address => uint256) public addLPTime;
    mapping(address => address) public inviters;
    mapping(address => mapping(address => bool)) public preBind;

    event BindInviter(
        address indexed user,
        address indexed inviter,
        uint256 timestamp
    );

    constructor() ERC20("FD", "FD") {
        _mint(msg.sender, 10000000 ether);
        _mint(msg.sender, 10000000 ether);
        _mint(msg.sender, 10000000 ether);
        _mint(pool, 180000000 ether);
    }

    function init(address _data) external onlyOwner {
        data = ITokenData(_data);
        if (data.pair() == address(0)) {
            data.createPair(address(this));
        }
        _approve(pool, data.mining(), type(uint256).max);
        _approve(pool, data.pledge(), type(uint256).max);
    }

    function changeAddLpTime(address user, uint256 time) external {
        require(msg.sender == data.market(), "no permission");
        addLPTime[user] = time;
    }

    function changeInviters(address user, address inviter) external {
        require(msg.sender == data.market(), "no permission");
        inviters[user] = inviter;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (data.directTransfer(from, to)) {
            super._transfer(from, to, amount);
            return;
        }
        address pair = data.pair();
        if (from == pair) {
            if (data.openBuy()) {
                super._transfer(from, to, amount);
                if (isRemoveLiquidity()) {
                    if (data.users(to) == 2 || data.users(to) == 3) {
                        super._transfer(from, to, amount);
                        return;
                    }
                    uint256 time = block.timestamp;
                    uint256 addTime = addLPTime[to];
                    if (addTime == 0) {
                        addTime = time;
                    }
                    uint256 duration = time - addTime;
                    uint256 fee = 0;
                    if (duration < 30 days) {
                        fee = (amount * 50) / 100;
                    } else if (duration < 60 days) {
                        fee = (amount * 30) / 100;
                    } else if (duration < 90 days) {
                        fee = (amount * 20) / 100;
                    } else if (duration < 120 days) {
                        fee = (amount * 10) / 100;
                    } else {
                        fee = (amount * 5) / 100;
                    }
                    if (fee > 0) {
                        super._transfer(to, data.market(), fee);
                    }
                    addLPTime[to] = 0;
                } else {
                    data.validBuy(to, amount);
                    takeFee(to, amount, false);
                }
            } else {
                goNotOpenTransfer(from, to, amount);
            }
        } else if (to == pair) {
            if (data.openSell()) {
                if (data.users(from) == 1) {
                    return;
                }
                if (isAddLiquidity()) {
                    super._transfer(from, to, amount);
                    addLPTime[from] = block.timestamp;
                } else {
                    super._transfer(
                        from,
                        to,
                        amount - takeFee(from, amount, true)
                    );
                }
            } else {
                goNotOpenTransfer(from, to, amount);
            }
        } else {
            require(data.users(from) != 1, "the address is in black list");
            super._transfer(from, to, amount);

            IPledge pl = IPledge(data.pledge());
            if (to == dead) {
                if (amount >= 100 ether) {
                    pl.pledgeByFD(from, amount);
                }
            } else {
                if (data.validBind(from, to, amount)) {
                    preBind[from][to] = true;
                    if (inviters[from] == address(0) && preBind[to][from]) {
                        //from未绑定上级
                        inviters[from] = to;
                        emit BindInviter(from, to, block.timestamp);
                    }
                }
            }

            (uint256 canRecv, , , ) = pl.getRecvInfo(from);
            if (canRecv > 0) {
                pl.goGet(from);
            }
        }
    }

    function goNotOpenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal {
        if (data.users(from) == 2 || data.users(to) == 2) {
            super._transfer(from, to, amount);
        }
    }

    function takeFee(
        address _user,
        uint256 _amount,
        bool _sell
    ) internal returns (uint256) {
        uint256 _feeRate = _sell ? data.sellFeeRate() : data.buyFeeRate();
        uint256 _fee = calFee(_user, _amount, _feeRate);

        if (_fee > 0) {
            // super._transfer(_user, address(this), _fee);
            address feeReceiver = _sell
                ? data.sellFeeReceiver()
                : data.buyFeeReceiver();
            super._transfer(_user, feeReceiver, _fee);
            IFeeReceiver(feeReceiver).handleNext(_user);
        }
        return _fee;
    }

    function calFee(
        address _user,
        uint256 _amount,
        uint256 _feeRate
    ) internal view returns (uint256) {
        if (data.users(_user) == 2 || data.users(_user) == 3) {
            return 0;
        }

        return (_amount * _feeRate) / 10000 / 100;
    }

    function isAddLiquidity() internal view returns (bool isAdd) {
        IPair mainPair = IPair(data.pair());
        (uint256 r0, uint256 r1, ) = mainPair.getReserves();

        address tokenOther = data.usdt();
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 bal = IERC20(tokenOther).balanceOf(address(mainPair));
        isAdd = bal > r;
    }

    function isRemoveLiquidity() internal view returns (bool isRemove) {
        IPair mainPair = IPair(data.pair());
        (uint256 r0, uint256 r1, ) = mainPair.getReserves();

        address tokenOther = data.usdt();
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint256 bal = IERC20(tokenOther).balanceOf(address(mainPair));
        isRemove = r >= bal;
    }
}
