     struct Round{
        uint256 totalTokenAmount; // Total amount of Pulse tokens to be sold in this round
        uint256 soldTokenAmount; // Amount of Pulse tokens that are already sold
        string  roundDescription; // name of the round (Round1, Round2 ...)
        uint256 minUSDTBuyAmount; // Min amount of USDT that can be spent

        uint256 roundPrice; // price of Pulse token in current round
        uint256 roundStart; // DateTime when round starts
        uint256 roundEnd;  // DateTime when round ends

        bool isActive; // is this round currently active?
    }

    Round[3] public rounds;

    uint8 public currentRound;
    address private pulsePrivateSale;


    struct HistoryRecord {
        uint256 boughtAmount;// Pulse token bought amount
        uint256 dateTime; // TimeStamp of operation
    }

    mapping(address => HistoryRecord[]) userHistory; //address => history
    
    IERC20Metadata public PULSE;
    IERC20Metadata public USDT;



    function initialize() public initializer {
        __AccessControl_init();
        __Pausable_init_unchained();

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

        currentRound = 0;

        rounds[0].roundDescription = "Round 1";
        rounds[1].roundDescription = "Round 2";
        rounds[2].roundDescription = "Round 3";

        rounds[0].roundPrice = 10000000000000000;
        rounds[1].roundPrice = 11000000000000000;
        rounds[2].roundPrice = 12000000000000000;
    }


    /**
    * @dev Throws if called by any account other than the one with the Admin role granted.
    */
    modifier onlyAdmin() {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not the Admin");
        _;
    }


//************* ADMIN FUNCTIONS ********************************//
    function pause() onlyAdmin public {
        super._pause();
    }
   
   
    function unpause() onlyAdmin public {
        super._unpause();
    }


    function setUSDTContractAddress(address _USDTContract) onlyAdmin public {
        USDT = IERC20Metadata(_USDTContract);
    }


    function setPULSEContractAddress(address _PULSEContract) onlyAdmin public {
        PULSE = IERC20Metadata(_PULSEContract);
    }


    //Round settings 
    function setTotalTokenAmount(uint8 _round, uint256 _totalTokenAmount) onlyAdmin public {
        require((_round >= 0) && (_round <=2), "Wrong round index");
        rounds[_round].totalTokenAmount = _totalTokenAmount * e18;
    }


    function setMinUSDTBuyAmount(uint8 _round, uint256 _minUSDTBuyAmount) onlyAdmin public {
        require((_round >= 0) && (_round <=2), "Wrong round index");
        rounds[_round].minUSDTBuyAmount = _minUSDTBuyAmount * e18;
    }


    function setRoundPrice(uint8 _round, uint256 _roundPrice) onlyAdmin public {
        require((_round >= 0) && (_round <=2), "Wrong round index");
        rounds[_round].roundPrice = _roundPrice;
    }


    function setRoundStart(uint8 _round, uint256 _roundStart) onlyAdmin public {
        require((_round >= 0) && (_round <=2), "Wrong round index");
        rounds[_round].roundStart = _roundStart;
    }


    function setRoundEnd(uint8 _round, uint256 _roundEnd) onlyAdmin public {
        require((_round >= 0) && (_round <=2), "Wrong round index");
        require(_roundEnd > rounds[_round].roundStart, "Wrong roundEnd");
        rounds[_round].roundEnd = _roundEnd;
    }


    function changeRoundStatus(uint8 _round) onlyAdmin public {
        require((_round >= 0) && (_round <=2), "Wrong round index");
        rounds[_round].isActive = !rounds[_round].isActive;
    }


    function setCurrentRound(uint8 _currentRound) onlyAdmin public {
        require((_currentRound >= 0) && (_currentRound <=2), "Wrong round index");
        currentRound = _currentRound;
    }


    function setPulsePrivateSaleAddress(address _newAddress) onlyAdmin public {
        pulsePrivateSale = _newAddress;
    }


//************* USER FUNCTIONS ********************************//
 
   function buy (uint256 _USDTAmount) public whenNotPaused{
       require(!isContract(msg.sender), "Contract calls are not available");
       require(rounds[currentRound].isActive,"Current round is not active");
       require(_USDTAmount >= rounds[currentRound].minUSDTBuyAmount,"USDT amount less than MIN");
       require((block.timestamp >= rounds[currentRound].roundStart), "Round has not started yet");
       require((block.timestamp <= rounds[currentRound].roundEnd), "Round has finished");

       uint256 pulseAmount = getPulseAmount(currentRound, _USDTAmount);

       require((rounds[currentRound].totalTokenAmount - rounds[currentRound].soldTokenAmount) >= pulseAmount,"Not enough Pulse tokens available for sale in this round");
       require(PULSE.balanceOf(address(this)) >= pulseAmount, "Not enough PULSE tokens on contract");

       uint256 balanceBefore = USDT.balanceOf(pulsePrivateSale);
       USDT.transferFrom(msg.sender, pulsePrivateSale, _USDTAmount);
       require(USDT.balanceOf(pulsePrivateSale) - balanceBefore == _USDTAmount, "USDT Payment error");

       PULSE.transfer(msg.sender, pulseAmount);

       rounds[currentRound].soldTokenAmount += pulseAmount;

       if ((rounds[currentRound].totalTokenAmount - rounds[currentRound].soldTokenAmount) < getPulseAmount(currentRound, rounds[currentRound].minUSDTBuyAmount)) {
           rounds[currentRound].soldTokenAmount = rounds[currentRound].totalTokenAmount;
           rounds[currentRound].isActive = false;
       }
       

       HistoryRecord memory tempRecord;
       tempRecord.boughtAmount = pulseAmount;
       tempRecord.dateTime = block.timestamp;

       userHistory[msg.sender].push(tempRecord);
   }


//************* VIEW FUNCTIONS ********************************//

    function getCurrentRound() public view whenNotPaused returns (uint8){
        return currentRound;
    }


    function getPulsePrice(uint8 _round) public view whenNotPaused returns (uint256){
        require((_round >= 0) && (_round <=2), "Wrong round index");
        return rounds[_round].roundPrice;
    }


    function getPulseAmount(uint8 _round, uint256 _USDTAmount) public view whenNotPaused returns (uint256){
        require((_round >= 0) && (_round <=2), "Wrong round index");
        return e18 * _USDTAmount / getPulsePrice(_round);
    }


    function getTotalTokenAmount(uint8 _round) public view whenNotPaused returns (uint256){
        require((_round >= 0) && (_round <=2), "Wrong round index");
        return rounds[_round].totalTokenAmount;
    }


    function getSoldTokenAmount(uint8 _round) public view whenNotPaused returns (uint256){
        require((_round >= 0) && (_round <=2), "Wrong round index");
        return rounds[_round].soldTokenAmount;
    }


    function getStartDate(uint8 _round) public view whenNotPaused returns (string memory){
        require((_round >= 0) && (_round <=2), "Wrong round index");
        return timestampToDate(rounds[_round].roundStart);
    }


    function getEndDate(uint8 _round) public view whenNotPaused returns (string memory){
        require((_round >= 0) && (_round <=2), "Wrong round index");
        return timestampToDate(rounds[_round].roundEnd);
    }


    function getRoundDescription(uint8 _round) public view whenNotPaused returns (string memory){
        require((_round >= 0) && (_round <=2), "Wrong round index");
        return rounds[_round].roundDescription;
    }


    function getMinUSDTBuyAmount(uint8 _round) public view whenNotPaused returns (uint256){
        require((_round >= 0) && (_round <=2), "Wrong round index");
        return rounds[_round].minUSDTBuyAmount;
    }


    function getUserHistory(address _userAddress) public view whenNotPaused returns (HistoryRecord[] memory){
        return userHistory[_userAddress];
    }


    function getDate(uint256 _timeStamp) public view whenNotPaused returns (string memory){
        return timestampToDate(_timeStamp);
    }

    function getRoundStatus(uint8 _round) public view whenNotPaused returns (bool){
        require((_round >= 0) && (_round <=2), "Wrong round index");
        return rounds[_round].isActive;
    }

//************* INTERNAL FUNCTIONS ********************************//

     // timestampToDate
    function timestampToDate(uint256 _timestamp) internal pure returns (string memory) {
        int __days = int(_timestamp/86400);

        int L = __days + 68569 + 2440588;
        int N = 4 * L / 146097;
        L = L - (146097 * N + 3) / 4;
        int _year = 4000 * (L + 1) / 1461001;
        L = L - 1461 * _year / 4 + 31;
        int _month = 80 * L / 2447;
        int _day = L - 2447 * _month / 80;
        L = _month / 11;
        _month = _month + 2 - 12 * L;
        _year = 100 * (N - 49) + _year + L;

        return string(abi.encodePacked(StringsUpgradeable.toString(uint(_day)),".", StringsUpgradeable.toString(uint(_month)),".",StringsUpgradeable.toString(uint(_year))));
    }   


    function isContract(address account) internal view returns (bool) {
        // This method relies on extcodesize, which returns 0 for contracts in
        // construction, since the code is only stored at the end of the
        // constructor execution.

        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }
}
