    /**
     * @dev Get Seller HnId Existence
     */
    function getSellerHnIdExistence(address seller, uint256 hnId)
        external
        view
        returns (bool)
    {
        return sellerHnIds[seller].contains(hnId);
    }

    /**
     * @dev Get Seller HnIds Length
     */
    function getSellerHnIdsLength(address seller)
        external
