    "contracts/BonusHander/BonusHander.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

import "../Resources/IContractsLibraryV2.sol";
import "./IBonusHander.sol";

contract BonusHander is IBonusHander,Initializable, AccessControlUpgradeable, ReentrancyGuardUpgradeable,UUPSUpgradeable  {
    IERC721 public erc721;
    IContractsLibraryV2 public oracle;
    IERC20 public token;
    bytes32 public constant MASTER_ROLE = keccak256("MASTER_ROLE");
    uint public revivePrice;
    uint public upgradePrice;
    uint public typesLength;
    mapping(address => uint) public nft_register;
    mapping(uint => uint) public bonusByType;
    mapping (uint => NFT_Data) public nftData;
    mapping(address => uint) public lastBlock;
    // 10_000 = 100%, 1_000 = 10%, 100 = 1%, 10 = 0.1%, 1 = 0.01%
    uint constant public PERCENTS_DIVIDER = 10_000;
    uint constant public MAX_REVIVE_PRICE = 300 ether;
    uint constant public flatFee = 0.0047 ether;

    // dev fee 1%
    uint constant public devFee = 100;

    address public devWallet;
    address public feeWallet;

    modifier tenBlocks() {
        require(block.number - lastBlock[msg.sender] > 10, "wait 10 blocks");
        _;
        lastBlock[msg.sender] = block.number;
    }

    modifier onlyMaster() {
        require(hasRole(MASTER_ROLE, msg.sender), "Caller is not a master");
        _;
    }

    modifier onlyOwner() {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not a owner");
        _;
    }


    function _authorizeUpgrade(address newImplementation)
        internal
        onlyOwner
        override
    {}


    function initialize(address _ERC721,  address _oracle, address _erc20,
        address _devWallet, address _feeWallet)
        initializer public
    {

        __AccessControl_init();
        __ReentrancyGuard_init();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        erc721 = IERC721(_ERC721);
        oracle = IContractsLibraryV2(_oracle);
        token = IERC20(_erc20);
        devWallet = _devWallet;
        feeWallet = _feeWallet;
        bonusByType[0] = 300;
        bonusByType[1] = 600;
        bonusByType[2] = 900;
        typesLength = 3;
        revivePrice = MAX_REVIVE_PRICE;
        upgradePrice = MAX_REVIVE_PRICE;                   
    }


    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
        }  


    function setMaster(address _master, bool _isMaster) external onlyOwner {
        if(_isMaster) {
            grantRole(MASTER_ROLE, _master);
        } else {
            revokeRole(MASTER_ROLE, _master);
        }
    }

    function setERC721(address _ERC721) external onlyOwner {
        erc721 = IERC721(_ERC721);
    }

    function setOracle(address _oracle) external onlyOwner {
        oracle = IContractsLibraryV2(_oracle);
    }

    function setToken(address _erc20) external onlyOwner {
        token = IERC20(_erc20);
    }

    function setBonusByType(uint _type, uint _bonus) external onlyOwner {
        require(_bonus <=  PERCENTS_DIVIDER, "Bonus is too big");
        require(_type < typesLength, "Type is too big");
        bonusByType[_type] = _bonus;
    }

    function addBonusByType(uint _bonus) external onlyOwner {
        require(_bonus <=  PERCENTS_DIVIDER, "Bonus is too big2");
        bonusByType[typesLength] = _bonus;
        typesLength++;
    }

    function setRevivePrice(uint _price) external onlyOwner {
        require(_price <=  MAX_REVIVE_PRICE, "Price is too big");
        revivePrice = _price;
    }

    function setUpgradePrice(uint _price) external onlyOwner {
        require(_price <=  MAX_REVIVE_PRICE, "Price is too big");
        upgradePrice = _price;
    }

    function getNFTaddress() external view returns (address) {
        return address(erc721);
    }

    function registerNFT(uint _id) external payable tenBlocks nonReentrant {
        _payFee();
        require(_id != 0, "Zero id");
        require(
            erc721.ownerOf(_id) == msg.sender,
            "You are not owner of this NFT"
        );
        NFT_Data storage _nft = nftData[_id];
        if (_nft.owner != address(0)) {
            address _old_owner = _nft.owner;
            delete _nft.owner;
            delete nft_register[_old_owner];
        }
        if (nft_register[msg.sender] != 0) {
            uint old_id = nft_register[msg.sender];
            delete nft_register[msg.sender];
            delete nftData[old_id].owner;
        }
        nft_register[msg.sender] = _id;
        _nft.owner = msg.sender;
    }

    function unRegisterNFT() external tenBlocks nonReentrant {
        require(nft_register[msg.sender] != 0, "You are not registered");
        uint _id = nft_register[msg.sender];
        delete nftData[_id].owner;
        delete nft_register[msg.sender];

    }

    function getMyPercent(
        address _user,
        uint _myDivider
    ) external view returns (uint) {
        if (nft_register[_user] == 0) {
            return 0;
        }
        (uint _percent, uint _divider) = getMyBonus(_user);
        return (_myDivider * _percent) / _divider;
    }

    function getMyBonus(address _user) public view returns (uint _percent, uint _divider) {
        uint _id = nft_register[_user];
        if (_id == 0) {
            return (0, PERCENTS_DIVIDER);
        }
        NFT_Data memory _nft = nftData[_id];
        if(_nft.used == true) {
            return (0, PERCENTS_DIVIDER);
        }
        return (bonusByType[_nft.getType], PERCENTS_DIVIDER);
    }

    function useMybonus(address _user) external onlyMaster returns (uint _percent, uint _divider) {
        uint _id = nft_register[_user];
        (_percent, _divider) = getMyBonus(_user);
        if(_percent == 0) {
            return (0, PERCENTS_DIVIDER);
        }
        nftData[_id].used = true;
        return (_percent, _divider);
    }

    function payForRevive(uint _id) public {
        require(_id != 0, "You are not registered");
        NFT_Data storage _nft = nftData[_id];
        require(_nft.used == true, "This NFT is not used");
        _nft.used = false;
        payFees(getPrice(revivePrice));
    }

    function payFees(uint _amout) public {
        token.transferFrom(msg.sender, address(this), _amout);
        uint _toDev = (_amout * devFee) / PERCENTS_DIVIDER;
        token.transfer(devWallet, _toDev);
        token.transfer(feeWallet, token.balanceOf(address(this)));
    }

    function payReviveMyNft() external {
        uint _id = nft_register[msg.sender];
        payForRevive(_id);
    }

    function getPrice(uint _amount) public view returns (uint) {
        return oracle.getBusdToBNBToToken(address(token), _amount);
    }

    function upgradeType(uint _id) public payable nonReentrant {
        _payFee();
        require(_id != 0, "You are not registered");
        NFT_Data storage _nft = nftData[_id];
        require(_nft.getType + 1 < typesLength, "Type is too big");
        payFees(getPrice(upgradePrice));
        _nft.getType += 1;
    }

    function upgradeMyType() external payable {
        uint _id = nft_register[msg.sender];
        upgradeType(_id);
    }

    function upgradeNFTAdmin(uint _id) external onlyMaster returns(bool) {
        if(_id == 0) {
            return false;
        }
        NFT_Data storage _nft = nftData[_id];
        if(_nft.getType + 1 >= typesLength) {
            return false;
        }
        _nft.getType += 1;
        return true;
    }

    function getNftData(uint _id) external view returns (NFT_Data memory) {
        return nftData[_id];
    }

    modifier enoughFee() {
        require(msg.value >= flatFee, "Flat fee");
        _;
    }

    function _payFee() internal enoughFee {
        if (flatFee > 0) {
            uint256 devFeeAmount = (msg.value * 10) / 100;
            uint256 feeAmount = msg.value - devFeeAmount;
            payable(feeWallet).transfer(feeAmount);
            payable(devWallet).transfer(devFeeAmount);
        }
    }

}
"
    },
    "contracts/BonusHander/IBonusHander.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IBonusHander {

    struct NFT_Data {
        address owner;
        uint id;
        uint getType;
        bool used;
    }

    function getMyPercent(address _user, uint _myDivider) external view returns(uint);

    function bonusByType(uint _type) external view returns(uint);

    function getNFTaddress() external view returns(address);

    function useMybonus(address _user) external returns (uint _percent, uint _divider);

    function getMyBonus(address _user) external view returns (uint _percent, uint _divider);

    function getNftData(uint _id) external view returns (NFT_Data memory);


}
"
    },
    "contracts/Resources/IContractsLibraryV2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "./IUniswapV2Router02.sol";

abstract contract IContractsLibraryV2 {
    function BUSD() external view virtual returns (address);

    function WBNB() external view virtual returns (address);

    function ROUTER() external view virtual returns (IUniswapV2Router02);

    function getBusdToBNBToToken(
        address token,
        uint _amount
    ) external view virtual returns (uint256);

    function getTokensToBNBtoBusd(
        address token,
        uint _amount
    ) external view virtual returns (uint256);

    function getTokensToBnb(
        address token,
        uint _amount
    ) external view virtual returns (uint256);

    function getBnbToTokens(
        address token,
        uint _amount
    ) public view virtual returns (uint256);

    function getTokenToBnbToAltToken(
        address token,
        address altToken,
        uint _amount
    ) public view virtual returns (uint256);

    function getLpPrice(
        address token,
        uint _amount
    ) public view virtual returns (uint256);

    function getUsdToBnB(uint amount) external view virtual returns (uint256);

    function getBnbToUsd(uint amount) external view virtual returns (uint256);
}
"
    },
    "contracts/Resources/IUniswapV2Router01.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IUniswapV2Router01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETH(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountToken, uint amountETH);
    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETHWithPermit(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountToken, uint amountETH);
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);
    function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);

    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}
"
    },
    "contracts/Resources/IUniswapV2Router02.sol": {
