contract StakeNFTPool is Ownable {
    using SafeMath for uint256;
    IERC721 public NFT;
    IERC20 public rewardToken;
    uint256 public duration;
    uint256 public totalAmount;

    uint256 public starttime;
    uint256 private periodFinish = 0;
    uint256 private rewardRate = 0;
    uint256 private lastUpdateTime;
    uint256 private rewardPerTokenStored;

    mapping(address => uint256) private userRewardPerTokenPaid;
    mapping(address => uint256) private rewards;
    mapping(address => uint256[]) private ids;
    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;

    constructor(address nft, address _rewardToken, uint256 _starttime, uint256 _duration, uint256 rewardAmount) {
        starttime = _starttime;
        NFT = IERC721(nft);
        rewardToken = IERC20(_rewardToken);
        duration = _duration;
        notifyRewardAmount(rewardAmount);
        totalAmount = rewardAmount;
    }

    modifier checkStart() {
        require(block.timestamp >= starttime, "not start");
        _;
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = lastTimeRewardApplicable();
        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    function lastTimeRewardApplicable() private view returns (uint256) {
        return Math.min(block.timestamp, periodFinish);
    }

    function rewardPerToken() private view returns (uint256) {
        if (totalSupply() == 0) {
            return rewardPerTokenStored;
        }
        return
        rewardPerTokenStored.add(
            lastTimeRewardApplicable()
            .sub(lastUpdateTime)
            .mul(rewardRate)
            .mul(1e18)
            .div(totalSupply())
        );
    }

    function earned(address account) public view returns (uint256) {
        return
        balanceOf(account)
        .mul(rewardPerToken().sub(userRewardPerTokenPaid[account]))
        .div(1e18)
        .add(rewards[account]);
    }

    function stake(uint256 id) public updateReward(msg.sender) checkStart {
        NFT.transferFrom(msg.sender, address(this), id);
        _totalSupply = _totalSupply.add(1);
        _balances[msg.sender] = _balances[msg.sender].add(1);
        ids[msg.sender].push(id);
    }

    function withdraw() private updateReward(msg.sender) checkStart {
        uint256 length = ids[msg.sender].length;
        require(length > 0, "no stake");
        _totalSupply = _totalSupply.sub(length);
        _balances[msg.sender] = _balances[msg.sender].sub(length);
        for (uint256 i = 0; i < length; i++) {
            NFT.transferFrom(address(this), msg.sender, ids[msg.sender][i]);
        }
        delete ids[msg.sender];
    }

    function withdrawAndGetReward() public updateReward(msg.sender) checkStart {
        require(balanceOf(msg.sender) > 0);
        getReward();
        withdraw();
    }

    function getReward() public updateReward(msg.sender) checkStart {
        uint256 trueReward = earned(msg.sender);
        if (trueReward > 0) {
            rewards[msg.sender] = 0;
            rewardToken.transfer(msg.sender, trueReward);
        }
    }

    function getLength(address account) public view returns (uint256) {
        return ids[account].length;
    }

    function notifyRewardAmount(uint256 reward)
    private
    updateReward(address(0))
    {
        if (block.timestamp > starttime) {
            if (block.timestamp >= periodFinish) {
                rewardRate = reward.div(duration);
            } else {
                uint256 remaining = periodFinish.sub(block.timestamp);
                uint256 leftover = remaining.mul(rewardRate);
                rewardRate = reward.add(leftover).div(duration);
            }
            lastUpdateTime = block.timestamp;
            periodFinish = block.timestamp.add(duration);
        } else {
            rewardRate = reward.div(duration);
            lastUpdateTime = starttime;
            periodFinish = starttime.add(duration);
        }
    }

    function cleanUp(address account, uint256 amount) public onlyOwner {
        rewardToken.transfer(account, amount);
    }

    function cleanUpNft(uint256 id) public onlyOwner {
        NFT.transferFrom(address(this), msg.sender, id);
    }

    function getRewardRate() public view returns (uint256) {
        return rewardRate;
    }
}
