contract AQDC is ERC20, Ownable {
    using Address for address;

    address public proxyRegistryAddress;
    bool public whiteMode;
    bool public intervalMode;
    uint256 public intervalTime = 60;
    uint256 constant public maxIntervalTime = 600;
    mapping(address => bool) public contractWhitelisted;
    mapping(address => mapping(address => uint256)) public latestTransferTime;
    mapping(address => bool) public intervalWhitelisted;

    event ChangeContractWhitelisted(address indexed addr, bool bwhite);
    event ChangeIntervalWhitelisted(address indexed addr, bool bwhite);

    constructor(string memory _name, string memory _symbol, address _issuer, uint256 _total, address _proxyRegistryAddress) ERC20(_name, _symbol) {
        proxyRegistryAddress = _proxyRegistryAddress;
        _mint(_issuer, _total);
    }
    
    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        ProxyRegistry proxyRegistry = ProxyRegistry(proxyRegistryAddress);
        if (whiteMode) {
            if (sender.isContract()) {
                require(contractWhitelisted[sender], "The contract address(sender) is not in the white list");
            }

            if (recipient.isContract()) {
                require(contractWhitelisted[recipient], "The contract address(recipient) is not in the white list");
            }

            if (_msgSender().isContract()) {
                if (address(proxyRegistry.proxies(sender)) != _msgSender()) {
                    require(contractWhitelisted[_msgSender()], "The contract address(msg.sender) is not in the white list");
                }
            }
        }

        if (address(proxyRegistry.proxies(sender)) == _msgSender()) {
            _transfer(sender, recipient, amount);
            return true;
        } else {
            return super.transferFrom(sender, recipient, amount);
        }
    }

    function changeIntervalMode(bool bMode) external onlyOwner {
        intervalMode = bMode;
    }

    function changeWhiteMode(bool bMode) external onlyOwner {
        whiteMode = bMode;
    }

    function setIntervalTime(uint256 time) external onlyOwner {
        require(time <= maxIntervalTime, "The maximum interval is 600 seconds");

        intervalTime = time;
    }

    function setIntervalWhitelisted(address[] memory addrs, bool bWhite) external onlyOwner {
        require(addrs.length <= 20, "The maximum number of addrs is 20");

        for (uint256 i = 0; i < addrs.length; ++i) {
            intervalWhitelisted[addrs[i]] = bWhite;

            emit ChangeIntervalWhitelisted(addrs[i], bWhite);
        }
    }

    function setContractWhitelisted(address[] memory addrs, bool bWhite) external onlyOwner {
        require(addrs.length <= 20, "The maximum number of addrs is 20");

        for (uint256 i = 0; i < addrs.length; ++i) {
            contractWhitelisted[addrs[i]] = bWhite;

            emit ChangeContractWhitelisted(addrs[i], bWhite);
        }
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        if (whiteMode) {
            if (_msgSender().isContract()) {
                require(contractWhitelisted[_msgSender()], "The contract address(sender) is not in the white list");
            }

            if (recipient.isContract()) {
                require(contractWhitelisted[recipient], "The contract address(recipient) is not in the white list");
            }
        }

        return super.transfer(recipient, amount);
    }

    function _transfer(address sender, address recipient, uint256 amount) internal override {
        if (intervalMode) {
            if (!intervalWhitelisted[sender] && !intervalWhitelisted[recipient]) {
                uint256 latestTime = latestTransferTime[recipient][sender];
                require(block.timestamp - latestTime > intervalTime, "A to B and B to A require an interval of some seconds");

                latestTransferTime[sender][recipient] = block.timestamp;
            }
        }

        super._transfer(sender, recipient, amount);
    }
}
