    "contracts/Helpers/Signers.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0-only

pragma solidity 0.8.17;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/ISigsVerifier.sol";

contract Signers is Ownable, ISigsVerifier {
    using ECDSA for bytes32;

    bytes32 public ssHash;

    /**
     * @notice Verifies that a message is signed by a quorum among the signers
     * The sigs must be sorted by signer addresses in ascending order.
     * @param _msg signed message
     * @param _sigs list of signatures sorted by signer addresses
     * @param _signers sorted list of current signers
     * @param _powers powers of current signers
     */
    function verifySigs(bytes memory _msg, bytes[] calldata _sigs, address[] calldata _signers, uint256[] calldata _powers) public view override {
        bytes32 h = keccak256(abi.encodePacked(_signers, _powers));
        // require(ssHash == h, "Mismatch current signers");
        _verifySignedPowers(keccak256(_msg).toEthSignedMessageHash(), _sigs, _signers, _powers);
    }

    // separate from verifySigs func to avoid "stack too deep" issue
    function _verifySignedPowers(bytes32 _hash, bytes[] calldata _sigs, address[] calldata _signers, uint256[] calldata _powers) private pure {
        require(_signers.length == _powers.length, "signers and powers length not match");
        uint256 totalPower; // sum of all signer.power
        for (uint256 i = 0; i < _signers.length; i++) {
            totalPower += _powers[i];
        }
        uint256 quorum = (totalPower * 2) / 3 + 1;

        uint256 signedPower; // sum of signer powers who are in sigs
        address prev = address(0);
        uint256 index = 0;
        for (uint256 i = 0; i < _sigs.length; i++) {
            address signer = _hash.recover(_sigs[i]);
            require(signer > prev, "signers not in ascending order");
            prev = signer;
            // now find match signer add its power
            while (signer > _signers[index]) {
                index += 1;
                require(index < _signers.length, "signer not found");
            }
            if (signer == _signers[index]) {
                signedPower += _powers[index];
            }
            if (signedPower >= quorum) {
                // return early to save gas
                return;
            }
        }
        revert("quorum not reached");
    }
}
"
    },
    "contracts/Helpers/VerifySigEIP712.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "../libraries/LibDiamond.sol";
import "../interfaces/Structs.sol";
import {LengthNotMatch} from "../Errors/GenericErrors.sol";

contract VerifySigEIP712 is EIP712("Plexus", "1") {
    bytes32 internal constant DATA_STORAGE_POSITION = keccak256("diamond.standard.sig.storage");

    struct SigData {
        address[] signerList;
        mapping(bytes32 => bool) txHashCheck; //add
    }

    struct Input {
        bytes data;
        uint256 gasFee;
        uint256 transferFee;
        address userAddress;
        bytes32 txHash;
    }

    bytes32 private constant SWAP_TYPEHASH = keccak256("Input(bytes data,uint256 gasFee,uint256 transferFee,address userAddress,bytes32 txHash)");

    function sigDataStorage() internal pure returns (SigData storage s) {
        bytes32 position = DATA_STORAGE_POSITION;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            s.slot := position
        }
    }

    function contractOwnerAddress() public view returns (address owner) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        owner = ds.contractOwner;
    }

    function relaySig(Input memory inputData, bytes[] memory signature) public view returns (bool) {
        uint256 CheckCount;
        SigData storage ds = sigDataStorage();
        require(ds.txHashCheck[inputData.txHash] == false, "already passed");
        for (uint256 i = 0; i < signature.length; i++) {
            bytes32 digest = _hashTypedDataV4(
                keccak256(
                    abi.encode(
                        SWAP_TYPEHASH,
                        keccak256(inputData.data),
                        inputData.gasFee,
                        inputData.transferFee,
                        inputData.userAddress,
                        inputData.txHash
                    )
                )
            );
            (bytes32 r, bytes32 s, uint8 v) = splitSignature(signature[i]);
            address signer = ECDSA.recover(digest, v, r, s);
            if (signer == ds.signerList[i]) {
                CheckCount++;
            }
        }
        if (CheckCount == signature.length) return true;
        else {
            revert LengthNotMatch();
        }
    }

    function splitSignature(bytes memory sig) public pure returns (bytes32 r, bytes32 s, uint8 v) {
        require(sig.length == 65, "invalid signature length");
        assembly {
            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }
    }
}
"
    },
