// Указываем версию Solidity
pragma solidity ^0.8.0;

// Объявляем интерфейс ERC-20
interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

// Определяем контракт ERC-20 токена
contract ERC20Token is IERC20 {
    string public name = "EUR"; // Название токена
    string public symbol = "EUR"; // Символ токена
    uint8 public decimals = 18; // Количество десятичных знаков
    uint256 private _totalSupply = 10000000000000000000; // Общее количество токенов

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    // Конструктор контракта
    constructor() {
        _balances[msg.sender] = _totalSupply;
        emit Transfer(address(0), msg.sender, _totalSupply);
    }

    // Возвращает общее количество токенов
    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    // Возвращает баланс указанного адреса
    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    // Передает токены указанному адресу
    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    // Возвращает оставшееся разрешенное количество токенов, которые spender может передать от имени владельца
    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    // Подтверждает указанному адресу перевод токенов от msg.sender
    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    // Передает токены от одного адреса к другому
    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender] - amount);
        return true;
    }

    // Внутренняя функция для выполнения фактического перевода токенов
    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "Transfer from the zero address");
        require(recipient != address(0), "Transfer to the zero address");
        require(_balances[sender] >= amount, "Insufficient balance");

        _balances[sender] -= amount;
        _balances[recipient] += amount;

        emit Transfer(sender, recipient, amount);
    }

    // Внутренняя функция для подтверждения разрешения на перевод токенов
    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "Approve from the zero address");
        require(spender != address(0), "Approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
}
