      emit Transfer(from, magellanicCloudCardAddress, nftAmount * magellanicCloudCardTotal / (supremeCardTotal + magellanicCloudCardTotal));
   } else {
     _balances[supremeCardAddress] += nftAmount;
      emit Transfer(from, supremeCardAddress, nftAmount);
   }
      _balances[burnAddress] += burnAmount;
      _balances[nodeAddress] += nodeAmount;
      _balances[daoAddress] += daoAmount;
      _balances[to] += amount - burnAmount - nftAmount - daoAmount - nodeAmount;

      emit Transfer(from, burnAddress, burnAmount);
      emit Transfer(from, nodeAddress, nodeAmount);
      emit Transfer(from, daoAddress, daoAmount);
      emit Transfer(from, to, amount - burnAmount - nftAmount - daoAmount - nodeAmount);
 }
 

  function getRateAmount(uint256 _amount, uint256 _rate) private view returns(uint256 amount) {
    amount = _amount * _rate / denominator;
  }

  function getWhitelistLength() public view returns(uint256) {
    return _whitelist.length();
  }

  function isWhitelist(address account) public view returns(bool) {
    return _whitelist.contains(account);
  }

  function getWhitelist(uint256 _index) public view onlyOwner returns(address) {
    require(_index <= getWhitelistLength() - 1, "token: index out of bounds");
    return _whitelist.at(_index);
  }

   function getToken(address _token) external onlyOwner {
    uint256 balance = IERC20(_token).balanceOf(address(this));
    IERC20(_token).safeTransfer(msg.sender, balance);
  }

  function getETH(uint256 _amount) external onlyOwner {
    payable(msg.sender).transfer(_amount);
  }

