contract SphTempToken is ERC20, Ownable {
    constructor(
        string memory name,
        string memory symbol,
        uint8 decimals,
        uint256 initialSupply,
        address owner
    ) ERC20(name, symbol) {
        _mint(owner, initialSupply * 10**uint256(decimals));
    }
    function mint( address account, uint256 amount) external onlyOwner {
      _mint(account, amount);
    }
    function burn( address account, uint256 amount) external onlyOwner {
      _burn(account, amount);
    }
}

interface ISphynxFactory {
  function deployNewERC20Token(
      string calldata name,
      string calldata symbol,
      uint8 decimals,
      uint256 initialSupply
  ) external returns (address);
}

contract SphynxFactory is ISphynxFactory {
    event ERC20TokenCreated(address tokenAddress);
    event ERC721TokenCreated(address tokenAddress);

    function deployNewERC20Token(
        string calldata name,
        string calldata symbol,
        uint8 decimals,
        uint256 initialSupply
    ) override  public returns (address) {
        SphTempToken t = new SphTempToken(
            name,
            symbol,
            decimals,
            initialSupply,
            msg.sender
        );
        emit ERC20TokenCreated(address(t));

        return address(t);
    }
}

contract Bridge is Ownable, ReentrancyGuard, SphynxFactory {
  using SafeERC20 for IERC20;
  using SafeMath for uint256;
  mapping(address => address) public sphTokenAddresses;
  mapping(uint256 => address) public poolTokens;
  uint256 public poolLength;
  mapping(uint256 => mapping(address => address)) public reversePairs;
  mapping(uint256 => mapping(bytes32 => bool)) isFilled;
  mapping(uint256 => uint256) public staticFee;
  mapping(uint256 => uint256) public performanceFee;

  address public nativeTokenAddr = address(1);
  uint256 public chainId;

  constructor() {
    uint256 id;
    assembly {
        id := chainid()
    }
    chainId = id;
    poolLength = 0;
  }

  event NewPairRegistered(
    uint256 chainId,
    address _token,
    uint256[] chainIds,
    address[] _tokens
  );

  event BridgeSwapStarted(
    address token,
    uint256 amount,
    uint256 targetChainId,
    address to
  );

  event BridgeSwapFilled(
    uint256 chainId,
    address token,
    uint256 amount,
    address to
  );

  function reisterPair(address _token, uint256[] memory _chainIds, address[] memory _tokens) external onlyOwner {
    require(_chainIds.length == _tokens.length, "not-equal-length");
    if (_token == nativeTokenAddr && sphTokenAddresses[_token] == address(0)) {
      sphTokenAddresses[_token] = ISphynxFactory(this).deployNewERC20Token(string(abi.encodePacked("sph", chainId)), string(abi.encodePacked("Ssph")), 18, 0);
      poolTokens[ poolLength ] = _token;
      poolLength = poolLength + 1;
    }
    if (sphTokenAddresses[_token] == address(0)) {
      ERC20 token = ERC20(_token);
      sphTokenAddresses[_token] = ISphynxFactory(this).deployNewERC20Token(string(abi.encodePacked("sph",token.name())), token.symbol(), token.decimals(), 0);
      poolTokens[ poolLength ] = _token;
      poolLength = poolLength + 1;
    }
    for(uint i = 0; i < _chainIds.length; i++) {
      reversePairs[_chainIds[i]][_tokens[i]] = _token;
    }

    emit NewPairRegistered(
      chainId,
      _token,
      _chainIds,
      _tokens
    );
  }

  function depositNative() external payable nonReentrant{
    require(sphTokenAddresses[nativeTokenAddr] != address(0), "Token is not registered");
    SphTempToken(sphTokenAddresses[nativeTokenAddr]).mint(msg.sender, msg.value);
  }

  function withDrawNative(uint256 _amount) external nonReentrant{
    require(sphTokenAddresses[nativeTokenAddr] != address(0), "Token is not registered");
    require(address(this).balance > _amount, "This has no enough token");
    IERC20 sphToken = IERC20(sphTokenAddresses[nativeTokenAddr]);
    require(sphToken.balanceOf(msg.sender) >= _amount, "You have no enough token");
    SphTempToken(sphTokenAddresses[nativeTokenAddr]).burn(msg.sender, _amount);
    address payable to = payable(msg.sender);
    to.transfer(_amount);
  }

  function deposit(address token, uint256 amount) external nonReentrant {
    require(token != address(0), "Token address should be not null");
    require(sphTokenAddresses[token] != address(0), "Token is not registered");
    require(IERC20(token).balanceOf(msg.sender) > amount, "You have no enough token");

    IERC20(token).transferFrom(msg.sender, address(this), amount);
    SphTempToken(sphTokenAddresses[token]).mint(msg.sender, amount);
  }

  function withDraw(address token, uint256 amount) external nonReentrant {
    require(token != address(0), "Token address should be not null");
    require(sphTokenAddresses[token] != address(0), "Token is not registered");
    require(IERC20(token).balanceOf(address(this)) >= amount, "This has no enough token");
    SphTempToken(sphTokenAddresses[token]).burn(msg.sender, amount);
    IERC20(token).safeTransfer( msg.sender, amount);
  }

  function updateStaticFee(uint256 _staticFee, uint256 _chainId) external onlyOwner {
      staticFee[_chainId] = _staticFee;
  }

  function updatePerformanceFee(uint256 _performanceFee, uint256 _chainId) external onlyOwner {
      performanceFee[_chainId] = _performanceFee;
  }

  function bridgeToken(address _token, uint256 _targetChainId, uint256 _amount, address _to) external payable nonReentrant {
    if(_token == nativeTokenAddr) {
        require(_amount + staticFee[_targetChainId] <= msg.value, "not-enough-static-fee");
    } else {
        require(msg.value >= staticFee[_targetChainId], "not-enough-static-fee");
        require(sphTokenAddresses[_token] != address(0), "token not registered");
        IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);
    }
    uint256 bridgeAmount = _amount.mul(10000 - performanceFee[_targetChainId]).div(10000);
    emit BridgeSwapStarted(_token, bridgeAmount , _targetChainId, _to);
  }

  function fillBridgeSwap(bytes32 originTxHash, address _token, uint256 _chainId, address _to, uint256 _amount) external payable onlyOwner {
    require(!isFilled[_chainId][originTxHash], "already-filled");
    if(reversePairs[_chainId][_token] == nativeTokenAddr) {
        address payable to = payable(_to);
        if (address(this).balance > _amount)
          to.transfer(_amount);
        else {
          to.transfer(address(this).balance);
          SphTempToken(sphTokenAddresses[_token]).mint(_to, _amount - address(this).balance);
        }
    } else {
        if (IERC20(reversePairs[_chainId][_token]).balanceOf(address(this)) > _amount) {
          IERC20(reversePairs[_chainId][_token]).safeTransfer(_to, _amount);
        } else {
          IERC20(reversePairs[_chainId][_token]).safeTransfer(_to, IERC20(reversePairs[_chainId][_token]).balanceOf(address(this)));
          SphTempToken(sphTokenAddresses[_token]).mint(_to, _amount - IERC20(_token).balanceOf(address(this)));
        }
    }
    isFilled[_chainId][originTxHash] = true;
    emit BridgeSwapFilled(_chainId, _token, _amount, _to);
  }

  function withdrawToken(address _token, uint256 _amount) external onlyOwner {
    IERC20(_token).safeTransfer(msg.sender, _amount);
  }

  function withdrawNativeCurrency() external payable onlyOwner {
      address payable msgSender = payable(msg.sender);
      msgSender.transfer(address(this).balance);
  }

  function withdrawNativeCurrency(uint256 _amount) external payable onlyOwner {
      address payable msgSender = payable(msg.sender);
      msgSender.transfer(_amount);
  }
}
