contract Ho is ERC20PresetFixedSupply, Ownable {

    address private _usdt = 0x55d398326f99059fF775485246999027B3197955;
    address public immutable uniswapV2Pair;
    mapping(address => bool) public whiteList;
    mapping(address => bool) public blackList;

    uint public _buytax;
    uint public _buytax1;
    uint public _buytax2;

    address public buytax_adr;
    address public buytax_adr1;
    address public buytax_adr2;
    uint public _tax2;
    uint public _lastTime;
    uint public pause;

    address public tax_adr2;
    constructor() ERC20PresetFixedSupply("HO", "HO", 200000000 * 10 ** decimals(), _msgSender()) {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0xDB9F1e7738c2f2aE89C520f76F2937EE52bC8209);
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this),_usdt);
        whiteList[msg.sender] = true;
        _buytax = 50;
        _buytax1 = 30;
        _buytax2 = 20;

        
        buytax_adr = 0x82dbDF473C03488B70efE26ef0459e2c2b608812;
        buytax_adr1 = 0x82dbDF473C03488B70efE26ef0459e2c2b608812;
        buytax_adr2 = 0x0000000000000000000000000000000000000000;
        
        _tax2 = 50;
        tax_adr2 = 0x4Dc34998f5606d253eB286a6bA4DBd2b8477b98b;
        pause = 0;
    }

 
    function setPause(uint _pause)
    public
    onlyOwner{
        require(_pause <= 1 , "pause is on");
        require(pause != _pause, "pause is on");
        pause = _pause;
    }

    function setBlack(address _adr)
    public
    onlyOwner{
        require(blackList[_adr] == false, "it is black");
        blackList[_adr] = true;
    }
 
    function removeBlack(address _adr)
    public
    onlyOwner{
        require(blackList[_adr] == true, "it is not black");
        blackList[_adr] = false;
    }

    function setBuyTime(uint _time)
    public
    onlyOwner{
        require(block.timestamp < _time, "_time must > now");
        _lastTime = _time;
    }
 
    function setBuyTax(uint _num,uint _num1,uint _num2,address _adr,address _adr1)
    public
    onlyOwner{
        require(_num + _num1 + _num2  < 450, "_num high");
        _buytax = _num;
        _buytax1 = _num1;
        _buytax2 = _num2;

 
        buytax_adr = _adr;
        buytax_adr1 = _adr1;
    }

    function setSellTax(uint _num)
    public
    onlyOwner{
          require(_num < 450, "_num  high");
        _tax2 = _num;
    }

    function setWhite(address _adr)
    public
    onlyOwner{
        require(whiteList[_adr] == false, "it is white");
        whiteList[_adr] = true;
    }

    function removeWhite(address _adr)
    public
    onlyOwner{
        require(whiteList[_adr] == true, "it is not white");
        whiteList[_adr] = false;
    }
    function _transfer(address sender, address recipient, uint256 amount) internal virtual override {
        
        require(blackList[sender] == false , "not  transfer");
        require(blackList[recipient] == false , "not  transfer");

        if(sender == uniswapV2Pair && whiteList[recipient] == false){
            require(pause == 0 , "not  transfer");
            uint256 taxAmount = amount * _tax2 / 1000;
            super._transfer(sender, tax_adr2, taxAmount);
            amount -= taxAmount;
        }else if(recipient == uniswapV2Pair && whiteList[sender] == false){
            require(pause == 0 , "not  transfer");
            require(block.timestamp >= _lastTime, "not  buy now");
            uint256 taxAmount = amount * _buytax / 1000;
            super._transfer(sender, buytax_adr, taxAmount);

            uint256 taxAmount1 = amount * _buytax1 / 1000;
            super._transfer(sender, buytax_adr1, taxAmount1);

            uint256 taxAmount2 = amount * _buytax2 / 1000;
            super._transfer(sender, buytax_adr2, taxAmount2);
            
            amount = amount - taxAmount- taxAmount1- taxAmount2;
        }

        super._transfer(sender, recipient, amount);
    }


    function  batchTranfer(address[] calldata _adr,uint256[] calldata _num) public{
        require(_adr.length == _num.length,"length is err");
        for(uint i=0;i<_adr.length;i++){
            transfer(_adr[i],_num[i]);
        }

    }
}
