pragma solidity 0.7.5;

import "./interfaces/rUMBV2.sol";
import "./interfaces/Blacklisted.sol";
import "./interfaces/OnDemandToken.sol";

contract rUMB2BSC is Blacklisted, OnDemandToken {
    constructor (
        address _owner,
        uint256 _maxAllowedTotalSupply,
        string memory _name,
        string memory _symbol
    )
        Owned(_owner)
        ERC20(_name, _symbol)
        MintableToken(_maxAllowedTotalSupply) {}

    /// @dev normally we should use `assertMaxSupply(_amount)` to check and update amounts
    ///         but for foreign tokens we should not, because we can mint/burn many times
    ///         and it should not affect max total supply
    function mint(address _holder, uint256 _amount)
        external
        override
        onlyOwnerOrMinter()
    {
        require(_amount != 0, "zero amount");
        require(totalSupply() + _amount <= maxAllowedTotalSupply, "total supply limit exceeded");

        _mint(_holder, _amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        override(Blacklisted, ERC20)
    {
        Blacklisted._beforeTokenTransfer(from, to, amount);
    }
}
