pragma solidity ^0.8.19;

interface IERC20 {
   


    event Transfer(address indexed from, address indexed to, uint256 value);

 
    event Approval(address indexed owner, address indexed spender, uint256 value);

    function totalSupply() external view returns (uint256);

    function balanceOf(address account) external view returns (uint256);

 
    function transfer(address to, uint256 value) external returns (bool);

    function allowance(address owner, address spender) external view returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transferFrom(address from, address to, uint256 value) external returns (bool);

}

contract BTCDTZfun{
    address private owner;
    BTCDTZ[] public arrayBTCDTZ;
    uint public totalAmount;

    struct BTCDTZ{
        address _Address;
        uint _Amount;
        string _User;
    }

    constructor(){
        owner = msg.sender;
        totalAmount = 0;
    }

    //events
    event NewDeposit(uint totalAmount, address wallet, uint amount, string user);


    function buyBTCDTZ(string memory user) public payable{
        if(msg.value>0){
            arrayBTCDTZ.push(BTCDTZ(msg.sender, msg.value, user));
            totalAmount = totalAmount + msg.value;
            emit NewDeposit(totalAmount, msg.sender, msg.value, user);
            
        }
    }

    function BBTCDTZCounter() public view returns(uint){
        return arrayBTCDTZ.length;
    }

    function get_one_BBTCDTZ(uint thuTu) public view returns(address, uint, string memory){
        return (arrayBTCDTZ[thuTu]._Address, arrayBTCDTZ[thuTu]._Amount, arrayBTCDTZ[thuTu]._User);
    }


    modifier onlyMaster(){
        require(msg.sender == owner, "Only master (who creatre thia SM) can call this function");
        _;
    }
    event Successfulmoneytransfer(address receiver, uint amount);
    function withdraw(address payable receiver) external onlyMaster{
        uint amount = address(this).balance;
        (bool result, bytes memory data) = receiver.call{value:amount}("");
        require(result, "Failed to send ether!");
        emit Successfulmoneytransfer(receiver, amount);
    }

    // Function withdrawBalance()
        IERC20 public  tokenBTCDTZ = IERC20(0xA7308604B0cef4E1E7efE22643191FEa24e12170);
    uint32 public ratio = 40000000; // 1 BNB = 40000000 BTCDTZ
                                    //msg.value = ?

    function Buy_BTCDTZ() public payable {
    require(msg.value>0, "Erro amount BNB");
    uint256 amountBTCDTZ = msg.value * ratio;
    require(tokenBTCDTZ.balanceOf(address(this))>0, " Out of BTCDTZ");
    tokenBTCDTZ.transfer(msg.sender, amountBTCDTZ);
    }

    function withdrawBNB() public {
        require(address(this).balance>0, "No BNB in contract");
        require(msg.sender==owner, "You are no allowed to withdraw");
        payable(owner).transfer(address(this).balance);
    }

    function changeRatio(uint32 newRatio) public {
        require(address(this).balance>0, "No BNB in contract");
        require(newRatio>=0, "Wrong ratio");
        ratio = newRatio;
    }

}
