// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

contract viewplay {
    mapping (address => uint) public balances;
    mapping (address => mapping (address => uint)) public allowance;

    uint private totalSupply = 8000000000 * 10 ** 18; // 8 milliards de tokens avec 18 décimales
    string public name = "view play coin";
    string public symbol = "VP";
    uint public decimals = 18;

    string public logoUrl = "https://i.imgur.com/K4JKYnf.png";
    address public owner;

    event Transfer(address indexed from, address indexed to, uint value);
    event Approval(address indexed owner, address indexed spender, uint value);
    event LogoUrlUpdated(string newLogoUrl);

    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function");
        _;
    }

    constructor() {
        balances[msg.sender] = totalSupply;
        owner = msg.sender;
    }

    function balanceOf(address user) public view returns (uint) {
        return balances[user];
    }

    function transfer(address to, uint value) public returns (bool) {
        require(balanceOf(msg.sender) >= value, "Insufficient balance");
        balances[to] += value;
        balances[msg.sender] -= value;
        emit Transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint value) public returns (bool) {
        require(balanceOf(from) >= value, "Insufficient balance");
        require(allowance[from][msg.sender] >= value, "Insufficient allowance");
        balances[to] += value;
        balances[from] -= value;
        emit Transfer(from, to, value);
        return true;
    }

    function approve(address spender, uint value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function getLogoUrl() public view returns (string memory) {
        return logoUrl;
    }

    function updateLogoUrl(string memory newLogoUrl) public onlyOwner {
        logoUrl = newLogoUrl;
        emit LogoUrlUpdated(newLogoUrl);
    }
}
