      "content": "//SPDX-License-Identifier: UNLICENSED
pragma solidity <=0.8.10;
import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "openzeppelin-solidity/contracts/token/ERC20/utils/SafeERC20.sol";
import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";

contract VestingMarketing {
    // +-----------+------------+-----------+
    // |31/03/2023 | 20/06/2022 - 20/12/2023|
    // +-----------+------------+-----------+
    // | 2,300,000 |    862,500    	        |    
    // +-----------+------------+-----------+

    IERC20 private _token;
    address public marketing;
    uint256 constant public totalAmount =  23_000_000* 10**18;
    uint256 constant public marAmount =  2_300_000* 10**18;
    uint256 constant public amountPerMonth =  862_500* 10**18;

    struct marketingInfo {
        uint256 withdrawtime;
        uint256 amount;
        bool withdrawed;
    }
    marketingInfo[] public withdrawTime;

    function initWithdrawTime() internal {
        //2022
        marketingInfo memory mar22infor = marketingInfo(1647993600,marAmount,false);//23/03/2022
        marketingInfo memory apr22infor = marketingInfo(1650412800,amountPerMonth,false);//20/04/2022
        marketingInfo memory may22infor = marketingInfo(1653004800,amountPerMonth,false);//20/05/2022
        marketingInfo memory jun22infor = marketingInfo(1655683200,amountPerMonth,false);//20/06/2022
        marketingInfo memory jul22infor = marketingInfo(1658275200,amountPerMonth,false);//20/07/2022
        marketingInfo memory aug22infor = marketingInfo(1660953600,amountPerMonth,false);//20/08/2022
        marketingInfo memory sep22infor = marketingInfo(1663632000,amountPerMonth,false);//20/09/2022
        marketingInfo memory oct22infor = marketingInfo(1666224000,amountPerMonth,false);//20/10/2022
        marketingInfo memory nov22infor = marketingInfo(1668902400,amountPerMonth,false);//20/11/2022
        marketingInfo memory dec22infor = marketingInfo(1671494400,amountPerMonth,false);//20/12/2022
         //2022
        withdrawTime.push(mar22infor);
        withdrawTime.push(apr22infor);
        withdrawTime.push(may22infor);
        withdrawTime.push(jun22infor);
        withdrawTime.push(jul22infor);
        withdrawTime.push(aug22infor);
        withdrawTime.push(sep22infor);
        withdrawTime.push(oct22infor);
        withdrawTime.push(nov22infor);
        withdrawTime.push(dec22infor);
        // 2023
        marketingInfo memory jan23infor = marketingInfo(1674172800,amountPerMonth,false);//20/01/2023
        marketingInfo memory feb23infor = marketingInfo(1676851200,amountPerMonth,false);//20/02/2023
        marketingInfo memory mar23infor = marketingInfo(1679270400,amountPerMonth,false);//20/03/2023
        marketingInfo memory apr23infor = marketingInfo(1681948800,amountPerMonth,false);//20/04/2023
        marketingInfo memory may23infor = marketingInfo(1684540800,amountPerMonth,false);//20/05/2023
        marketingInfo memory jun23infor = marketingInfo(1687219200,amountPerMonth,false);//20/06/2023
        marketingInfo memory jul23infor = marketingInfo(1689811200,amountPerMonth,false);//20/07/2023
        marketingInfo memory aug23infor = marketingInfo(1692489600,amountPerMonth,false);//20/08/2023
        marketingInfo memory sep23infor = marketingInfo(1695168000,amountPerMonth,false);//20/09/2023
        marketingInfo memory oct23infor = marketingInfo(1697760000,amountPerMonth,false);//20/10/2023
        marketingInfo memory nov23infor = marketingInfo(1700438400,amountPerMonth,false);//20/11/2023
        marketingInfo memory dec23infor = marketingInfo(1703030400,amountPerMonth,false);//20/12/2023
        //2023
        withdrawTime.push(jan23infor);
        withdrawTime.push(feb23infor);
        withdrawTime.push(mar23infor);
        withdrawTime.push(apr23infor);
        withdrawTime.push(may23infor);
        withdrawTime.push(jun23infor);
        withdrawTime.push(jul23infor);
        withdrawTime.push(aug23infor);
        withdrawTime.push(sep23infor);
        withdrawTime.push(oct23infor);
        withdrawTime.push(nov23infor);
        withdrawTime.push(dec23infor);

        // 2024
        marketingInfo memory jan24infor = marketingInfo(1705708800,amountPerMonth,false);//20/01/2024
        marketingInfo memory feb24infor = marketingInfo(1708387200,amountPerMonth,false);//20/02/2024
        marketingInfo memory mar24infor = marketingInfo(1710892800,amountPerMonth,false);//20/03/2024
        //2024
        withdrawTime.push(jan24infor);
        withdrawTime.push(feb24infor);
        withdrawTime.push(mar24infor);

    }

    constructor(ERC20 token, address marketingAddress) {
        _token = token;
        marketing = marketingAddress;
        initWithdrawTime();
    }

    /**
     * @dev send token from sender to this contract
     *
     * - 'amount' amount of token in transaction
     */
    function deposit() external {
        require(msg.sender == marketing, "sender is not public sale");
        require(
            _token.balanceOf(msg.sender) >= totalAmount,
            "Insufficient account balance"
        );
        SafeERC20.safeTransferFrom(
            _token,
            msg.sender,
            address(this),
            totalAmount
        );
    }
function withdraw() external {
        require(msg.sender == marketing, "sender is not public sale");
        uint256 withdrawAmount = 0;
        uint currentTime = block.timestamp;
        for (uint256 index = 0; index < withdrawTime.length; index++) {
            marketingInfo storage infor = withdrawTime[index];
            if (
               infor.withdrawed == false && infor.withdrawtime <= currentTime
            ) {
                withdrawAmount += infor.amount;
                infor.withdrawed = true;
            }
        }
        require(withdrawAmount>0,"withdraw amount is zero");
        require(
            _token.balanceOf(address(this)) >= withdrawAmount,
            "Insufficient account balance"
        );
        _token.transfer(marketing, withdrawAmount);
    }
}
