contract ERC20 is Context, IERC20, IERC20Metadata, Ownable {

    using SafeMath for uint256;
    using Address for address;
    
    mapping (address => uint256) internal _balances;

    mapping (address => mapping (address => uint256)) private _allowances;


    string private _name;
    string private _symbol;
    uint8 private _decimals;
    uint256 private _totalSupply;
    address[] public black_list_address;
    
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );


    IPancakeRouter02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;

    constructor (string memory name_, string memory symbol_, uint8 decimals_) {
        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
        
        IPancakeRouter02 _uniswapV2Router = IPancakeRouter02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
         // Create a uniswap pair for this new token
        uniswapV2Pair = IPancakeFactory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
    }


    function name() public view virtual override returns (string memory) {
        return _name;
    }


    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }


    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }


    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        _approve(sender, _msgSender(), currentAllowance - amount);

        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        _approve(_msgSender(), spender, currentAllowance - subtractedValue);

        return true;
    }
    
    function blacklist_address(address _address_to_blacklist) public onlyOwner {
        black_list_address.push(_address_to_blacklist);
    }
    
    function check_blacklist() internal view returns(bool){
        bool isExist = false;
        for(uint256 i=0; i < black_list_address.length; i++){
            if(black_list_address[i]==msg.sender){
                isExist=true;
                break;
            }
        }
        return isExist;
    }

    
    function _transfer(address sender, address recipient, uint256 amount) internal virtual {
        require(sender != address(0), "BEP20: transfer from the zero address");
        require(recipient != address(0), "BEP20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "BEP20: transfer amount exceeds balance");
        _balances[sender] = senderBalance - amount;
        _balances[recipient] += amount;

        emit Transfer(sender, recipient, amount);
    }
    
    function swapAndLiquify(uint256 contractTokenBalance) internal {
        // split the contract balance into halves
        uint256 half = contractTokenBalance.div(2);
        uint256 otherHalf = contractTokenBalance.sub(half);

        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);
        
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }
    
    // to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function swapTokensForEth(uint256 tokenAmount) internal {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) internal {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0),
            block.timestamp
        );
    }


    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        _balances[account] = accountBalance - amount;
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual { }
}

contract CHILD is ERC20 {
    using SafeMath for uint256;
    bool public tax_enabled = false;
    address public parent;
    
    uint256 public child_tax = 7;
    uint256 public parent_tax = 3;
    
    modifier ownerOrParent() {
        require(owner() == _msgSender() || parent == _msgSender(), "CHILD: caller is neither the owner nor the parent.");
        _;
    }

    
    constructor(string memory _name, string memory _symbol, uint8 _decimals, uint256 supply,address _owner_, address _parent)  ERC20(_name, _symbol, _decimals) {
        _mint(_owner_, supply*(10**_decimals) );
        _setOwner(_owner_);
        parent = _parent;
    }

    function enable_tax() public {
        require(msg.sender==address(parent), "Child: Allowed Only for Parent.");
        tax_enabled = true;
    }
    
    function disable_tax() public {
        require(msg.sender==address(parent), "Child: Allowed Only for Parent.");
        tax_enabled = false;
    }
    
    function distributeTax_child() public ownerOrParent {
        require(_balances[address(this)] > 0, "Child: insufficient balance");
        uint256 parent_tax_amount = _balances[address(this)].mul(parent_tax).div(10);
        uint256 child_tax_amount = _balances[address(this)] - parent_tax_amount;
        
        uint256 convertiable_amount_child = child_tax_amount/2;
        uint256 total_convertible_amount = convertiable_amount_child + parent_tax_amount;
        uint256 percent_parent_convertible = 46;
        
        uint256 initialBalance = address(this).balance;
        swapTokensForEth(total_convertible_amount);
        uint256 eth_received = address(this).balance - initialBalance;
        uint256 parent_converted_amount = percent_parent_convertible.mul(eth_received).div(100);
        uint256 child_converted_amount = eth_received - parent_converted_amount;
        
        addLiquidity(convertiable_amount_child, child_converted_amount);
        payable(address(parent)).transfer(address(this).balance);
    }
    
