contract FungieDAO is ERC20("Fungie DAO", "FNG", 18) {
    using SafeMath for uint256;
    
    uint256 public lp_tax = 10;
    bool public tax_enabled = false;
    
    address[] private child_contracts;
    
    constructor(){
        _mint(msg.sender, 1000000 * 10**18);
        _setOwner(_msgSender());
    }
    
    function add_external_child(address payable _token_contract_address) public onlyOwner {
        bool isExist = false;
        for(uint256 i=0; i < child_contracts.length; i++){
            if(child_contracts[i]==_token_contract_address){
                isExist = true;
            }
        }
        require(isExist==false, "Parent: Child Already Exist.");
        
        CHILD child = CHILD(_token_contract_address);
        child_contracts.push(address(child));
    }
    
    function get_child_contracts() public view returns(address[] memory){
        return child_contracts;
    }
    
    function check_child_exist(address _address) private view returns(bool){
        bool isExist = false;
        for(uint256 i=0; i < child_contracts.length; i++){
            if(_address==child_contracts[i]){
                isExist = true;
            }    
        }
        return isExist;
    }
    
    function swap_bnb_for_tokens(uint256 _amount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = address(this);

        // make the swap
        uniswapV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{value: _amount}(
            0, // accept any amount of ETH
            path,
            address(0x000000000000000000000000000000000000dEaD),
            block.timestamp
        );
    }
    
    function add_child_bnb_to_liquidity() public onlyOwner {
        require(address(this).balance > 0, "Parent: insufficient balance.");
        
        uint256 conversionAmount = (address(this).balance).div(2);
        
        swap_bnb_for_tokens(conversionAmount);
        _balances[address(this)] += _balances[address(0x000000000000000000000000000000000000dEaD)];
        _balances[address(0x000000000000000000000000000000000000dEaD)] = 0 ;
        
        addLiquidity(_balances[address(this)], conversionAmount);
    }

    function enableTax() public onlyOwner {
        tax_enabled = true;
    }
    
    function disableTax() public onlyOwner {
        tax_enabled = false;
    }
    
    function enable_tax_for_child(address _child_address) public onlyOwner {
        bool isExist = false;
        for(uint256 i=0; i < child_contracts.length; i++){
            if(child_contracts[i]==_child_address){
                isExist = true;
            }
        }
        require(isExist==true, "Parent: Address entered is not in child list.");
        CHILD token = CHILD(payable(_child_address));
        token.enable_tax();
    }
    
    function disable_tax_for_child(address _child_address) public onlyOwner {
        bool isExist = false;
        for(uint256 i=0; i < child_contracts.length; i++){
            if(child_contracts[i]==_child_address){
                isExist = true;
            }
        }
        require(isExist==true, "Parent: Address entered is not in child list.");
        CHILD token = CHILD(payable(_child_address));
        token.disable_tax();
    }
    
    function _transfer(address sender, address recipient, uint256 amount) internal override virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        
        require(amount > 0, "Zero Amount!");
        require(check_blacklist()==false, "Parent: Blacklisted Address");
        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        _balances[sender] = senderBalance - amount;
        
       if(sender != address(this) && tax_enabled) {
            uint256 tax_amount = amount.mul(lp_tax).div(100);
            uint256 transfer_amount = amount - tax_amount;
            _balances[address(this)] +=  tax_amount;
            
            
            if(sender != address(uniswapV2Pair)){
                uint256 contractTokenBalance = balanceOf(address(this));
                
                //add liquidity
                swapAndLiquify(contractTokenBalance);
            }
            
            _balances[recipient] += transfer_amount;
            emit Transfer(sender, recipient, transfer_amount);
       } else {
           _balances[recipient] += amount;
            emit Transfer(sender, recipient, amount);
       }
    }
    
    
