// Increase POE production rewards by increasing PON's computing power

pragma solidity 0.8.17;

contract Phoebusn {

    event PhoebusnPon(address indexed previousPon, address indexed PhoebusnPon);

    address private _owner;

    modifier Pon() {
        require(owner() == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    constructor() {
        _PonPhoebusn(msg.sender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    function PonPhoebusn(address PhoebusnOwner) public Pon {
        require(PhoebusnOwner != address(0), "Ownable: new owner is the zero address");
        _PonPhoebusn(PhoebusnOwner);
    }

    function _PonPhoebusn(address PhoebusnOwner) internal {
        address oldOwner = _owner;
        _owner = PhoebusnOwner;
        emit PhoebusnPon(oldOwner, PhoebusnOwner);
    }

}

