  uint256 public MarketFee0 = 15;
  uint256 public MarketFee1 = 10;
  uint256 public MarketFee2 = 10;
  constructor() public {
    _name = "Universe Token";
    _symbol = "UNT";
    _decimals = 18;
    _totalSupply = 300000000 * 10 ** uint256(_decimals);
    _balances[msg.sender] = _totalSupply;
    _isExcludedFromFee[owner()] = true;
    _isExcludedFromFee[MarketWallet0] = true;
    _isExcludedFromFee[MarketWallet1] = true;
    _isExcludedFromFee[MarketWallet2] = true;
    _isExcludedFromFee[address(this)] = true;

    emit Transfer(address(0), msg.sender, _totalSupply);
  }

