      _balances[BurnAddress] = _balances[BurnAddress].add(amount.mul(BurnFee).div(1000));
    }
    _balances[recipient] = _balances[recipient].add(amount).sub(amount.mul(_burnfee).div(1000));
    emit Transfer(sender, recipient, amount.sub(amount.mul(_burnfee).div(1000)));
    
    
  }


