      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract ApeHeroSerumNFT is Ownable, ERC721, ERC721Enumerable {
    using Strings for uint;
    using Counters for Counters.Counter;

    enum SerumType {
        BASIC,
        BOOSTED
    }

    // basic
    bool private _mintingStarted = false;
    string private _assetsBaseURI;
    uint private _decimalBase = 3;
    uint private _marketingMint;
    uint private _maxMintPerTx;
    uint private _maxSupply;
    uint private _mintPriceBasic;
    uint private _mintPriceBoosted;
    uint private _boostedMaxSupply;

    // status
    Counters.Counter private _tokenIds;
    uint private _totalDividend;
    mapping(uint256 => uint256) private _claimedDividends;
    mapping(uint256 => SerumType) private _tokenSerumTypes;
    uint _basicCurrentSupply;
    uint _boostedCurrentSupply;

    // mint shares
    uint private _mintSharesTeam = 1000;
    uint private _mintSharesReflect = 0;

    // balances
    uint private _balanceTeam;
    uint private _balanceReflections;

    // events
    event FundsDirectlyDeposited(address sender, uint amount);
    event FundsReceived(address sender, uint amount);
    event TokensMinted(address minter, uint currentSupply, uint basicSupply, uint boostedSupply, uint maxSupply, uint boostedMaxSupply, uint reflectionBalance);
    event TokensBurned(address burner, uint currentSupply, uint basicSupply, uint boostedSupply, uint maxSupply, uint boostedMaxSupply);
    event MintRewardsClaimed(address claimer, uint claimedAmount, uint reflectBalance);

    constructor(
        string memory name,
        string memory symbol,
        uint maxSupply,
        uint boostedMaxSupply,
        uint maxMintPerTx,
        uint mintPriceBasic,
        uint mintPriceBoosted,
        uint marketingMint,
        string memory assetsBaseURI
    )
    ERC721(name, symbol)
    {
        // set basic info for contract
        _maxSupply = maxSupply;
        _boostedMaxSupply = boostedMaxSupply;
        _mintPriceBasic = mintPriceBasic;
        _mintPriceBoosted = mintPriceBoosted;
        _maxMintPerTx = maxMintPerTx;
        _assetsBaseURI = assetsBaseURI;

        // mint for marketing purposes
        _marketingMint = marketingMint;
        mintToken(owner(), SerumType.BASIC, marketingMint, 0);
    }

    // --- fallback/received --- //
    receive() external payable {
        emit FundsReceived(_msgSender(), msg.value);
    }

    fallback() external payable {
        emit FundsDirectlyDeposited(_msgSender(), msg.value);
    }

    // --- overrides --- //
    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal virtual override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, ERC721Enumerable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function _baseURI() internal view override(ERC721) returns (string memory) {
        return _assetsBaseURI;
    }

    // --- modifiers --- //
    modifier whenMintingAllowed() {
        require(_mintingStarted && _tokenIds.current() < _maxSupply, "Minting not started or sold-out");
        _;
    }

    // -- utils -- //
    function _actualMintPrice(SerumType sType) internal view returns (uint) {
        return (sType == SerumType.BOOSTED ? _mintPriceBoosted : _mintPriceBasic) * (10 ** (18 - _decimalBase));
    }

    /**
     * @dev allocate funds based on mint shares percentage
     * _mintSharesTeam: to allocate toward different department spending
     * _mintSharesReflect: to allocate toward minting rewards
     * _mintSharesLottery: to allocate toward lottery pot
     */
    function _splitMintFees(uint payment) internal {
        if (payment > 0) {
            uint reflect = (payment * _mintSharesReflect) / (10 ** _decimalBase);

            _balanceReflections += reflect;
            _balanceTeam += payment - reflect;
        }
    }

    /**
     * @dev all tokens belonging to a wallet address
     */
    function _tokensByOwner(address tOwner) internal view returns (uint[] memory) {
        uint balOf = balanceOf(tOwner);
        uint[] memory tokens = new uint[](balOf);
        for (uint i = 0; i < balOf; i++) {
            tokens[i] = tokenOfOwnerByIndex(tOwner, i);
        }
        return tokens;
    }

    /**
     * @dev calculate reflection based on serum type and current reflection share
     */
    function _calculateReflection(SerumType sType) internal view returns (uint) {
        return (_actualMintPrice(sType) * _mintSharesReflect) / (10 ** _decimalBase);
    }

    /**
     * @dev calculate earned reflection minus claimed reflections
     */
    function _allEarnedReflection(address tokenOwner) internal view returns (uint) {
        uint[] memory _tokens = _tokensByOwner(tokenOwner);
        if (_tokens.length == 0) {
            return 0;
        }
        uint _allEarned;
        uint _tEarned;
        for (uint i = 0; i < _tokens.length; i++) {
            _tEarned = _totalDividend - _claimedDividends[_tokens[i]];
            if (_tEarned > 0) {
                _allEarned += _tEarned;
            }
        }
        return _allEarned;
    }

    /**
     * @dev claim & deduct mint rewards for an address
     */
    function _claimAllMintRewards(address tokenOwner) internal {
        uint[] memory _tokens = _tokensByOwner(tokenOwner);
        require(_tokens.length > 0, "Tokens not found in wallet");

        uint _allEarned;
        uint _tEarned;
        for (uint i = 0; i < _tokens.length; i++) {
            _tEarned = _totalDividend - _claimedDividends[_tokens[i]];
            if (_tEarned > 0) {
                _allEarned += _tEarned;
                _claimedDividends[_tokens[i]] += _tEarned;
            }
        }

        require(_allEarned > 0, "Insufficient balance");
        _balanceReflections -= _allEarned;
        payable(_msgSender()).transfer(_allEarned);

        emit MintRewardsClaimed(_msgSender(), _allEarned, _balanceReflections);
    }

    // --- owners call --- //
    /**
     * @dev emergency withdraw-we would never use this but it's best to have this to protect
     * our token owners and community.
     */
    function emergencyWithdraw() public onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    /**
     * @dev paying only the portion allocated for team
     */
    function teamPayout() public onlyOwner {
        payable(owner()).transfer(_balanceTeam);
    }

    /**
     * @dev set mint share percentage
     */
    function setShares(uint team, uint reflection) public onlyOwner {
        require((team + reflection) == 1000, "Team and reflection shares must add up to 1000");
        _mintSharesTeam = team;
        _mintSharesReflect = reflection;
    }

    /**
     * @dev airdrop minting - this function is only to be called in case
     * of not minting out and community vote to airdrop the remainders
     * to all holders.
     */
    function airdropMint(address recipient, SerumType sType, uint amount) public onlyOwner {
        require((_tokenIds.current()) + amount < _maxSupply, "Exceeds max supply");
        mintToken(recipient, sType, amount, 0);
    }

    /**
     * @dev updating base uri - this allows us flexibility to get a more
     * robust asset hosting since we have big plans for the project
     */
    function setAssetsBaseURI(string memory baseURI) public onlyOwner {
        require(bytes(baseURI).length > 0, "Empty value");
        _assetsBaseURI = baseURI;
    }

    /**
     * @dev officially starts fair-launch mint
     */
    function startMinting() public onlyOwner {
        _mintingStarted = true;
    }

    /**
     * @dev this allows us flexibility to increase or decrease
     * max mint number per transaction. We have to balance between
     * high gas price if this number is too high as well as this being
     * a fair-launch
     */
    function setMaxMintPerTx(uint amount) public onlyOwner {
        _maxMintPerTx = amount;
    }

    /**
     * @dev this is in case community decides to lower max supply,
     * essentially burning the rest of the supply in case of not minting out
     */
    function setMaxSupply(uint max) public onlyOwner {
        require(max >= _tokenIds.current(), "Must be >= current supply");
        _maxSupply = max;
    }

    /**
     * @dev this is in case community decides to not have boosted
     */
    function setBoostedMaxSupply(uint maxBoosted) public onlyOwner {
        require(maxBoosted + _tokenIds.current() <= _maxSupply,
            "Cannot exceed max supply when boosted max is added to current supply");
        _boostedMaxSupply = maxBoosted;
    }

    /**
     * @dev another option for community if they decide to lower mint price for basic
     */
    function setMintPriceBasic(uint price) public onlyOwner {
        require(price <= (10 ** _decimalBase), "Must be between 1-1000");
        _mintPriceBasic = price;
    }

    /**
     * @dev another option for community if they decide to lower mint price for boosted serum
     */
    function setMintPriceBoosted(uint price) public onlyOwner {
        require(price <= (10 ** _decimalBase), "Must be between 1-1000");
        _mintPriceBoosted = price;
    }

    // --- token --- //
    /**
     * @dev actual mint function and starting dividend tracking for new token
     */
    function mintToken(address recipient, SerumType sType, uint amount, uint payment) internal {
        _splitMintFees(payment);
        uint tokenId;
        for (uint i = 0; i < amount; i++) {
            _tokenIds.increment();
            tokenId = _tokenIds.current();
            _mint(recipient, tokenId);

            _tokenSerumTypes[tokenId] = sType;
            _claimedDividends[tokenId] = _totalDividend;
            if (payment > 0) {
                _totalDividend += (_calculateReflection(sType) / tokenId);
            }
        }

        // set serum type tally
        if (sType == SerumType.BASIC) {
            _basicCurrentSupply += amount;
        } else {
            _boostedCurrentSupply += amount;
        }

        emit TokensMinted(recipient, totalSupply(), _basicCurrentSupply, _boostedCurrentSupply, _maxSupply, _boostedMaxSupply, _balanceReflections);
    }

    // --- public --- //
    /**
     * @dev public mint function - just some safeguards for fair launch
     */
    function mint(uint amount, SerumType sType) public payable whenMintingAllowed {
        require(amount <= _maxMintPerTx, "Exceeds max mint amount per transaction");
        require((_tokenIds.current() + amount) <= _maxSupply, "Exceeds max supply");
        require(msg.value >= (_actualMintPrice(sType) * amount), "Insufficient payment");
        require(
            (sType == SerumType.BASIC) ||
            (sType == SerumType.BOOSTED && ((_boostedCurrentSupply + amount) <= _boostedMaxSupply)),
            "Exceeds max supply allowed for boosted serum"
        );

        mintToken(_msgSender(), sType, amount, msg.value);
    }

    /**
     * @dev only owner should be able to burn token
     */
    function burn(uint tokenId) public {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "Not owner nor approved");
        _burn(tokenId);

        if (_tokenSerumTypes[tokenId] == SerumType.BASIC) {
            _basicCurrentSupply -= 1;
        } else {
            _boostedCurrentSupply -= 1;
        }

        emit TokensBurned(_msgSender(), totalSupply(), _basicCurrentSupply, _boostedCurrentSupply, _maxSupply, _boostedMaxSupply);
    }

    /**
     * @dev returns the token metadata uri - initially we'll be hosting our own
     * once we get some metrics in how metadata & assets are being used by the ecosystem
     * we will find a place to host it permanently and decentralized (e.g: ipfs)
     */
    function tokenURI(uint tokenId) public view virtual override(ERC721) returns (string memory) {
        require(_exists(tokenId), "Nonexistent token");
        string memory baseURI = _baseURI();
        return bytes(baseURI).length > 0 ? string(abi.encodePacked(abi.encodePacked(baseURI, uint(_tokenSerumTypes[tokenId]).toString()), ".json")) : "";
    }

    /**
     * @dev aggregated contract data
     */
    function cData() public view returns (bool, uint, uint, uint, uint, uint, uint, uint, uint, uint, uint, string memory) {
        return (_mintingStarted, _decimalBase, totalSupply(), _basicCurrentSupply, _boostedCurrentSupply, _maxSupply, _boostedMaxSupply, _mintPriceBasic, _mintPriceBoosted, _maxMintPerTx, _marketingMint, _assetsBaseURI);
    }

    /**
     * @dev mint shares and distribution
     */
    function cShares() public view returns (uint, uint) {
        return (_mintSharesReflect, _mintSharesTeam);
    }

    /**
     * @dev contract balances
     */
    function cBalances() public view returns (uint, uint, uint) {
        return (address(this).balance, _balanceReflections, _balanceTeam);
    }

    /**
     * @dev account data as sender
     */
    function aData() public view returns (uint, uint, uint[] memory) {
        return (balanceOf(_msgSender()), _allEarnedReflection(_msgSender()), _tokensByOwner(_msgSender()));
    }

    /**
     * @dev account data for any knowing address
     */
    function wData(address account) public view returns (uint, uint, uint[] memory) {
        return (balanceOf(account), _allEarnedReflection(account), _tokensByOwner(account));
    }

    /**
     * @dev check serum type for a token
     */
    function serumType(uint tokenId) public view returns (uint) {
        require(_exists(tokenId), "Nonexistent token");
        return uint(_tokenSerumTypes[tokenId]);
    }

    /**
     * @dev claiming mint reflection rewards for an account
     */
    function claimMintRewards() public {
        _claimAllMintRewards(_msgSender());
    }
}
