        require(isContract(target), "Address: delegate call to non-contract");
        (bool success, bytes memory returndata) = target.delegatecall(data);
        return _verifyCallResult(success, returndata, errorMessage);
    }

    function _verifyCallResult(
        bool success,
        bytes memory returndata,
        string memory errorMessage
    ) private pure returns (bytes memory) {
        if (success) {
            return returndata;
        } else {
            if (returndata.length > 0) {
                assembly {
                    let returndata_size := mload(returndata)
                    revert(add(32, returndata), returndata_size)
                }
            } else {
                revert(errorMessage);
            }
        }
    }
}

// File: contracts/ Monetas.sol

pragma solidity 0.8.7;

contract Monetas is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;
    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) private _isQuant;

    mapping(address => bool) public _isStaking;

    address public _stakingContractAddress;

    uint256 private _tTotal;
    uint256 private _tMax;
    string private _name;
    string private _symbol;

    uint8 private _decimals = 18;

    uint256 public _burnFee;
    uint256 private _previousBurnFee = _burnFee;

    uint256 public _totalBurnt = 0;
    uint256 public _burnLimit;

    constructor(
        uint256 _currentSupply,
        uint256 _maxSupply,
        string memory name_,
        string memory symbol_,
        uint256 burnFee,
        uint256 burnLimit
    ) {
        _tTotal = _currentSupply;
        _tMax = _maxSupply;
        _name = name_;
        _symbol = symbol_;
        _burnFee = burnFee;
        _burnLimit = burnLimit;
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        _tOwned[owner()] = _tTotal;
        emit Transfer(address(0), owner(), _tTotal);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function maxTotalSupply() public view returns (uint256) {
        return _tMax;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _tOwned[account];
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function claimNative(address payable to) public onlyOwner {
        require(address(this).balance > 0, "Monetas: No balance to transfer");
        to.transfer(address(this).balance);
    }

    function claimTokens(address _to, uint256 _amount) public onlyOwner {
        require(
            balanceOf(address(this)) >= _amount,
            "Monetas: No balance to transfer"
        );

        IERC20(address(this)).transfer(_to, _amount);
    }

    function burn(uint256 _amount) external onlyOwner {
        _transferExcluded(msg.sender, address(0), _amount);
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function QuantWallet(address account) public onlyOwner {
        _isQuant[account] = true;
    }

    function deQuantWallet(address account) public onlyOwner {
        _isQuant[account] = false;
    }

    function flagStakeWallet(
        address[] memory addresses,
        bool flag
    ) external onlyOwner {
        require(
            addresses.length < 200,
            "Monetas: flag less than 200 wallets at a time"
        );

        for (uint8 i = 0; i < 200; i++) {
            if (i == addresses.length) {
                break;
            }
            _isStaking[addresses[i]] = flag;
        }
    }

    function mint(uint256 _amount) external onlyOwner {
        require(_tTotal.add(_amount) <= _tMax, "Monetas: Max Supply Limit");
        _tTotal += _amount;
        _tOwned[owner()] += _amount;
        emit Transfer(address(0), owner(), _amount);
    }

    function setBurnFeePercent(uint256 burnFee) external onlyOwner {
        _burnFee = burnFee;
    }

    function setMaxBurnPercent(uint256 maxTxPercent) external onlyOwner {
        _burnLimit = _tTotal.mul(maxTxPercent).div(10 ** 5);
    }

    function setStakingContract(address stakingContract) external onlyOwner {
        _stakingContractAddress = stakingContract;
    }

    receive() external payable {}

    struct FeesInfo {
        uint256 tTransferAmount;
        uint256 tBurn;
    }

    function _getValues(uint256 tAmount) private returns (uint256, uint256) {
        FeesInfo memory feesInfo;
        (feesInfo.tTransferAmount, feesInfo.tBurn) = _getTValues(tAmount);
        return (feesInfo.tTransferAmount, feesInfo.tBurn);
    }

    struct TValues {
        uint256 tBurn;
        uint256 tTransferAmount;
    }

    function _getTValues(uint256 tAmount) private returns (uint256, uint256) {
        TValues memory tValues;
        tValues.tBurn = calculateBurnFee(tAmount);

        _totalBurnt += tValues.tBurn;

        if (_totalBurnt > _burnLimit) {
            tValues.tBurn -= (_totalBurnt - _burnLimit);
            _burnFee = 0;
            _totalBurnt = _burnLimit;
        }
        tValues.tTransferAmount = tAmount.sub(tValues.tBurn);
        return (tValues.tTransferAmount, tValues.tBurn);
    }

    function _takeBurn(address sender, uint256 tBurn) private {
        _tOwned[address(0)] = _tOwned[address(0)].add(tBurn);
        emit Transfer(sender, address(0), tBurn);
    }

    function calculateBurnFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_burnFee).div(10 ** 5);
    }

    function removeAllFee() private {
        _previousBurnFee = _burnFee;
        _burnFee = 0;
    }

    function restoreAllFee() private {
        _burnFee = _previousBurnFee;
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function isQuant(address account) public view returns (bool) {
        return _isQuant[account];
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address from, address to, uint256 amount) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        require(!_isQuant[from], "Monetas: unexpected error");

        if (_isStaking[from]) {
            require(
                to == _stakingContractAddress,
                "Monetas: transfer to non-stake"
            );
        }

        bool takeFee = true;
        if (
            _isExcludedFromFee[from] ||
            _isExcludedFromFee[to] ||
            _totalBurnt >= _burnLimit
        ) {
            takeFee = false;
        }
        _tokenTransfer(from, to, amount, takeFee);
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount,
        bool takeFee
    ) private {
        if (!takeFee) removeAllFee();
        if (_isExcludedFromFee[sender] || _isExcludedFromFee[recipient]) {
            _transferExcluded(sender, recipient, amount);
        } else {
            _transferStandard(sender, recipient, amount);
        }
        if (!takeFee) restoreAllFee();
    }

    function _transferStandard(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        (uint256 tTransferAmount, uint256 tBurn) = _getValues(tAmount);
        if (tBurn != 0) _takeBurn(sender, tBurn);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferExcluded(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tAmount);
        emit Transfer(sender, recipient, tAmount);
    }
}
