    "/contracts/Treasury.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./ISTV2.sol";

contract Treasury is Ownable {
    address private SECURITY_TOKEN;
    string private SBID;
    bool private treasuryInitiated;
    ISTV2 STV2;

    error AccessDenied(address account, bytes32 role);
    error TreasuryAlreadyInitiated();
    error InitiateTreasury();

    function initTreasury(
        address _SecurityToken,
        string memory _SBID
    ) external onlyOwner {
        if (treasuryInitiated) revert TreasuryAlreadyInitiated();
        treasuryInitiated = true;

        SECURITY_TOKEN = _SecurityToken;
        SBID = _SBID;
        STV2 = ISTV2(SECURITY_TOKEN);
    }

    function withdraw(
        address _token,
        address _reciever,
        uint256 _amount
    ) external {
        if (!treasuryInitiated) revert InitiateTreasury();
        _hasPermissionAs(STV2.FINANCIAL_MANAGER_ROLE(), _msgSender());
        IERC20(_token).transfer(_reciever, _amount);
    }

    function giveAllowanceFor(
        address _ERC20token,
        address _spender,
        uint256 _amount
    ) external {
        if (!treasuryInitiated) revert InitiateTreasury();
        _hasPermissionAs(STV2.FINANCIAL_MANAGER_ROLE(), _msgSender());
        IERC20(_ERC20token).approve(_spender, _amount);
    }

    function getSBID() external view returns (string memory) {
        return SBID;
    }

    function getSTV2() external view returns (address) {
        return SECURITY_TOKEN;
    }

    function _hasPermissionAs(bytes32 role, address account) internal view {
        if (!STV2.hasRole(role, account)) revert AccessDenied(account, role);
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes memory
    ) external pure returns (bytes4) {
        return this.onERC721Received.selector;
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) external pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }
}
"
