pragma solidity ^0.8.0;

interface ISTV2 {
    function transfer(address _to, uint256 _amount) external returns (bool);

    function transferFrom(
        address _from,
        address _to,
        uint256 _amount
    ) external returns (bool);

    function lockUpTokensOnAddress(
        address[] memory _bundleTo,
        uint256[] memory _bundleAmounts,
        uint256 _daysToLock
    ) external returns (bool);

    function balanceOf(address _account) external view returns (uint256);

    function corporateTreasury() external view returns (address);

    function hasRole(
        bytes32 role,
        address account
    ) external view returns (bool);

    function DEFAULT_ADMIN_ROLE() external view returns (bytes32);

    function FINANCIAL_MANAGER_ROLE() external view returns (bytes32);

    function COMPLIANCE_MANAGER_ROLE() external view returns (bytes32);

    function RECOVERY_MANAGER_ROLE() external view returns (bytes32);
}
"
