    "contracts/production/clickTeamDistributor.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract ClickTeamDistributor is Ownable {
    mapping(address => bool) public isAdmin;
    uint16[7] public feePercent;
    address[7] public feeCollectors;

    constructor(address[7] memory admins) Ownable(_msgSender()) {
        feePercent = [543, 6739, 1223, 680, 136, 462, 217];
        for (uint i = 0; i < admins.length; i++) {
            isAdmin[admins[i]] = true;
        }
        feeCollectors = admins;
    }

    function withdraw() external {
        require(address(this).balance > 0, "no eth on contract");
        require(isAdmin[msg.sender], "not authorized");        
        uint256 contractBalance = address(this).balance;
        for (uint i = 0; i < feeCollectors.length; i++) {
            uint256 amount = contractBalance * feePercent[i] / 10000;
            sendEth(payable(feeCollectors[i]), amount);
        }
    }

    function changeFeePercent(uint16[7] calldata newFeePercent) external onlyOwner {
        uint total;
        for (uint i = 0; i < newFeePercent.length; i++) {
            total += newFeePercent[i];
        }
        require(total == 10000, "wrong fee percents");
        feePercent = newFeePercent;
    }

    function changeAdmin(address oldAdmin, address newAdmin) external onlyOwner {
        for (uint i = 0; i < feeCollectors.length; i++) {
            if (feeCollectors[i] == oldAdmin) {
                feeCollectors[i] = newAdmin;
                isAdmin[newAdmin] = true;
                isAdmin[oldAdmin] = false;
                return;
            }
        }
        revert("no admin");
    }  

    function sendEth(address payable to, uint256 amount) internal {
        if (amount == 0) {
            return;
        }
        (bool success, ) = to.call{value: amount}("");
        require(success, "eth transfer failed");
    }

    receive() external payable {}

    fallback() external payable {}

    function withdrawERC20(address tokenAddress) external onlyOwner {
        uint256 amount = IERC20(tokenAddress).balanceOf(address(this));
        require(amount > 0, "no tokens on contract");
        IERC20(tokenAddress).transfer(_msgSender(), amount);
    }
}"
