    "/contracts/CandaoId.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity 0.8.19;

import "./libraries/Events.sol";
import "./libraries/Structures.sol";
import "./libraries/Modifiers.sol";
import "./libraries/CandaoReferral.sol";
import "./libraries/CandaoRole.sol";
import "./libraries/CandaoInterest.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract CandaoId is
    Initializable,
    AccessControlUpgradeable,
    PausableUpgradeable,
    UUPSUpgradeable
{
    using AddressUpgradeable for address;
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    mapping(address => Structures.Id) internal ids;

    function initialize() public initializer {
        __AccessControl_init();
        __Pausable_init();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(UPGRADER_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function setId(
        Structures.Id memory _data,
        Structures.IdExternal memory _dataExternal,
        bool[14] calldata _mask
    ) external whenNotPaused {
        Modifiers.onlySender(_data.address_[0]);

        Structures.Id storage id = ids[_data.address_[0]];
        if (_mask[1]) id.name = _data.name;
        if (_mask[2]) id.avatar = _data.avatar;
        if (_mask[3]) id.nickname = _data.nickname;
        if (_mask[6]) id.description = _data.description;
        if (_mask[7]) id.location = _data.location;
        if (_mask[8]) id.avatar = _data.avatar;
        if (_mask[9]) id.image_ = _data.image_;
        if (_mask[10]) id.background = _data.background;
        if (_mask[11]) id.sex = _data.sex;
        if (_mask[12]) _setRole(_dataExternal.role);
        if (_mask[13]) _setInterest(_dataExternal.interest);

        emit Events.Id(id);
    }

    // This function makes up as a GAS data store.
    function _setId(
        string memory _data
    ) external {
        Modifiers.onlyGas();
    }

    function getId(
        address _address
    ) external view returns (Structures.Id memory) {
        return ids[_address];
    }

    function getAddress(
        address _address
    ) external view returns (address[] memory) {
        CandaoReferral candaoReferral = CandaoReferral(
            0x88af00A4f666795367481207dFC137ef189690Dc
        );

        return candaoReferral.getChildren(_address);
    }

    function _setAddress(
        address _address,
        address _data
    ) private whenNotPaused {
        Modifiers.onlySender(_address);

        Structures.Id storage id = ids[_address];
        id.address_.push(_data);
    }

    function _setRole(string[] memory _role) internal {
        ICandaoRole(0xf19a86746a34C43071865DEE5D8F0bb074B43933).setRole(
            msg.sender,
            _role
        );
    }

    function _setInterest(string[] memory _interest) internal {
        ICandaoInterest(0xCde0988e28856A76d1070fA4dD1776737cb8d867).setInterest(
            msg.sender,
            _interest
        );
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyRole(UPGRADER_ROLE) {}
}
"
    },
    "/contracts/libraries/Structures.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity 0.8.19;

library Structures {
    struct Id {
        address[] address_;
        string name;
        /** @deprecated */
        string symbol;
        string nickname;
        /** @deprecated */
        string image;
        /** @deprecated */
        string[] interests;
        string description;
        string location;
        string avatar;
        string[] image_;
        string background;
        uint8 sex;
    }

    struct IdExternal {
        string[] role;
        string[] interest;
    }
}
"
    },
    "/contracts/libraries/Modifiers.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity 0.8.19;

import "./Errors.sol";

library Modifiers {
    address public constant AccountingSystem = 0xB2eb6EAd50Ae046023D1D7d37A80aD6A9b00E233;
    address public constant Gas = 0xc59bAd7CB72729b9faC1273eD7Bb87BBf7F4f042;

    modifier _onlySender(address _address) {
        if (_address != tx.origin) revert Errors.NotSender(_address);
        _;
    }
    modifier _onlyAccountingSystem() {
        if (tx.origin != AccountingSystem) revert Errors.Unauthorized(tx.origin);
        _;
    }
    modifier _onlyGas() {
        if (tx.origin != Gas) revert Errors.Unauthorized(tx.origin);
        _;
    }

    function onlySender(address _address) internal _onlySender(_address) {}
    function onlyAccountingSystem() internal _onlyAccountingSystem() {}
    function onlyGas() internal _onlyGas() {}
}
"
    },
