pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import "lib/BokkyPooBahsDateTimeLibrary.sol";

contract Statistics is ERC2771Context, Ownable {

    struct Record {
        uint date;
        uint counter;
    }

    mapping(string => mapping(uint => uint)) public _actionStats;
    uint[] public _dates;

    constructor(address _trustedForwarder) ERC2771Context(_trustedForwarder) {

    }

    function _msgSender() internal view override(Context, ERC2771Context) returns (address sender) {
        sender = ERC2771Context._msgSender();
    }

    function _msgData() internal view override(Context, ERC2771Context) returns (bytes calldata) {
        return ERC2771Context._msgData();
    }

    function saveAction(string memory action) public {
        uint currentDate = getCurrentDate();
        _actionStats[action][currentDate]++;

        if (_dates.length != 0 && (_dates[_dates.length - 1] == currentDate)) {
            return;
        }

        _dates.push(currentDate);
    }

    function showAllRecordsByAction(string memory action) public view returns (Record[] memory) {
        mapping(uint => uint) storage dateCounter = _actionStats[action];
        Record[] memory records = new Record[](_dates.length);
        uint j = 0;
        for (uint i = 0; i < _dates.length; i++) {
            records[j++] = Record(_dates[i], dateCounter[_dates[i]]);
        }
        return records;
    }

    function showRecordsDeepByAction(string memory action, uint _records) public view returns (Record[] memory) {
        mapping(uint => uint) storage dateCounter = _actionStats[action];
        Record[] memory records = new Record[](_records);
        uint j = 0;
        for (uint i = _dates.length; i > 0; i--) {
            uint date = _dates[i - 1];
            uint counter = dateCounter[date];

            if (counter != 0) {
                records[j++] = Record(date, counter);
                _records--;
            }
            if (_records == 0) {
                break;
            }
        }
        return records;
    }

    function showDaysDeepByAction(string memory action, uint _days) public view returns (Record[] memory) {
        uint currentDate = getCurrentDate();
        mapping(uint => uint) storage dateCounter = _actionStats[action];
        Record[] memory records = new Record[](_days);
        uint j = 0;
        for (uint i = 0; i < _days; i++) {
            uint date = dateMinusDays(currentDate, i);
            uint counter = dateCounter[date];
            records[j++] = Record(date, counter);
        }
        return records;
    }

    function showDaysBetweenByAction(string memory action, uint fromDate, uint toDate) public view returns (Record[] memory) {
        require(fromDate < toDate, "fromDate should be lower than toDate");
        mapping(uint => uint) storage dateCounter = _actionStats[action];
        Record[] memory records = new Record[](daysBetween(fromDate, toDate));
        uint j = 0;
        while (toDate != fromDate) {
            uint counter = dateCounter[toDate];
            records[j++] = Record(toDate, counter);
            toDate = dateMinusDays(toDate, 1);
        }
        return records;
    }

    function daysBetween(uint fromDate, uint toDate) internal pure returns (uint) {
        uint fromDay = fromDate % 100;
        fromDate = fromDate / 100;
        uint fromMonth = fromDate % 100;
        uint fromYear = fromDate / 100;

        uint toDay = toDate % 100;
        toDate = toDate / 100;
        uint toMonth = toDate % 100;
        uint toYear = toDate / 100;

        return BokkyPooBahsDateTimeLibrary._daysFromDate(toYear, toMonth, toDay) -
        BokkyPooBahsDateTimeLibrary._daysFromDate(fromYear, fromMonth, fromDay);
    }

    function dateMinusDays(uint date, uint sub) private pure returns (uint) {
        uint day = date % 100;
        date = date / 100;
        uint month = date % 100;
        uint year = date / 100;
        while (sub > 0) {
            if (day > sub) {
                day -= sub;
                break;
            }
            sub -= day;
            month--;
            if (month <= 0) {
                year--;
                month = 12;
            }
            day = BokkyPooBahsDateTimeLibrary._getDaysInMonth(year, month);
        }
        return (year * 10000) + (month * 100) + day;
    }

    function getCurrentDate() private view returns (uint) {
        (uint year, uint month, uint day) = BokkyPooBahsDateTimeLibrary.timestampToDate(block.timestamp);
        return (year * 10000) + (month * 100) + day;
    }

}
