    "contracts/SINDI.sol": {
      "content": "//SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

contract SINDI is ERC20{

    uint256 constant public TOTAL_EMIT=21_000_000 ether;
    uint256 constant public HOLD_AMOUNT=333_333 ether;
    uint256 constant public MAX_TRANSFER=333_333 ether;

    uint256 constant public BURN_FEE_AMOUNT = 3;
    uint256 constant public BUY_FEE_AMOUNT = 14;
    uint256 constant public SELL_FEE_AMOUNT = 14;

    //using Address for address;

    mapping (address => bool) public _isExcludedFromFee; 

    uint256 private constant MAX = ~uint256(0);

    uint8 private txCount = 0;
    uint8 private swapTrigger = 3;

    address[6] private _feeOwners=[
      0x2484feF1Cc370c54Cc664bf09ba23CcDbB5cE49e,
      0x6F0fa7482Aaa302b1Bb4AAc3C2c90B1854793e7b,
      0x2D6B2f44176336299c67B59129C9F4f1c27Ba670,
      0x5639acBE39f5Bdb22B0E08E5249fC99d699B90a9,
      0x66B6a9989DdF050C3A3163E4f51572d2A5aeB568,
      0x95fAf7d8151A9C265A481F166Ba3A4D62436d265
    ];
    uint256[6] private _feePlan=[40,30,20,20,15,15];

    IUniswapV2Router02 private uniswapV2Router;
    address private uniswapV2Pair;
    bool private inSwapAndLiquify;
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }
    constructor (address router) ERC20('SINDI','SINDI'){
        address owner=msg.sender;
        uniswapV2Router = IUniswapV2Router02(router); 
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), uniswapV2Router.WETH());

        _isExcludedFromFee[owner] = true;
        _isExcludedFromFee[address(this)] = true;
        for(uint i;i<6;i++){
          _isExcludedFromFee[_feeOwners[i]] = true;
        }
        _mint(owner, TOTAL_EMIT) ;
    }

    receive() external payable {}

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        if (!_isExcludedFromFee[from] && !_isExcludedFromFee[to]){

            require(amount <= MAX_TRANSFER, "Transaction transfer limit");
        }


        if(
            txCount >= swapTrigger && 
            !inSwapAndLiquify &&
            from != uniswapV2Pair 
            )
        { 
            uint256 contractTokenBalance = balanceOf(address(this));
            //if(contractTokenBalance > _maxTxAmount) {contractTokenBalance = _maxTxAmount;}
            txCount = 0;
            swapAndLiquify(contractTokenBalance);
        }
        bool takeFee = true;
        bool isBuy;
        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]){
            takeFee = false;
            super._transfer(from,to, amount);
        } else {
            if(from == uniswapV2Pair){
                isBuy = true;
            }

            txCount++;
            _tokenTransfer(from, to, amount, takeFee, isBuy);
        }


    }
    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        //uint256 balanceBeforeSwap = address(this).balance;
        swapTokensForBNB(contractTokenBalance);
        uint eth_amount=address(this).balance;
        if ( eth_amount> 0) {
            for(uint256 i;i<_feeOwners.length;i++){
                uint fee=eth_amount * _feePlan[i] / 140;
                (bool success, ) = payable(_feeOwners[i]).call{value: fee}("");
                if (!success) {
                    revert("Error send BNB");
                }
            }
        }
    }

    function swapTokensForBNB(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, 
            path,
            address(this),
            block.timestamp
        );
    }

    function _tokenTransfer(address sender, address recipient, uint256 tAmount, bool takeFee, bool isBuy) private {
        if (!takeFee) {
            super._transfer(sender, recipient, tAmount);
        }
        else 
        if (isBuy) {
            uint256 buyFee = tAmount * BUY_FEE_AMOUNT / 100;
            uint256 burnFee = tAmount * BURN_FEE_AMOUNT / 100;
            uint256 tTransferAmount = tAmount - buyFee - burnFee;

            super._transfer(sender,recipient,tTransferAmount);
            super._transfer(sender,address(this),buyFee);
            super._burn(sender, burnFee);
        } else {
            uint256 sellFee = tAmount * SELL_FEE_AMOUNT / 100;
            uint256 burnFee = tAmount * BURN_FEE_AMOUNT / 100;
            uint256 tTransferAmount = tAmount - sellFee - burnFee;

            super._transfer(sender,recipient,tTransferAmount);
            super._transfer(sender,address(this),sellFee);
            super._burn(sender, burnFee);
        }
    }
}"
