contract ZefiBase is Ownable, ERC20 {
    using SafeMath for uint256;

    bool internal botGuard;
    uint256 public antiBotBuyAmount;
    uint256 public constant botGuardDuration = 30 minutes;

    uint256 public constant buyFeeRate = 2;
    uint256 public constant sellFeeRate = 3;

    constructor(string memory name, string memory symbol) ERC20(name, symbol) {}

    function setAntiBotAmount(uint256 _buyMaxAmount) public onlyOwner { antiBotBuyAmount = _buyMaxAmount; }

    //Incase of accident send bnb to the contract, we can remove that bnb
    function emergencySweepBNB() public onlyOwner {
        payable(_msgSender()).transfer(address(this).balance);
    }
    //Incase of accident send token to the contract, we can remove that token
    function emergencySweepToken(address _token) public onlyOwner {
        IERC20(_token).transfer(_msgSender(), IERC20(_token).balanceOf(address(this)));
    }
    //Incase of accident send NFT to the contract, we can remove that NFT
    function emergencySweepNFT(address _nft, uint256 _tId) public onlyOwner {
        IERC721(_nft).transferFrom(address(this), _msgSender(), _tId);
    }
}

contract Zefi is ZefiBase {
    using SafeMath for uint256;
    using Address for address;

    uint256 public constant maxSupply = 500 * 10**6 * 10**18;
    uint256 public liquidityTime;
    uint256 public liquidityBlock;

    mapping(address => bool) _feeExempt;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    address public feeAddress;
    address public ownerAddress = 0x4F4a44599931e4F51855025BE0ED3A89847D23c3;

    bool private isSetLiquidity;

    constructor() ZefiBase("CryptoZeroFi", "ZERI_V2")
    {
        _mint(ownerAddress, maxSupply);
        uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), uniswapV2Router.WETH());

        antiBotBuyAmount = 37000 * 10**18; //2 BNB
        feeAddress = _msgSender();
        _feeExempt[_msgSender()] = true;
        botGuard = true;
        isSetLiquidity = true;

        _transferOwnership(ownerAddress);
    }

    function burn(uint256 amount) public {
        _burn(_msgSender(), amount);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        uint256 transferFeeRate = 0;

        if (!_feeExempt[sender]) {
            // if not fee exempted => check if liquidity seted
            require(liquidityTime != 0, "ZERI: LIQUIDITY HAVENT SET");
            require(liquidityBlock != 0, "ZERI: LIQUIDITY HAVENT SET");
            require(block.timestamp != liquidityBlock, "ZERI: LIQUIDITY HAVENT SET");
        }

        // antibot for first 30 minutes
        if (botGuard) {
            // determine fee case
            bool isSell = recipient == uniswapV2Pair;
            bool isBuy = sender == uniswapV2Pair;
            uint256 time_since_ld = block.timestamp - liquidityTime;

            // block buy too much
            if (isBuy) {
                require(amount <= antiBotBuyAmount, "ZERI: Pumping is not allowed during this period");
            }

            // apply tax early buy
            transferFeeRate = time_since_ld > 15 minutes ? (isBuy ? 10 : (isSell ? 20 : 0))
                            : time_since_ld > 10 minutes ? (isBuy ? 20 : (isSell ? 30 : 0))
                            : time_since_ld > 5 minutes ? (isBuy ? 30 : (isSell ? 40 : 0))
                            : (isBuy ? 40 : (isSell ? 50 : 0));

            // disable botGuard automatically after 30 minutes
            if (!isSetLiquidity && (time_since_ld > botGuardDuration)) 
            { 
                botGuard = false; 
                transferFeeRate = isBuy ? buyFeeRate 
                                : isSell ? sellFeeRate
                                : 0;
            }

            // set liquidity fee = 0 and set liquidity timestamp
            if (isSetLiquidity && recipient == uniswapV2Pair) {
                transferFeeRate = 0;
                isSetLiquidity = false;
                liquidityTime = block.timestamp - 1; // safety margin 1 second
                liquidityBlock = block.number;
            }
        }
        else 
        {
            // apply fee for the transaction incase of sell or buy accordingly
            transferFeeRate = sender == uniswapV2Pair ? buyFeeRate 
                            : recipient == uniswapV2Pair ? sellFeeRate
                            : 0;
        }  

        if (
            transferFeeRate > 0 &&
            sender != address(this) &&
            recipient != address(this)
        ) {
            uint256 _fee = amount.mul(transferFeeRate).div(100);
            super._transfer(sender, feeAddress, _fee); // TransferFee
            amount = amount.sub(_fee);
        }

        super._transfer(sender, recipient, amount);
    }

    // receivable token
    receive() external payable {}

    function setFeeAddress(address _feeAddress) public onlyOwner { feeAddress = _feeAddress; }

    // set fee exemption for admin address and liquidity address
    function setFeeExempt(address _add, bool _val) public onlyOwner {
        require(_feeExempt[_add] != _val, "ZERI: Fee Exempt already set");
        _feeExempt[_add] = _val;

        emit SetFeeExempt(_add, _val, block.timestamp);
    }

    event SetFeeExempt(address indexed _address, bool _bool, uint256 _timestamp);
}
