    "/contracts/DipoContract.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

contract BaseDipoLTD is Ownable, ReentrancyGuard {
  using SafeMath for uint256;

  ERC20 public ltdToken;
  ERC20 public htdToken;
  ERC20 public busdToken;
  uint256 public tokenPrice;
  uint256 public ltdAmountRequire;

  // TGE details
  uint256 public startTimeTGE; // Time start
  uint256 public TGE_RELEASE; // 6%

  bool public isPauseContract = false;
  uint256 public totalCap;

  // Time payment
  uint256 public startTimePayment;
  uint256 public endTimePayment;

  // Struct Stage
  struct StageDetail {
    uint256 id;
    uint256 stageRelease;
    uint256 startTime;
    uint256 endTime;
  }

  mapping(uint256 => StageDetail) public stageDetails;
  StageDetail[] public listStage;

  struct NFTTypeDetails {
    address nft;
    uint256 typeNFT;
    uint256 minAmount;
    uint256 maxAmount;
  }

  // 1-2-3-4 / Elite-Gold-Platinum-Diamond
  mapping(uint256 => NFTTypeDetails) public nftTypeDipo;

  mapping(address => bool) public userWhitelist; // List User address whitelist buy IDO
  mapping(address => uint256) public userStakeNFTType; // Type NFT stake for white list
  mapping(address => bool) public userIsPaidIDO; // Check user paid IDO
  mapping(address => uint256) public locks; // HTD lock by user - total HTD of user
  mapping(address => uint256) public userRemaining; // HTD remaining by user;
  mapping(address => bool) public userIsClaimTGE; // Check user claim TGE;
  mapping(address => mapping(uint256 => bool)) public userIsClaimStage; // Is user claim this stage;

  address public ownerDipo;

  event EvePaidIDO(address indexed user, uint256 time);
  event ClaimTGE(address indexed account, uint256 amount, uint256 time);
  event ClaimStage(
    address indexed account,
    uint256 amount,
    uint256 stageID,
    uint256 time
  );

  constructor(
    address _ltdToken,
    address _htdTokenAddress,
    address _busdTokenAddress,
    uint256 _tokenPrice,
    uint256 _ltdAmountRequire
  ) {
    ltdToken = ERC20(_ltdToken);
    htdToken = ERC20(_htdTokenAddress);
    busdToken = ERC20(_busdTokenAddress);
    tokenPrice = _tokenPrice;
    ltdAmountRequire = _ltdAmountRequire;
  }

  function onOffContract(bool _bool) external onlyOwner {
    isPauseContract = _bool;
  }

  function releasedClaim(address _user) public view returns (uint256) {
    return locks[_user] - userRemaining[_user];
  }

  function setTypeNFTDipo(
    address[] calldata _listNFTAddress,
    uint256[] calldata _listType,
    uint256[] calldata _listMin,
    uint256[] calldata _listMax
  ) external onlyOwner {
    require(_listNFTAddress.length == _listType.length, "Invalid length");
    require(_listNFTAddress.length == _listMin.length, "Invalid length");
    require(_listNFTAddress.length == _listMax.length, "Invalid length");
    for (uint256 i = 0; i < _listNFTAddress.length; i++) {
      NFTTypeDetails memory obj;
      obj.nft = _listNFTAddress[i];
      obj.typeNFT = _listType[i];
      obj.minAmount = _listMin[i];
      obj.maxAmount = _listMax[i];

      nftTypeDipo[_listType[i]] = obj;
    }
  }

  // Set stage claim - Need set base on stage before 1 - index+
  function addStage(
    uint256[] memory _stages,
    uint256[] memory _stageRelease,
    uint256[] memory _startTime
  ) external onlyOwner {
    require(_stages.length == _stageRelease.length, "Invalid input");
    require(_stages.length == _startTime.length, "Invalid input");

    delete listStage;

    for (uint256 i = 0; i < _stages.length; i++) {
      StageDetail memory obj;

      obj.id = _stages[i];
      obj.stageRelease = _stageRelease[i];
      obj.startTime = _startTime[i];

      stageDetails[_stages[i]] = obj;

      listStage.push(obj);
    }
  }

  // Add user by type NFT
  function setWhiteListClaim(
    address[] calldata _newList,
    uint256[] calldata _typeNFT
  ) external onlyOwner {
    for (uint256 i = 0; i < _newList.length; i++) {
      userWhitelist[_newList[i]] = true;
      userStakeNFTType[_newList[i]] = _typeNFT[i];
    }
  }

  // Return number of stage range
  function getAllStage() public view returns (uint256) {
    return listStage.length;
  }

  function removeUserWhiteListClaim(address[] calldata _newList)
    external
    onlyOwner
  {
    for (uint256 i = 0; i < _newList.length; i++) {
      userWhitelist[_newList[i]] = false;
    }
  }

  function setTimePayment(uint256 _startTimePayment, uint256 _endTime)
    external
    onlyOwner
  {
    endTimePayment = _endTime;
    startTimePayment = _startTimePayment;
  }

  function userPayIDO() external nonReentrant {
    require(isPauseContract != true, "This contract is pause!");

    require(
      userWhitelist[msg.sender] == true,
      "You not in whitelist, please staking NFT for joined whitelist"
    );
    // Check time payment
    require(block.timestamp >= startTimePayment, "IDO not in start time");
    require(block.timestamp <= endTimePayment, "Time to payment is end!");

    // Check type
    uint256 typeNFTbyUser = userStakeNFTType[msg.sender];
    NFTTypeDetails storage obj = nftTypeDipo[typeNFTbyUser];

    // check paid
    require(userIsPaidIDO[msg.sender] == false, "Has paid before");

    // Transfer money
    require(ownerDipo != address(0), "OwnerIDO is not define");
    busdToken.transferFrom(msg.sender, ownerDipo, obj.maxAmount);
    ltdToken.transferFrom(msg.sender, ownerDipo, ltdAmountRequire);

    // time with 10**18 agian để chia k bị lẻ số amount và mất số ,
    uint256 tokenAmount = obj.maxAmount.mul(10**18).div(tokenPrice);

    // Set Balance User can claim
    locks[msg.sender] = tokenAmount;
    userRemaining[msg.sender] = tokenAmount;

    // Set totalcap
    totalCap += tokenAmount;

    userIsPaidIDO[msg.sender] = true;

    emit EvePaidIDO(msg.sender, block.timestamp);
  }

  function setTimeTGE(uint256 _time, uint256 _tgeRelease) external onlyOwner {
    startTimeTGE = _time;

    TGE_RELEASE = _tgeRelease;
  }

  // Claim TGE
  function claimTGE() external nonReentrant {
    require(userIsPaidIDO[msg.sender] == true, "You need paid IDO first");

    require(isPauseContract != true, "This contract is pause!");

    require(locks[msg.sender] > 0, "You're not buy this round");

    require(userIsClaimTGE[msg.sender] == false, "TGE is claimed");

    // calc token can claim TGE
    uint256 HTDAmount = locks[msg.sender].mul(TGE_RELEASE).div(100);

    // decrease remain
    userRemaining[msg.sender] -= HTDAmount;

    // transfer token to user
    htdToken.transfer(msg.sender, HTDAmount);

    // Set userIsClaimTGE
    userIsClaimTGE[msg.sender] = true;

    emit ClaimTGE(msg.sender, HTDAmount, block.timestamp);
  }

  function setOwnerIDO(address _owner) external nonReentrant onlyOwner {
    ownerDipo = _owner;
  }

  // Update single stage by stageID
  function updateStage(
    uint256 _stageID,
    uint256 _stageRelease,
    uint256 _startTime
  ) external onlyOwner {
    StageDetail memory obj = stageDetails[_stageID];

    obj.stageRelease = _stageRelease;
    obj.startTime = _startTime;

    stageDetails[_stageID] = obj;
  }

  // Claim Stage
  function claimStage(uint256 _stage) external nonReentrant {
    require(isPauseContract != true, "This contract is pause!");

    require(userIsPaidIDO[msg.sender] == true, "You need paid IDO first");

    uint256 totalBalanceByUser = userRemaining[msg.sender];
    require(totalBalanceByUser > 0, "Your balance is not enough!!");

    StageDetail memory obj = stageDetails[_stage];

    require(block.timestamp >= obj.startTime, "Stage not release!!");

    // calc amount claim
    uint256 totalClaimByStage = locks[msg.sender].mul(obj.stageRelease).div(
      100
    );

    require(obj.id > 0, "Not good");

    require(
      userIsClaimStage[msg.sender][_stage] != true,
      "This stage is claimed"
    );

    if (userRemaining[msg.sender] < totalClaimByStage) {
      htdToken.transfer(msg.sender, userRemaining[msg.sender]);
      // Calc userRemain
      userRemaining[msg.sender] = 0;
    } else {
      // transfer token to user
      htdToken.transfer(msg.sender, totalClaimByStage);
      // Calc userRemain
      userRemaining[msg.sender] = userRemaining[msg.sender] - totalClaimByStage;
    }

    userIsClaimStage[msg.sender][obj.id] = true;

    emit ClaimStage(msg.sender, totalClaimByStage, _stage, block.timestamp);
  }

  /* ========== EMERGENCY ========== */
  function governanceRecoverUnsupported(
    address _token,
    address _to,
    uint256 _amount
  ) external onlyOwner {
    // require(_token != address(HTD), "Token invalid");
    ERC20(_token).transfer(_to, _amount);
  }
}
