// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IERC20 {
    function transfer(address recipient, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

contract Airdrops {
    address public owner;
    address public tokenAddress; // 代币地址可变
    uint8 public decimals; // 用于代币小数位数

    constructor() {
        owner = msg.sender;
    }

    // 确保只有合约拥有者可以调用特定功能
    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    // 设置代币地址
    function setTokenAddress(address _tokenAddress) public onlyOwner {
        tokenAddress = _tokenAddress;
    }

    // 设置代币的小数位数
    function setDecimals(uint8 _decimals) public onlyOwner {
        decimals = _decimals;
    }

    function airdropCvt(address[] memory recipients, uint256[] memory amounts) public onlyOwner {
        require(recipients.length == amounts.length, "Recipients and amounts do not match");
        require(tokenAddress != address(0), "Token address not set");
        IERC20 token = IERC20(tokenAddress);

        for (uint256 i = 0; i < recipients.length; i++) {
            uint256 formattedAmount = amounts[i] * (10 ** decimals);
            token.transfer(recipients[i], formattedAmount);
        }
    }

    // 提取合约中的代币
    function withdrawToken() public onlyOwner {
        require(tokenAddress != address(0), "Token address not set");
        IERC20 token = IERC20(tokenAddress);
        uint256 balance = token.balanceOf(address(this));
        token.transfer(owner, balance);
    }
}
