    "contracts/projectSale/FinceptorSale.sol": {
      "content": "// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.16;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./IFinceptorSale.sol";

contract FinceptorSale is IFinceptorSale, Ownable, Pausable {
    using SafeERC20 for IERC20;

    /// @notice Address of the beneficiary of the sale
    address public beneficiary;
    /// @notice Address of the token used for payment
    address public paymentToken;

    /// @notice Total amount of tokens that can be sold
    uint256 public saleCap;
    /// @notice Total amount of tokens sold
    uint256 public currentSaleAmount;

    /// @notice Addresses of the users who deposited
    address[] public buyerAddresses;
    /// @notice Amount of tokens deposited by each user
    mapping(address => uint256) public userPurchasedAmount;

    /// @notice Timestamp when the sale starts
    uint256 public saleStartTime;
    /// @notice Duration of the sale
    uint256 public saleDuration;

    /// @notice Modifier to check if the sale is active
    modifier onlyWhileSaleActive() {
        require(isSaleActive(), "Sale is not active");
        _;
    }

    /**
     * @notice Constructor of the contract for Finceptor Token Sale
     * @param _beneficiary The beneficiary to receive the funds
     * @param _paymentToken The token to be used for payment
     * @param _saleCap Maximum amount of tokens to be sold
     * @param _saleStartTime The start timestamp of the sale
     * @param _saleDuration The duration of the sale
     */
    constructor(
        address _beneficiary,
        address _paymentToken,
        uint256 _saleCap,
        uint256 _saleStartTime,
        uint256 _saleDuration
    ) {
        require(_beneficiary != address(0), "Invalid beneficiary address");
        require(_paymentToken != address(0), "Invalid payment token address");
        require(_saleCap > 0, "Invalid sale cap");
        require(_saleStartTime > block.timestamp, "Invalid sale start time");
        require(_saleDuration > 0, "Invalid sale duration");

        beneficiary = _beneficiary;
        paymentToken = _paymentToken;
        saleCap = _saleCap;
        saleStartTime = _saleStartTime;
        saleDuration = _saleDuration;
    }

    //============ ONLY OWNER FUNCTIONS ============//

    /**
     * @notice The function to change beneciary address
     * @dev Only owner can call this function
     * @param _beneficiary The beneficiary to receive the funds
     */
    function setBeneficiary(address _beneficiary) external onlyOwner {
        require(_beneficiary != address(0), "Invalid beneficiary address");
        beneficiary = _beneficiary;
        emit BeneficiaryChanged(_beneficiary);
    }

    /**
     * @notice The function to change payment token address
     * @dev Only owner can call this function
     * @param _paymentToken The token to be used for payment
     */
    function setPaymentToken(address _paymentToken) external onlyOwner {
        require(_paymentToken != address(0), "Invalid payment token address");
        paymentToken = _paymentToken;
        emit PaymentTokenChanged(_paymentToken);
    }

    /**
     * @notice The function to change sale cap
     * @dev Only owner can call this function
     * @param _saleCap Maximum amount of tokens to be sold
     */
    function setSaleCap(uint256 _saleCap) external onlyOwner {
        require(_saleCap > 0, "Invalid sale cap");
        saleCap = _saleCap;
        emit SaleCapChanged(_saleCap);
    }

    /**
     * @notice The function to change sale start time
     * @dev Only owner can call this function
     * @param _saleStartTime The start timestamp of the sale
     */
    function setSaleStartTime(uint256 _saleStartTime) external onlyOwner {
        require(_saleStartTime > block.timestamp, "Invalid sale start time");
        saleStartTime = _saleStartTime;
        emit SaleStartTimeChanged(_saleStartTime);
    }

    /**
     * @notice The function to change sale duration
     * @dev Only owner can call this function
     * @param _saleDuration The duration of the sale
     */
    function setSaleDuration(uint256 _saleDuration) external onlyOwner {
        require(_saleDuration > 0, "Invalid sale duration");
        saleDuration = _saleDuration;
        emit SaleDurationChanged(_saleDuration);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    //============ PUBLIC FUNCTIONS ============//

    /**
     * @notice The function to join the sale and deposit tokens
     * @param _amount The amount of tokens to be deposited
     */
    function deposit(uint256 _amount) external override onlyWhileSaleActive whenNotPaused {
        require(currentSaleAmount < saleCap, "Sale cap exceeded");
        require(_amount > 0, "Invalid amount");

        if (currentSaleAmount + _amount > saleCap) {
            _amount = saleCap - currentSaleAmount;
        }

        if (userPurchasedAmount[msg.sender] == 0) {
            buyerAddresses.push(msg.sender);
        }
        userPurchasedAmount[msg.sender] += _amount;
        currentSaleAmount += _amount;

        IERC20(paymentToken).safeTransferFrom(msg.sender, beneficiary, _amount);
        emit Deposited(msg.sender, _amount);
    }

    /**
     * @notice The function to get the sale cap
     * @return The sale cap
     */
    function getSaleCap() external view override returns (uint256) {
        return saleCap;
    }

    /**
     * @notice The function to get the current sale amount
     * @return The current sale amount
     */
    function getCurrentSaleAmount() external view override returns (uint256) {
        return currentSaleAmount;
    }

    /**
     * @notice The function to get the sale start time
     * @return The sale start time
     */
    function getSaleStartTime() external view override returns (uint256) {
        return saleStartTime;
    }

    /**
     * @notice The function to get the sale duration
     * @return The sale duration
     */
    function getSaleDuration() external view override returns (uint256) {
        return saleDuration;
    }

    /**
     * @notice The function to get the number of users who deposited
     * @return The number of users who deposited
     */
    function getDepositedUserNumber() external view returns (uint256) {
        return buyerAddresses.length;
    }

    /**
     * @notice The function to get the beneficiary address
     * @return The beneficiary address
     */
    function getBeneficiary() external view returns (address) {
        return beneficiary;
    }

    /**
     * @notice The function to get the amount deposited by a user address
     * @param _user The address of the user
     * @return The amount of tokens deposited by the user
     */
    function depositedOf(address _user) external view override returns (uint256) {
        return userPurchasedAmount[_user];
    }

    /**
     * @notice The function to get the user deposit information
     * @param _start The start index of the user array
     * @param _amount The amount of users to be returned
     * @return The array of user addresses
     * @return The array of user deposit amounts
     */
    function getUserDeposits(
        uint256 _start,
        uint256 _amount
    ) external view returns (address[] memory, uint256[] memory) {
        require(_start < buyerAddresses.length, "Invalid start index");
        require(_amount > 0, "Invalid amount");

        uint256 end = _start + _amount;
        if (end > buyerAddresses.length) {
            end = buyerAddresses.length;
        }

        address[] memory addresses = new address[](end - _start);
        uint256[] memory amounts = new uint256[](end - _start);

        for (uint256 i = _start; i < end; i++) {
            addresses[i - _start] = buyerAddresses[i];
            amounts[i - _start] = userPurchasedAmount[buyerAddresses[i]];
        }

        return (addresses, amounts);
    }

    /**
     * @notice The function to get if the sale is active or not
     * @return True if the sale is active, false otherwise
     */
    function isSaleActive() public view returns (bool) {
        return block.timestamp >= saleStartTime && block.timestamp <= saleStartTime + saleDuration;
    }
}
