    "contracts/Store.sol": {
      "content": "// SPDX-License-Identifier: MIT
// OpenZeppelin Contracts v4.4.1 (access/Ownable.sol)
pragma solidity ^0.8.14;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./SafeMath.sol";
import "./TransferHelper.sol";
import "./ReentrancyGuard.sol";
import "./ECDSA.sol";
contract Store is Ownable,ReentrancyGuard {

    using SafeMath for uint256;

    struct Project {
        address tokenIn;
        address tokenOut;
        uint inWeight;
        uint outWeight;
        uint limitSend;
        bool isOut;
    }

    struct GroupItem {
        address wallet;
        uint rate;
    }

    GroupItem[] private nullGroup;
    GroupItem[] public fundGroup;
    uint private pointMax = 10 ** 8;

    mapping(string => Project) public PMS;
   

    event Swap(address player,address tokenIn,uint inAmount,address tokenOut,uint outAmount);
    event Build(string pid,address tokenIn,address tokenOut,uint inWeight,uint outWeight,uint limitSend,bool isOut);

    constructor(GroupItem[] memory _fundGroup) {
        _setting(_fundGroup);
    }

    receive() external payable {}
    fallback() external payable {}
    
    
    function _tokenSend(
        address _token,
		address _to,
        uint _amount
    ) private returns (bool result) {
        if(_token == address(0)){
            Address.sendValue(payable(_to),_amount);
            result = true;
        }else{
            IERC20 token = IERC20(_token);
            result = token.transfer(_to,_amount);
        }
    }

    function tokenSend(
        address _token,
		address _to,
        uint _amount
    ) public onlyOwner {
        require(_tokenSend(_token,_to,_amount),"Store::extract fail");
    }

   
    function balanceOf(
		address _token
	) public view returns (uint balance) {
		if(_token == address(0)) {
			balance = address(this).balance;
		}else {
			IERC20 token = IERC20(_token);
            balance = token.balanceOf(address(this));
		}
	}
    
    function swap(
        string memory id,
        uint outAmount
    ) external payable nonReentrant {
        require(msg.sender == tx.origin,"Store::Illegal caller");
        
        // 接收代币
        uint inAmount = PMS[id].inWeight.mul(outAmount).div(PMS[id].outWeight);
		if(PMS[id].tokenIn == address(0)) {
			require(msg.value == inAmount,"Store::Input eth is not accurate");
		}else {
			TransferHelper.safeTransferFrom(PMS[id].tokenIn,msg.sender,address(this),inAmount);
		}
        
        uint outBalance = balanceOf(PMS[id].tokenOut);
        require(outBalance >= outAmount,"Store::outBalance < outAmount");
        
        uint inBalance = balanceOf(PMS[id].tokenIn);
        
        // 溢出执行
        if(PMS[id].isOut && inBalance >= PMS[id].limitSend) {
            for(uint i = 0;i < fundGroup.length; i++) {
                require(_tokenSend(PMS[id].tokenIn,fundGroup[i].wallet,fundGroup[i].rate.mul(inBalance).div(pointMax)),"Store::Fund reception failed");
            }
        }
        
        require(_tokenSend(PMS[id].tokenOut,msg.sender,outAmount),"Store:: Swap fail");
        
        emit Swap(msg.sender,PMS[id].tokenIn,inAmount,PMS[id].tokenOut,outAmount);
    }
    
    function build(
        string memory pid,
        address tokenIn,
        address tokenOut,
        uint inWeight,
        uint outWeight,
        uint limitSend,
        bool isOut
    ) external onlyOwner {
        require(inWeight > 0 && outWeight > 0,"Store::outWeight > 0 && inWeight > 0");
        
        PMS[pid] = Project({
            tokenIn:tokenIn,
            tokenOut:tokenOut,
            inWeight:inWeight,
            outWeight:outWeight,
            limitSend:limitSend,
            isOut:isOut
        });
        
        
        
        emit Build(pid, tokenIn, tokenOut, inWeight,outWeight,limitSend,isOut);
    }


    function setting(
        GroupItem[] memory _fundGroup
    ) external onlyOwner {
        _setting(_fundGroup);
    }

    function _setting(
        GroupItem[] memory _fundGroup
    ) internal {
        fundGroup = nullGroup;
        uint total = 0;
        for(uint i = 0; i < _fundGroup.length; i++) {
            total = total.add(_fundGroup[i].rate);
            fundGroup.push(_fundGroup[i]);
        }
        // require(total == pointMax,"Store::Over 100%");
    }

	
}
"
    },
