    "contracts/LraceGame.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import {MintLrace} from "./MintLrace.sol";

contract LraceGame is Ownable {
    address public CarNFTAddress;
    mapping(address => bool) private _admins;

    modifier onlyAdmins() {
        require(_admins[_msgSender()], "!Admin");
        _;
    }

    struct Player {
        address playerAddress;
        uint256 tokenId;
        uint256 score;
    }
    uint256 public playerCount;
    mapping(uint256 => Player) public players;
    event PlayerAdded(address playerAddress, uint256 tokenId, uint256 score);

    struct Track {
        string name;
        uint256[] influences;
    }
    event TrackAdded(string trackName);
    Track[] public tracks;

    event RacingFinished(string trackName, Player winner);

    constructor(address _CarNFTAddress) {
        CarNFTAddress = _CarNFTAddress;
        _admins[msg.sender] = true;
    }

    // admin function

    function addNewAdmin(address _newAdmin) external onlyOwner {
        _admins[_newAdmin] = true;
    }

    function removeAdmin(address _admin) external onlyOwner {
        _admins[_admin] = false;
    }

    function checkIsAdmin(address _address) external view returns (bool) {
        return _admins[_address];
    }

    // services

    function setCarNFTAddress(address _CarNFTAddress) external onlyOwner {
        CarNFTAddress = _CarNFTAddress;
    }

    function addPlayer(address playerAddress, uint256 tokenId) public {
        require(
            MintLrace(CarNFTAddress).getUserBalance(playerAddress, tokenId) > 0,
            "Insufficient funds"
        );

        Player memory targetPlayer = getPlayer(playerAddress, tokenId);

        require(
            targetPlayer.playerAddress == address(0),
            "PlayerAlreadyExist!"
        );

        playerCount++;
        Player memory newPlayer = Player({
            playerAddress: playerAddress,
            tokenId: tokenId,
            score: 0
        });
        players[playerCount] = newPlayer;

        emit PlayerAdded(playerAddress, tokenId, 0);
    }

    function clearPlayers() internal {
        for (uint256 i = 1; i <= playerCount; i++) {
            delete players[i];
        }
        playerCount = 0;
    }

    function addTrack(
        string memory name,
        uint256 speed,
        uint256 accelerate,
        uint256 handling,
        uint256 offRoad
    ) public onlyAdmins {
        require(bytes(name).length > 0, "Track name cannot be empty");
        require(
            speed + accelerate + handling + offRoad == 100,
            "Sum of influences should be 100"
        );
        uint256[] memory influences = new uint256[](4);
        influences[0] = speed;
        influences[1] = accelerate;
        influences[2] = handling;
        influences[3] = offRoad;
        tracks.push(Track(name, influences));
        emit TrackAdded(name);
    }

    function getTrack(
        uint256 index
    ) public view returns (string memory name, uint256[] memory influences) {
        require(index < tracks.length, "Invalid track index");
        Track storage track = tracks[index];
        return (track.name, track.influences);
    }

    function removeTrack(string memory name) public onlyAdmins {
        require(bytes(name).length > 0, "Track name cannot be empty");
        for (uint i = 0; i < tracks.length; i++) {
            if (keccak256(bytes(tracks[i].name)) == keccak256(bytes(name))) {
                delete tracks[i];
                // Move the last element to the position of the deleted element
                if (i < tracks.length - 1) {
                    tracks[i] = tracks[tracks.length - 1];
                }
                // Remove the last element
                tracks.pop();
                break;
            }
        }
    }

    function startRace() public onlyAdmins {
        require(
            playerCount > 1,
            "At least two cars are required to start a race"
        );
        require(
            tracks.length > 0,
            "At least one track is required to start a race"
        );

        uint256 winnerIndex = 0;
        uint256 highestScore = 0;

        uint256 trackIndex = randMod(tracks.length);
        string memory trackName = tracks[trackIndex].name;

        for (uint256 i = 1; i <= playerCount; i++) {
            Player memory targetPlayer = players[i];
            uint256[] memory _capabilities = MintLrace(CarNFTAddress)
                .getCapability(targetPlayer.tokenId);
            uint256 speed = _capabilities[0];
            uint256 accelerate = _capabilities[1];
            uint256 handling = _capabilities[2];
            uint256 offroad = _capabilities[3];
            uint256 score = (tracks[trackIndex].influences[0] * speed) +
                (tracks[trackIndex].influences[1] * accelerate) +
                (tracks[trackIndex].influences[2] * handling) +
                (tracks[trackIndex].influences[3] * offroad);

            players[i].score = score;
            if (score > highestScore) {
                highestScore = score;
                winnerIndex = i;
            }
        }

        bool winerPaid = false;
        for (uint256 i = 1; i <= playerCount; i++) {
            if (players[i].score < highestScore) {
                if (winerPaid = false) {
                    MintLrace(CarNFTAddress).safeTransferFrom(
                        players[i].playerAddress,
                        players[winnerIndex].playerAddress,
                        players[i].tokenId,
                        1,
                        ""
                    );
                    winerPaid = true;
                } else {
                    MintLrace(CarNFTAddress).burn(
                        players[i].playerAddress,
                        players[i].tokenId,
                        1
                    );
                }
            }
        }
        emit RacingFinished(trackName, players[winnerIndex]);
        clearPlayers();
    }

    function getPlayer(
        address playerAddress,
        uint256 tokenId
    ) public view returns (Player memory) {
        for (uint i = 1; i <= playerCount; i++) {
            Player memory _player = players[i];
            if (
                address(_player.playerAddress) == address(playerAddress) &&
                _player.tokenId == tokenId
            ) {
                return _player;
            }
        }
        return Player({playerAddress: address(0), tokenId: 0, score: 0});
    }

    function getPlayerTokens(
        address playerAddress
    ) public view returns (Player[] memory) {
        uint256 index = 0;
        Player[] memory _players = new Player[](playerCount);
        for (uint i = 1; i <= playerCount; i++) {
            Player memory _player = players[i];
            if (address(_player.playerAddress) == address(playerAddress)) {
                _players[index] = _player;
                index += 1;
            }
        }
        return _players;
    }

    uint private randNonce = 0;

    function randMod(uint _modulus) internal returns (uint) {
        randNonce++;
        return
            uint(
                keccak256(
                    abi.encodePacked(block.timestamp, msg.sender, randNonce)
                )
            ) % _modulus;
    }
}
"
    },
    "contracts/MintLrace.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/utils/Counters.sol";

import {ERC1155} from "./eip/ERC1155.sol";
// import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";

import "./extension/ContractMetadata.sol";
import "./extension/Multicall.sol";
import "./extension/Ownable.sol";
import "./extension/BatchMintMetadata.sol";

import "./lib/IStrings.sol";

contract MintLrace is
    ERC1155,
    ContractMetadata,
    Ownable,
    Multicall,
    BatchMintMetadata
{
    using IStrings for uint256;
    using Counters for Counters.Counter;

    event Minted(
        uint256 _tokenId,
        address _to,
        string _tokenURI,
        uint256 _amount,
        address _tokenAddress
    );

    /*//////////////////////////////////////////////////////////////
                        State variables
    //////////////////////////////////////////////////////////////*/

    Counters.Counter private tokenIdCounter;

    /*//////////////////////////////////////////////////////////////
                        Mappings
    //////////////////////////////////////////////////////////////*/

    mapping(uint256 => uint256) public totalSupply;
    mapping(uint256 => uint256[]) private _capability;

    /*//////////////////////////////////////////////////////////////
                            Constructor
    //////////////////////////////////////////////////////////////*/

    constructor(
        string memory _name,
        string memory _symbol
    ) ERC1155(_name, _symbol) {
        _setupOwner(msg.sender);
    }

    modifier onlyTokenOwner(uint256 _tokenId) {
        require(balanceOf[msg.sender][_tokenId] > 0, "!OWNER");
        _;
    }

    /*//////////////////////////////////////////////////////////////
                    Overridden metadata logic
    //////////////////////////////////////////////////////////////*/

    function uri(
        uint256 _tokenId
    ) public view virtual override returns (string memory) {
        string memory uriForToken = _uri[_tokenId];
        if (bytes(uriForToken).length > 0) {
            return uriForToken;
        }

        string memory batchUri = _getBaseURI(_tokenId);
        return string(abi.encodePacked(batchUri, _tokenId.toString()));
    }

    /*//////////////////////////////////////////////////////////////
                        Mint / burn logic
    //////////////////////////////////////////////////////////////*/

    function mintTo(
        address _to,
        uint256[] memory capabilities,
        string memory _tokenURI,
        uint256 _amount
    ) public virtual onlyOwner {
        require(capabilities.length == 4, "Invalid capability length");
        for (uint256 i = 0; i < 4; i++) {
            require(
                capabilities[i] >= 10 && capabilities[i] <= 100,
                "Invalid capability"
            );
        }
        uint256 tokenIdToMint = getNextTokenIdToMint();
        _mint(_to, tokenIdToMint, _amount, "");
        _setTokenURI(tokenIdToMint, _tokenURI);
        _capability[tokenIdToMint] = capabilities;
        emit Minted(tokenIdToMint, _to, _tokenURI, _amount, address(this));
    }

    function batchMintTo(
        address _to,
        uint256 _nftCount,
        uint256[] memory _amounts,
        string memory _baseURI
    ) public virtual {
        require(_amounts.length > 0, "Minting zero tokens.");
        require(_nftCount == _amounts.length, "Length mismatch.");

        uint256[] memory _tokenIds;

        for (uint256 i = 0; i < _nftCount; i += 1) {
            _tokenIds[i] = getNextTokenIdToMint();
        }

        if (_nftCount > 0) {
            _batchMintMetadata(_tokenIds[0], _nftCount, _baseURI);
        }

        _mintBatch(_to, _tokenIds, _amounts, "");
    }

    function burn(
        address _owner,
        uint256 _tokenId,
        uint256 _amount
    ) external virtual {
        address caller = msg.sender;

        require(
            caller == _owner || isApprovedForAll[_owner][caller],
            "Unapproved caller"
        );
        require(
            balanceOf[_owner][_tokenId] >= _amount,
            "Not enough tokens owned"
        );

        _burn(_owner, _tokenId, _amount);
    }

    function burnBatch(
        address _owner,
        uint256[] memory _tokenIds,
        uint256[] memory _amounts
    ) external virtual {
        address caller = msg.sender;

        require(
            caller == _owner || isApprovedForAll[_owner][caller],
            "Unapproved caller"
        );
        require(_tokenIds.length == _amounts.length, "Length mismatch");

        for (uint256 i = 0; i < _tokenIds.length; i += 1) {
            require(
                balanceOf[_owner][_tokenIds[i]] >= _amounts[i],
                "Not enough tokens owned"
            );
        }

        _burnBatch(_owner, _tokenIds, _amounts);
    }

    /*//////////////////////////////////////////////////////////////
                            View functions
    //////////////////////////////////////////////////////////////*/

    function getCurrentTokenId() public view virtual returns (uint) {
        return tokenIdCounter.current();
    }

    function getCapability(uint256 id) public view returns (uint256[] memory) {
        uint256[] memory _capabilities = new uint256[](4);
        _capabilities[0] = _capability[id][0];
        _capabilities[1] = _capability[id][1];
        _capabilities[2] = _capability[id][2];
        _capabilities[3] = _capability[id][3];
        return _capabilities;
    }

    function getUserBalance(
        address _user,
        uint256 _tokenId
    ) public view virtual returns (uint256) {
        return balanceOf[_user][_tokenId];
    }

    struct Token {
        uint256 tokenId;
        address tokenAddress;
        uint256 balance;
        string uri;
        address walletAddress;
        uint256[] capability;
    }

    function getUserTokens(
        address user
    ) public view virtual returns (Token[] memory) {
        uint256 index = 0;
        uint256 lastTokenId = tokenIdCounter.current();
        Token[] memory tokens = new Token[](lastTokenId);
        for (uint i = 1; i <= lastTokenId; i++) {
            uint256 balance = balanceOf[user][i];
            if (balance > 0) {
                tokens[index] = Token(
                    i,
                    address(this),
                    balance,
                    uri(i),
                    user,
                    getCapability(i)
                );
                index += 1;
            }
        }
        return tokens;
    }

    function getUserTokenById(
        address _userAddress,
        uint256 _tokenId
    ) public view virtual returns (Token memory) {
        return
            Token(
                _tokenId,
                address(this),
                balanceOf[_userAddress][_tokenId],
                uri(_tokenId),
                _userAddress,
                getCapability(_tokenId)
            );
    }

    function getNextTokenIdToMint() private returns (uint) {
        tokenIdCounter.increment();
        return tokenIdCounter.current();
    }

    /*//////////////////////////////////////////////////////////////
                    Internal (overrideable) functions
    //////////////////////////////////////////////////////////////*/

    function _canSetContractURI()
        internal
        view
        virtual
        override
        returns (bool)
    {
        return msg.sender == owner();
    }

    function _canMint() internal view virtual returns (bool) {
        return msg.sender == owner();
    }

    function _canSetOwner() internal view virtual override returns (bool) {
        return msg.sender == owner();
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        if (from == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                totalSupply[ids[i]] += amounts[i];
            }
        }

        if (to == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                totalSupply[ids[i]] -= amounts[i];
            }
        }
    }

    function editCapability(
        uint256 id,
        uint256[] memory capabilities
    ) external onlyOwner {
        require(capabilities.length == 4, "Invalid capability length");
        for (uint256 i = 0; i < 4; i++) {
            require(
                capabilities[i] >= 10 && capabilities[i] <= 100,
                "Invalid capability"
            );
        }

        _capability[id] = capabilities;
    }

    function transferAny(uint256 _tokenId, uint256 amount) external onlyOwner {
        _safeTransferFrom(address(this), msg.sender, _tokenId, amount, "");
    }

    function transferTo(
        uint256 _tokenId,
        address _to,
        uint256 amount
    ) external onlyTokenOwner(_tokenId) {
        require(
            amount <= balanceOf[msg.sender][_tokenId],
            "Insufficient funds"
        );
        _safeTransferFrom(msg.sender, _to, _tokenId, amount, "");
    }

    function updateUri(
        uint256 _tokenId,
        string memory _newUri
    ) external virtual onlyOwner {
        _uri[_tokenId] = _newUri;
    }
