    "contracts/Lottery.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/VRFCoordinatorV2Interface.sol";
import "./interfaces/ILogAutomation.sol";
import "./VRFConsumerBaseV2.sol";
import "hardhat/console.sol";

contract LotteryFactory is Ownable {
    string public name;
    string public symbol = "LUCKY1";
    uint public ticketPrice;
    uint public maxTickets;
    uint public endDate;
    address public charity;
    address public feeToken;
    uint public creatorFee;
    uint public charityFee;
    // affiliate fee on ticket purchase
    uint public affiliateFee;
    uint public startTime;
    uint public maxTicketsPerWallet;
    uint[] public prizeDistribution;
    uint public maxWinners;
    // upkeep contract for monitoring logs
    ILogAutomation public upkeep;
    address public forwarder;
    using Counters for Counters.Counter;
    Counters.Counter private _lotteryIdCounter;
    uint public creationFee = 0;
    // 10% affiliate percentage cut on creating lottery
    uint public affiliateFeeOnCreation = 10;
    address feeAddress = 0x750BD3353C2C88de4e520A23B6e9C0F4562cC6ad;
    uint platformFee = 10; // Platform fee is 10%
    mapping(address => bool) public isWhitelisted;

    // vrf storage
    VRFCoordinatorV2Interface public vrfCoordinator;
    uint64 public subId;

    struct LotteryFee {
        address feeToken;
        address charity;
        // affiliate wallet on lottery creation
        address payable affiliateWallet;
        uint creatorFee;
        uint charityFee;
        // affiliate fee for purchasing ticket
        uint affiliateFee;
    }

    event LotteryCreated(
        address indexed creator,
        address indexed lottery,
        uint ticketPrice,
        uint maxTickets,
        uint endDate,
        address charity,
        address feeToken,
        uint creatorFee,
        uint charityFee,
        uint startTime,
        uint maxTicketsPerWallet,
        uint maxWinners
    );

    event TriggerUpkeepLog(
        // the consumer being removed from vrf subscription
        address indexed consumer
    );

    event ConsumerRemoved(address indexed consumer);

    Lottery[] public lotteries;

    constructor(address _vrfCoordinator) {
        vrfCoordinator = VRFCoordinatorV2Interface(_vrfCoordinator);
    }

    /**
     * used to accept ownership of the vrf subscription from the EOA
     * LNK balance must  be at least 10 LNK before switching ownership
     */
    function acceptSubscriptionOwnership(uint64 _subId) external {
        (uint96 balance, , , ) = vrfCoordinator.getSubscription(_subId);
        require(
            balance > 10 * 10 ** 18,
            "minimum required link balance is 10 LNK"
        );
        vrfCoordinator.acceptSubscriptionOwnerTransfer(_subId);
        subId = _subId;
    }

    function requestSubscriptionOwnerTransfer(
        address _newOwner
    ) external onlyOwner {
        vrfCoordinator.requestSubscriptionOwnerTransfer(subId, _newOwner);
    }

    function whitelistAddress(
        address _address,
        bool _status
    ) external onlyOwner {
        isWhitelisted[_address] = _status;
    }

    // Batch whitelisting function for the LotteryFactory
    function whitelistAddresses(
        address[] memory _addresses,
        bool[] memory _statuses
    ) external onlyOwner {
        require(_addresses.length == _statuses.length, "Invalid input arrays");
        for (uint i = 0; i < _addresses.length; i++) {
            isWhitelisted[_addresses[i]] = _statuses[i];
        }
    }

    function setAffiliateFeeOnCreate(uint _affiliateFee) external onlyOwner {
        require(_affiliateFee < 100, "incorrect affiliate fee");
        affiliateFeeOnCreation = _affiliateFee;
    }

    function setPlatformAddress(address _feeAddress) external onlyOwner {
        feeAddress = _feeAddress;
    }

    function setPlatformFee(uint _platfomFee) external onlyOwner {
        require(_platfomFee < 25, "incorrect platfom fee");
        platformFee = _platfomFee;
    }

    function setCreationFee(uint _creationFee) external onlyOwner {
        creationFee = _creationFee;
    }

    function withdrawEther() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    function withdrawtoken(
        IERC20 _token,
        uint _amount,
        address _beneficiary
    ) external onlyOwner {
        _token.transferFrom(address(this), _beneficiary, _amount);
    }

    function createLottery(
        string memory _name,
        uint _ticketPrice,
        uint _maxTickets,
        uint _endDate,
        LotteryFee memory _feeParams,
        uint _maxTicketsPerWallet,
        uint[] memory _prizeDistribution
    ) public payable {
        // Pay creation fee in ether
        if (!isWhitelisted[msg.sender]) {
            require(msg.value == creationFee, "Incorrect fee");
        }

        uint affiliateCommission = (creationFee * affiliateFeeOnCreation) / 100;

        // if affiliate address is set and sender is NOT whitelisted, send affiliate cut to affiliate
        // and remaining to fee address
        if (
            _feeParams.affiliateWallet != address(0) &&
            !isWhitelisted[msg.sender]
        ) {
            _feeParams.affiliateWallet.transfer(affiliateCommission);
            // transfer remaining fee to fee wallet
            payable(feeAddress).transfer(creationFee - affiliateCommission);
        }
        // Convert any 0 values in prize distribution to 100
        for (uint i = 0; i < _prizeDistribution.length; i++) {
            if (_prizeDistribution[i] == 0) {
                _prizeDistribution[i] = 100;
            }
        }

        // Check if the sum of prize distribution percentages is equal to 100
        validatePrizeDistribution(_prizeDistribution);

        // If maxTicketsPerWallet is 0 or not provided, set it to 10% of all tickets
        if (maxTicketsPerWallet == 0) {
            maxTicketsPerWallet = (maxTickets * 10) / 100;
        }

        maxWinners = uint32(_prizeDistribution.length);

        Lottery.FeeParams memory feeParams = Lottery.FeeParams({
            feeAddress: feeAddress,
            charity: _feeParams.charity,
            feeToken: _feeParams.feeToken,
            platformFee: platformFee,
            creatorFee: _feeParams.creatorFee,
            charityFee: _feeParams.charityFee,
            affiliateFee: _feeParams.affiliateFee
        });

        Lottery lottery = new Lottery(
            _name,
            _ticketPrice,
            _maxTickets,
            _endDate,
            _maxTicketsPerWallet,
            _prizeDistribution,
            feeParams,
            subId,
            address(vrfCoordinator)
        ); // Deploy the contract without sending ether
        lotteries.push(lottery);

        // transfer ownership to creator
        lottery.transferOwnership(msg.sender);

        // add contract as consumer
        vrfCoordinator.addConsumer(subId, address(lottery));

        emit LotteryCreated(
            msg.sender,
            address(lottery),
            _ticketPrice,
            _maxTickets,
            _endDate,
            _feeParams.charity,
            _feeParams.feeToken,
            _feeParams.creatorFee,
            _feeParams.charityFee,
            block.timestamp,
            _maxTicketsPerWallet,
            maxWinners
        );
    }

    /**
     * @dev sets the upkeep contract responsible for monitoring the TriggerUpkeepLog
     * event.
     */
    function setUpkeep(address _upkeep, address _forwarder) external onlyOwner {
        upkeep = ILogAutomation(_upkeep);
        forwarder = _forwarder;
    }

    /**
     * @dev this function emits an event log that is being monitored by chainlink
     * automation nodes. Once the log is detected, the automation network will
     * call the checkLog function of the upkeep contract and consumer will be removed
     * from VRF coordinator via factory's remove consumer (since factory is sub owner).
     */
    function triggerUpkeep(address _consumer) public {
        require(_consumer == address(msg.sender), "caller must be consumer");
        // can not end a lottery if upkeep is not configured
        require(
            address(upkeep) != address(0) && address(forwarder) != address(0),
            "upkeep not configured"
        );

        emit TriggerUpkeepLog(_consumer);
    }

    /**
     * @dev simply removing the consumer as the function already checks if the subId and consumer
     * are valid. It will revert if consumer does not exist.
     */
    function removeConsumer(address _consumer, address _upkeepCaller) external {
        // only the upkeep contract can call this function
        require(
            address(msg.sender) == address(upkeep) &&
                _upkeepCaller == address(forwarder),
            "caller is not legal upkeep"
        );

        vrfCoordinator.removeConsumer(subId, _consumer);
        emit ConsumerRemoved(_consumer);
    }

    function validatePrizeDistribution(
        uint[] memory _prizeDistribution
    ) internal pure {
        uint totalPrizeDistribution;
        for (uint i = 0; i < _prizeDistribution.length; i++) {
            totalPrizeDistribution += _prizeDistribution[i];
        }
        require(totalPrizeDistribution == 100, "Invalid prize distribution");
    }
}

contract Lottery is ERC721, Ownable, VRFConsumerBaseV2 {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIdCounter;

    event TicketPurchased(address indexed buyer, uint256 amount);
    event PrizeWithdrawn(address indexed winner, uint256 amount);
    event AddressWhitelisted(address indexed addr, bool isWhitelisted);

    uint public ticketPrice;
    uint public maxTickets;
    uint public endDate;
    address public charity;
    address public feeAddress;
    address public feeToken;
    uint public creatorFee;
    uint public charityFee;
    uint public affiliateFee;
    uint public startTime;
    uint public maxTicketsPerWallet;
    uint[] public prizeDistribution;
    uint32 public maxWinners;
    uint public platformFee;
    uint16 public totalTicketsPurchased;
    address public vrfCoordinator;
    // lottery factory
    LotteryFactory public factory;
    uint64 public subId;

    address[] public winners;
    mapping(address => address) public affiliations;
    mapping(address => bool) public isWinner;
    mapping(address => bool) public isWithdrawn;
    mapping(address => uint) public myTickets;
    mapping(address => uint[]) public myTicketsIds;
    mapping(address => bool) private _isWhitelisted; // Private mapping for lottery-specific whitelisted addresses
    mapping(uint => bool) private _isNumberChosen;

    bool public isRunning;

    struct FeeParams {
        address feeAddress;
        uint platformFee;
        address charity;
        address feeToken;
        uint creatorFee;
        uint charityFee;
        uint affiliateFee;
    }

    constructor(
        string memory _name,
        uint _ticketPrice,
        uint _maxTickets,
        uint _endDate,
        uint _maxTicketsPerWallet,
        uint[] memory _prizeDistribution,
        FeeParams memory _feeParams,
        uint64 _subId,
        address _vrfCoordinator
    )
        ERC721(_name, "LUCKY1")
        VRFConsumerBaseV2(0x6A2AAd07396B36Fe02a22b33cf443582f682c82f)
    {
        ticketPrice = _ticketPrice;
        maxTickets = _maxTickets;
        endDate = _endDate;
        charity = _feeParams.charity;
        feeToken = _feeParams.feeToken;
        creatorFee = _feeParams.creatorFee;
        charityFee = _feeParams.charityFee;
        affiliateFee = _feeParams.affiliateFee;
        startTime = block.timestamp;
        maxTicketsPerWallet = _maxTicketsPerWallet;
        prizeDistribution = _prizeDistribution;
        maxWinners = uint32(_prizeDistribution.length);
        feeAddress = _feeParams.feeAddress;
        platformFee = _feeParams.platformFee;
        factory = LotteryFactory(msg.sender);
        isRunning = true;
        subId = _subId;
        vrfCoordinator = _vrfCoordinator;

        _transferOwnership(msg.sender);
    }

    function purchaseLottery(uint amount, address _affiliateWallet) external {
        require(isRunning, "Lottery is over");
        require(amount > 0, "Amount must be greater than 0");
        require(
            totalTicketsPurchased + amount <= maxTickets,
            "Max tickets exceeded"
        );
        require(
            myTickets[msg.sender] + amount <= maxTicketsPerWallet, // Enforce the limit on tickets per wallet
            "Max tickets per wallet exceeded"
        );
        // Calculate total ticket price
        uint totalTicketPrice = amount * ticketPrice;

        if (!_isWhitelisted[msg.sender]) {
            // If not whitelisted, transfer the ticket price to the contract
            require(
                IERC20(feeToken).transferFrom(
                    msg.sender,
                    address(this),
                    totalTicketPrice
                ),
                "Failed to transfer feeToken"
            );

            // deduct the platform fee and send it to platform address if ticket price > 0
            if (ticketPrice > 0) {
                uint platformCut = (totalTicketPrice * platformFee) / 100;

                require(
                    IERC20(feeToken).transfer(feeAddress, platformCut),
                    "Failed to transfer platform fee"
                );
            }
        }

        uint affiliateCut = _affiliateWallet != address(0) &&
            !_isWhitelisted[msg.sender]
            ? (totalTicketPrice * affiliateFee) / 100
            : 0;

        // if purchasing through affiliate link, transfer affiliate cut to referrer
        if (affiliateCut > 0) {
            // store the affiliate in mapping
            require(
                _affiliateWallet != msg.sender,
                "purchaser cannot be the same as the affiliate"
            );
            affiliations[msg.sender] = _affiliateWallet;
            IERC20(feeToken).transfer(_affiliateWallet, affiliateCut);
        }

        for (uint i = 0; i < amount; i++) {
            uint256 tokenId = _tokenIdCounter.current();
            _tokenIdCounter.increment();
            _safeMint(msg.sender, tokenId);
            myTickets[msg.sender] += 1;
            myTicketsIds[msg.sender].push(tokenId);
            totalTicketsPurchased++;
        }
        emit TicketPurchased(msg.sender, amount);
    }

    function getPurchasedTickets() external view returns (uint256) {
        return _tokenIdCounter.current();
    }

    function endLottery(uint32 _gasAmount) external {
        if (totalTicketsPurchased == 0) {
            revert("no tickets bought");
        }
        requestRandomNumbers(_gasAmount);
        // if purchased tickets AND max winners are greater than 16, request for more random
        // numbers using => max winners / 16
    }

    /**
     * @dev chainlink RNG oracle
     * NOTE: Cannot exceed VRFCoordinatorV2.MAX_NUM_WORDS.
     * NOTE: Extracting sub-random numbers from chainlink's random number
     * @dev a max of 16 sub-random numbers can be extracted from chainlink's random number
     * if the lottery requires more random numbers, please request for more
     * Assumes the subscription is funded sufficiently.
     */
    function requestRandomNumbers(
        uint32 _gasAmount
    ) internal returns (uint256) {
        uint32 randomNumCount;
        // calculate number of random numbers required
        if (totalTicketsPurchased < maxWinners) {
            randomNumCount = 1;
        } else {
            randomNumCount = (maxWinners / 16) + (maxWinners % 16 == 0 ? 0 : 1);
        }

        uint256 requestId = VRFCoordinatorV2Interface(vrfCoordinator)
            .requestRandomWords(
                0xba6e730de88d94a5510ae6613898bfb0c3de5d16e609c5b7da808747125506f7, // 500 gwei lane
                subId,
                3, // request confirmations
                _gasAmount,
                randomNumCount // decided according to number of maxWinners
            );

        return requestId;
    }

    /**
     * @dev callback function that recieves the requested random numbers and sets winners
     * @param randomWords random numbers
     */
    function fulfillRandomWords(
        uint256,
        uint256[] memory randomWords
    ) internal override {
        uint randomIndex = 0;
        uint randomNum;

        if (totalTicketsPurchased < maxWinners) {
            // total tickets bought are less than max winners
            // there will be only 1 winner
            randomNum = randomWords[randomIndex] % totalTicketsPurchased;
            uint[] memory selectedWinners = new uint[](1);
            selectedWinners[0] = randomNum;
            setWinners(selectedWinners);
        } else {
            uint[] memory selectedWinners = new uint[](maxWinners);
            uint16 subRandomNumber;
            uint16 backupOffset;
            uint16 selectedTicket;
            for (uint i = 0; i < maxWinners; i++) {
                subRandomNumber = uint16(randomNum);
                backupOffset = 0;
                selectedTicket =
                    (subRandomNumber + backupOffset) %
                    totalTicketsPurchased;
                // keep incrementing offset until unchosen ticket found if ticket is already chosen
                while (_isNumberChosen[selectedTicket]) {
                    backupOffset++;
                    selectedTicket =
                        (subRandomNumber + backupOffset) %
                        totalTicketsPurchased;
                }
                _isNumberChosen[selectedTicket] = true;
                selectedWinners[i] = selectedTicket;

                if (i != 0 && i % 16 == 0) {
                    // random number exhausted, move to next
                    randomIndex++;
                    randomNum = randomWords[randomIndex];
                } else {
                    randomNum = randomNum >> 16;
                }
            }
            setWinners(selectedWinners);
        }
    }

    function setWinners(uint[] memory tokenIds) internal {
        uint _totalFunds = IERC20(feeToken).balanceOf(address(this));
        // deduct platform fee if ticket price
        if (ticketPrice == 0) {
            uint platformCut = (_totalFunds * platformFee) / 100;
            IERC20(feeToken).transfer(feeAddress, platformCut);
            _totalFunds = _totalFunds - platformCut;
        }
        uint _creatorFee = (_totalFunds * creatorFee) / 100;
        uint _charityFee = (_totalFunds * charityFee) / 100;
        uint totalPrize = _totalFunds - _creatorFee - _charityFee;

        require(totalPrize > 0, "No balance");

        for (uint i = 0; i < tokenIds.length; i++) {
            address winner = ownerOf(tokenIds[i]);
            winners.push(winner);
            isWinner[winner] = true;
        }

        for (uint i = 0; i < tokenIds.length; i++) {
            address winner = winners[i];

            // Calculate the prize distribution for this winner
            uint prizePercentage = (tokenIds.length > 1)
                ? prizeDistribution[i]
                : 100;
            uint prizeAmount = (totalPrize * prizePercentage) / 100;

            // if the winner's ticket was purchased through affiliate, distribute affiliate cut as well
            address affiliate = affiliations[winner];
            if (affiliate != address(0)) {
                uint affiliateCut = (prizeAmount * affiliateFee) / 100;
                IERC20(feeToken).transfer(affiliate, affiliateCut);
                prizeAmount = prizeAmount - affiliateCut;
            }

            // transfer to affiliate
            IERC20(feeToken).transfer(winner, prizeAmount);
        }

        IERC20(feeToken).transfer(charity, _charityFee);
        IERC20(feeToken).transfer(owner(), _creatorFee);

        // removed from consumers
        factory.triggerUpkeep(address(this));

        isRunning = false;
    }

    // Function to whitelist an address for this lottery
    function whitelistAddresses(
        address[] memory _addresses,
        bool[] memory _statuses
    ) external onlyOwner {
        require(_addresses.length == _statuses.length, "Invalid input arrays");
        for (uint i = 0; i < _addresses.length; i++) {
            _isWhitelisted[_addresses[i]] = _statuses[i];
            emit AddressWhitelisted(_addresses[i], _statuses[i]);
        }
    }
}

contract Upkeep is ILogAutomation, Ownable {
    LotteryFactory public factory;

    constructor(address _lotteryFactory) {
        // assign lottery factory
        factory = LotteryFactory(_lotteryFactory);
    }

    /**
     * @dev Don't need to check anything, all the checks will be made by remove consumer function
     */
    function checkLog(
        Log calldata log,
        bytes memory
    ) external pure returns (bool upkeepNeeded, bytes memory performData) {
        upkeepNeeded = true;
        address consumerAddress = bytes32ToAddress(log.topics[1]);
        performData = abi.encode(consumerAddress);
    }

    function performUpkeep(bytes calldata performData) external override {
        // only the automation contract is allowed to call this function
        address consumerAddress = abi.decode(performData, (address));
        // remove consumer through lottery factory
        factory.removeConsumer(consumerAddress, address(msg.sender));
    }

    function bytes32ToAddress(bytes32 _address) public pure returns (address) {
        return address(uint160(uint256(_address)));
    }

    function setFactory(address _factory) external onlyOwner {
        factory = LotteryFactory(_factory);
    }
}
"
