{"Acceptable.sol":{"content":"pragma solidity 0.8.4;
// SPDX-License-Identifier: Unlicensed
import "EnumerableSet.sol";
import "Ownable.sol";

contract Acceptable is Ownable {
    using EnumerableSet for EnumerableSet.AddressSet;
    EnumerableSet.AddressSet adminSet;
    EnumerableSet.AddressSet minterSet;

    modifier onlyAdmin {
        require(adminSet.contains(msg.sender),"caller is not the admin");
        _;
    }
    modifier onlyMinter {
        require(minterSet.contains(msg.sender),"caller is not the minter");
        _;
    }

    function addAdmin(address account) external onlyOwner {
        adminSet.add(account);
    }
    function removeAdmin(address account) external onlyOwner {
        adminSet.remove(account);
    }
    function adminAt(uint index) external view onlyOwner returns(address) {
        return adminSet.at(index);
    }
    function getAdminLength() external view onlyOwner returns(uint) {
        return adminSet.length();
    }

    function addMinter(address account) external onlyAdmin{
        minterSet.add(account);
    }
    function removeMinter(address account) external onlyAdmin{
        minterSet.remove(account);
    }
    function minterAt(uint index) external view onlyAdmin returns(address) {
        return minterSet.at(index);
    }
    function getMinterLength() external view onlyAdmin returns(uint) {
        return minterSet.length();
    }

}"},"Context.sol":{"content":"
