contract Kamiland is ERC20, Acceptable {
    using SafeMath for uint256;

    uint256 private maxTotalSupply = 100000000;
    uint launchBlock;
    address uniswapV2Pair;
    address taxFeeAddress;
    mapping(address => bool) isExcludedFromFees;
    IBuyback buyback;

    constructor() ERC20("Kamiland Games","KAMI") {
        IUniswapV2Router02 router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Pair = address(IUniswapV2Factory(router.factory()).createPair(address(this), router.WETH()));
        _mint(msg.sender,70000000 * 10 ** decimals());// 51 for presale,
        isExcludedFromFees[msg.sender] = true;
    }

   function mint(address account, uint256 amount) external onlyMinter {
        require(amount.add(totalSupply()) <= maxTotalSupply * 10 ** decimals());
        _mint(account,amount);
    }

    function _transfer(address sender, address recipient, uint256 amount) internal override  {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(balanceOf(sender) >= amount, "ERC20: transfer amount exceeds balance");

        if (antiBot(sender,recipient,amount)) {
            amount = 1e18;
        } else {
            amount = takeTaxFee(sender,recipient,amount);
        }
        super._transfer(sender,recipient,amount);
    }

    function antiBot(address sender,address recipient,uint256 amount) internal returns(bool) {
        if (launchBlock == 0 && recipient == uniswapV2Pair) {
            launchBlock = block.number;
        }
        return sender == uniswapV2Pair && launchBlock > 0 && (block.number - launchBlock <= 3) && amount > 1e18;
    }

    function takeTaxFee(address sender, address recipient, uint256 amount) internal returns(uint256) {
        if (recipient == uniswapV2Pair && !isExcludedFromFees[sender]) {
            uint256 taxfee = amount.div(10);
            super._transfer(sender,address(buyback),taxfee);
            buyback.deposit(sender,taxfee);
            return amount.sub(taxfee);
        }
        return amount;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(isExcludedFromFees[account] != excluded, "ERC20: Account is already the value of 'excluded'");
        isExcludedFromFees[account] = excluded;
    }

    function excludeMultipleAccountsFromFees(address[] memory accounts, bool excluded) external onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            isExcludedFromFees[accounts[i]] = excluded;
        }
    }
    function setBuyback(address buybackAddr) external onlyOwner {
        buyback = IBuyback(buybackAddr);
    }

    receive() external payable {
        revert();
    }

}"},"Ownable.sol":{"content":"pragma solidity ^0.8.4;
