    "contracts/MintFactory.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

interface INFT {
    function mintBatch(address account, uint256 amount)
        external
        returns (uint256[] memory);
}

contract MintFactory is Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    address private treasuryReceiver =
        0xCa69748234895f77d448911F001ab269937d1bAD;

    struct Rules {
        uint256 startTime;
        uint256 endTime;
        uint256 cost;
    }
    mapping(address => Rules) public listNfts;

    mapping(uint256 => address) public userLength;

    mapping(address => mapping(address => bool)) public freeWallets;


    uint256 public numberOfUser = 0;

    event Mint(
        address indexed owner,
        uint256 tokenId,
        address indexed nftAddress
    );

    constructor() {}

    modifier onlyActive(address _nftAddress) {
        if (!_publicActive(_nftAddress)) {
            revert("Minting_Inactive");
        }
        _;
    }

    /* ============ External Functions ============ */
    function mintWeek(
        address _nftAddress
    ) external payable onlyActive(_nftAddress) nonReentrant {
        require(isEOA(_msgSender()), "Sender is not an EOA");

        require(
            !freeWallets[_msgSender()][_nftAddress], "Max NFT Per Wallet exceeded"
        );

        uint256 _amount = 1;

        transferFund(_nftAddress, _amount);

        INFT(_nftAddress).mintBatch(_msgSender(), _amount);

        freeWallets[_msgSender()][_nftAddress] = true;

        emit Mint(_msgSender(), _amount, _nftAddress);
    }

    /* ============ Internal Functions ============ */
    function transferFund(
        address _nftAddress,
        uint256 _amount
    ) internal {
        uint256 costMint = listNfts[_nftAddress].cost;

        require(msg.value >= costMint * _amount, "Insufficient funds!");

        (bool success, ) = payable(treasuryReceiver).call{
                value: msg.value
        }("");

        require(success, "TransferHelper: ETH_TRANSFER_FAILED");
    }

    /* ============ Admin Functions ============ */
    function emergencyWithdraw() external onlyOwner {
        uint256 bnbBalance = address(this).balance;

        if (bnbBalance > 0) {
            (bool success, ) = payable(treasuryReceiver).call{
                value: address(this).balance
            }("");
            require(success, "TransferHelper: ETH_TRANSFER_FAILED");
        }
    }

    function setRuleNFT(address _nftAddress, uint256 _cost, uint256 _startTime, uint256 _endTime) external onlyOwner {
        Rules storage rules = listNfts[_nftAddress];
        rules.cost = _cost;
        rules.startTime = _startTime;
        rules.endTime = _endTime;
    }

    function isEOA(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size == 0;
    }

    function _publicActive(address _nftAddress) internal view returns (bool) {
        return
            listNfts[_nftAddress].startTime <= block.timestamp &&
            listNfts[_nftAddress].endTime > block.timestamp;
    }
}
