contract Premine {
    event Premint(
        address indexed _tokenHolder,
        uint _amount
    );

    modifier onlyPreminter(
        address _address
    )
    {
        require(
            _preminter == _address,
            "is not preminter!"
        );
        _;
    }

    modifier onlyNotPreminted(
        address _address
    )
    {
        require(
            !_isPreminted[_address],
            "already preminted!"
        );
        _;
    }

    modifier onlyPreminting()
    {
        require(
            !_stopped,
            "already stopped to premine!"
        );
        _;
    }

    bool private _stopped;

    mapping(address => bool) internal _isPreminted;
    address internal _preminter;


    constructor (
        address _initPreminter
    )
        public
    {
        _preminter = _initPreminter;
    }

    function _disablePremine()
        internal
    {
        _stopped = true;
        _preminter = address(0x0);
    }
}
