import "./ERC20.sol";
import "./Premine.sol";

contract WrappedTokenSimple is ERC20, Premine {
    event BurnWrappedToken(
        address indexed _tokenHolder,
        string _coinReceiver,
        uint256 _amount
    );

    constructor(
        address _initPreminter,
        string memory _initName,
        string memory _initSymbol
    ) public ERC20(_initName, _initSymbol) Premine(_initPreminter) {}

    // lockable added to origin transfer, transferFrom from uniswap sourcecode
    function transfer(address recipient, uint256 amount) public returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public returns (bool) {
        _transferFrom(msg.sender, sender, recipient, amount);
        return true;
    }

    function burn(uint256 _amount, string memory _coinReceiver) public {
        address sender = msg.sender;
        _burn(sender, _amount);
        emit BurnWrappedToken(sender, _coinReceiver, _amount);
    }

    function premine(address _tokenHolder, uint256 _amount)
        public
        onlyPreminter(msg.sender)
        onlyNotPreminted(_tokenHolder)
        onlyPreminting()
    {
        _isPreminted[_tokenHolder] = true;
        _mint(_tokenHolder, _amount);
        emit Premint(_tokenHolder, _amount);
    }

    function stopPremine() public onlyPreminter(msg.sender) {
        _disablePremine();
    }
}
