pragma solidity ^0.8.2;

import "IERC20.sol";
import "ERC1155.sol";
import "Ownable.sol";
import "ECDSA.sol";
import "IMintCoin.sol";

contract HaglienNFT is ERC1155, Ownable {
    constructor(
        address treasury_,
        address signer_,
        address pool_
    ) ERC1155("http://api.heglien.com/token/{id}.json") {
        _treasury = treasury_;
        _signer = signer_;
        _pool = pool_;
    }

    event Success(uint256 nonce);

    address private _treasury;
    address private _signer;
    address private _pool;
    mapping(uint256 => bool) private _nonces;

    // don't overlap to kind range
    uint256 private _idCounter = 10000;
    // 1: FT, 2: NFT
    mapping(uint256 => uint8) private _kindTypes;
    mapping(uint256 => uint256) private _tokenToKind;
    mapping(uint256 => uint256) private _kindAmounts;

    function setURI(string memory newuri) external onlyOwner {
        _setURI(newuri);
    }

    function setTreasury(address treasury_) external onlyOwner {
        _treasury = treasury_;
    }

    function setSigner(address signer_) external onlyOwner {
        _signer = signer_;
    }

    function setPool(address pool_) external onlyOwner {
        _pool = pool_;
    }

    function getInfo()
        external
        view
        returns (
            address,
            address,
            address
        )
    {
        return (_treasury, _signer, _pool);
    }

    function addKindTypes(uint256[] memory kinds, uint8[] memory types)
        external
        onlyOwner
    {
        require(kinds.length == types.length);

        for (uint256 i = 0; i < kinds.length; i++) {
            uint256 kind = kinds[i];
            uint8 type_ = types[i];
            require(
                kind <= 10000 &&
                    _kindTypes[kind] == 0 &&
                    (type_ == 1 || type_ == 2)
            );

            _kindTypes[kind] = type_;
            if (type_ == 1) {
                _tokenToKind[kind] = kind;
            }
        }
    }

    function getKindType(uint256 kind) external view returns (uint8) {
        return _kindTypes[kind];
    }

    function _charge(address token, uint256 amount) internal {
        if (token == address(0)) {
            require(msg.value >= amount, "insufficient value");
            payable(_treasury).transfer(msg.value);
        } else {
            require(
                IERC20(token).transferFrom(msg.sender, _treasury, amount),
                "fails to transfer token"
            );
        }
    }

    function _verify(
        uint256 nonce,
        bytes memory signature,
        bytes memory packed
    ) internal {
        // this recreates the message that was signed on the client
        bytes32 message = ECDSA.toEthSignedMessageHash(keccak256(packed));
        address signer = ECDSA.recover(message, signature);
        require(signer == _signer, "signature invalid");

        require(!_nonces[nonce], "nonce not available");
        _nonces[nonce] = true;

        emit Success(nonce);
    }

    function _verifyBatch(
        uint256[] memory nonces,
        bytes memory signature,
        bytes memory packed
    ) internal {
        // this recreates the message that was signed on the client
        bytes32 message = ECDSA.toEthSignedMessageHash(keccak256(packed));
        address signer = ECDSA.recover(message, signature);
        require(signer == _signer, "signature invalid");

        for (uint256 i = 0; i < nonces.length; i++) {
            uint256 nonce = nonces[i];
            require(!_nonces[nonce], "nonce not available");
            _nonces[nonce] = true;

            emit Success(nonce);
        }
    }

    function _mintFungible(
        address to,
        uint256 kind,
        uint256 amount
    ) internal {
        require(_kindTypes[kind] == 1, "kind not FT");
        _mint(to, kind, amount, "");
        _kindAmounts[kind] += amount;
    }

    function _mintNonFungible(address to, uint256 kind)
        internal
        returns (uint256)
    {
        require(_kindTypes[kind] == 2, "kind not NFT");

        _idCounter += 1;
        _mint(to, _idCounter, 1, "");
        _tokenToKind[_idCounter] = kind;
        _kindAmounts[kind] += 1;

        return _idCounter;
    }

    function mintFungible(
        address to,
        uint256 kind,
        uint256 amount,
        address coin,
        uint256 coinAmount,
        uint256 nonce,
        bytes memory signature
    ) external payable {
        _verify(
            nonce,
            signature,
            abi.encodePacked(to, kind, amount, coin, coinAmount, nonce)
        );

        if (coinAmount > 0) {
            _charge(coin, coinAmount);
        }

        _mintFungible(to, kind, amount);
    }

    function mintNonFungibles(
        address to,
        uint256[] memory kinds,
        uint256[] memory limits,
        address coin,
        uint256 coinAmount,
        uint256 nonce,
        bytes memory signature
    ) external payable {
        require(kinds.length == limits.length);
        _verify(
            nonce,
            signature,
            abi.encodePacked(to, kinds, limits, coin, coinAmount, nonce)
        );

        if (coinAmount > 0) {
            _charge(coin, coinAmount);
        }

        for (uint256 i = 0; i < kinds.length; i++) {
            require(_kindAmounts[kinds[i]] < limits[i], "limit exceeded");
            _mintNonFungible(to, kinds[i]);
        }
    }

    function mintVarious(
        address to,
        uint256[] memory nonFungibleKinds,
        uint256[] memory fungibleKinds,
        uint256[] memory fungibleAmounts,
        address mintCoin,
        uint256 mintCoinAmount,
        uint256 mintIntoPool,
        uint256[] memory nonces,
        bytes memory signature
    ) external payable {
        require(fungibleKinds.length == fungibleAmounts.length);
        _verifyBatch(
            nonces,
            signature,
            abi.encodePacked(
                to,
                nonFungibleKinds,
                fungibleKinds,
                fungibleAmounts,
                mintCoin,
                mintCoinAmount,
                mintIntoPool,
                nonces
            )
        );

        if (nonFungibleKinds.length > 0) {
            for (uint256 i = 0; i < nonFungibleKinds.length; i++) {
                _mintNonFungible(to, nonFungibleKinds[i]);
            }
        }

        if (fungibleKinds.length > 0) {
            for (uint256 i = 0; i < fungibleKinds.length; i++) {
                _mintFungible(to, fungibleKinds[i], fungibleAmounts[i]);
            }
        }

        if (mintCoinAmount > 0) {
            IMintCoin(mintCoin).mint(msg.sender, mintCoinAmount);
        }

        if (mintIntoPool > 0) {
            IMintCoin(mintCoin).mint(_pool, mintIntoPool);
        }
    }

    function burnFungible(
        uint256 kind,
        uint256 amount,
        uint256 nonce,
        bytes memory signature
    ) external payable {
        _verify(nonce, signature, abi.encodePacked(kind, amount, nonce));

        _burn(msg.sender, kind, amount);
        _kindAmounts[kind] -= amount;
    }

    function upgradeNonFungible(
        address to,
        uint256 upgradeTo,
        uint256[] memory tokens,
        uint256[] memory tokenAmounts,
        address coin,
        uint256 coinAmount,
        uint256 nonce,
        bytes memory signature
    ) external payable {
        _verify(
            nonce,
            signature,
            abi.encodePacked(
                to,
                upgradeTo,
                tokens,
                tokenAmounts,
                coin,
                coinAmount,
                nonce
            )
        );

        if (coinAmount > 0) {
            _charge(coin, coinAmount);
        }

        if (tokens.length > 0) {
            require(tokens.length == tokenAmounts.length, "parameter invalid");
            _burnBatch(msg.sender, tokens, tokenAmounts);
        }

        if (upgradeTo > 0) {
            _mintNonFungible(to, upgradeTo);
        }
    }

    function convertTokens(
        address to,
        uint256[] memory kindOrTokens,
        int256[] memory amounts,
        address coin,
        uint256 coinAmount,
        uint256 nonce,
        bytes memory signature
    ) external payable {
        require(kindOrTokens.length == amounts.length);
        _verify(
            nonce,
            signature,
            abi.encodePacked(to, kindOrTokens, amounts, coin, coinAmount, nonce)
        );

        if (coinAmount > 0) {
            _charge(coin, coinAmount);
        }

        for (uint256 i = 0; i < kindOrTokens.length; i++) {
            uint256 kt = kindOrTokens[i];
            int256 amount = amounts[i];
            require(amount != 0, "amount != 0");

            if (amount > 0) {
                require(_kindTypes[kt] != 0, "kind unknown");
                if (_kindTypes[kt] == 1) {
                    _mintFungible(to, kt, uint256(amount));
                } else {
                    for (int256 j = 0; j < amount; j++) {
                        _mintNonFungible(to, kt);
                    }
                }
            } else {
                _burn(msg.sender, kt, uint256(-amount));
            }
        }
    }

    function pay(
        address coin,
        uint256 coinAmount,
        uint256 nonce,
        bytes memory signature
    ) external payable {
        _verify(nonce, signature, abi.encodePacked(coin, coinAmount, nonce));

        if (coinAmount > 0) {
            _charge(coin, coinAmount);
        }
    }

    function airDropNonFungible(uint256 kind, address[] memory accounts)
        external
        onlyOwner
    {
        require(_kindTypes[kind] == 2, "kind not NFT");

        for (uint256 i = 0; i < accounts.length; i++) {
            _idCounter += 1;
            _mint(accounts[i], _idCounter, 1, "");
            _tokenToKind[_idCounter] = kind;
        }
        _kindAmounts[kind] += accounts.length;
    }

    function totalSupply(uint256 kind) public view returns (uint256) {
        return _kindAmounts[kind];
    }

    function getKind(uint256 token) public view returns (uint256) {
        return _tokenToKind[token];
    }
}
