     * Requirements:
     *
     * - `index` must be strictly less than {length}.
     */
    function at(UintSet storage set, uint256 index)
        internal
        view
        returns (uint256)
    {
        return uint256(_at(set._inner, index));
    }
}

interface IDigitalaxNFT {
    function price(uint256 tokenId) external view returns (uint256);

    function tokenOfOwnerByIndex(address owner, uint256 index)
        external
        view
        returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) external;

    function approve(address to, uint256 tokenId) external;

    function ownerOf(uint256 tokenId) external view returns (address owner);
}

contract ZionFarmersLockPool is Ownable, Pausable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;
    IDigitalaxNFT public parentNFT;

    struct UserInfo {
        uint256 amount;
        uint256[] tokenIds;
        mapping(uint256 => uint256) tokenIndex;
        uint256 rewardDebt;
        uint256 pendingRewards;
        uint256 depositedAt;
        uint256 claimedAt;
    }

    uint256 public lastUpdateTime;
    uint256 public accPerShare;
    uint256 public totalSupply;
    uint256 public maxCap = uint256(-1);
    uint256 public totalReward;
    uint256 public lockupDuration = 30 days;
    uint256 public claimLockDuration;

    IERC20 public rewardToken;
    address public feeRecipient;
    uint256 public claimFee;
    uint256 public constant MAX_FEE = 10000;
    uint256 public constant FEE_LIMIT = 10000; // 100%
    uint256 public rewardRate = uint256(0.0023 ether);
    uint256 public endTime;
    uint256 public rewardCycle;

    mapping(address => UserInfo) public userInfo;
    mapping(uint256 => bool) public allowedTokens;
    EnumerableSet.AddressSet users;

    event Stake(address indexed user, uint256 amount);
    event UnStake(address indexed user, uint256 amount);
    event Claim(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);

    modifier updateUserList() {
        _;
        if (
            userInfo[msg.sender].amount > 0 ||
            userInfo[msg.sender].pendingRewards > 0
        ) _checkOrAddUser(msg.sender);
        else _removeUser(msg.sender);
    }

    modifier updateReward() {
        UserInfo storage user = userInfo[msg.sender];

        if (totalSupply > 0) {
            uint256 multiplier = Math.min(block.timestamp, endTime).sub(
                lastUpdateTime
            );
            uint256 reward = multiplier.mul(rewardRate);
            totalReward = totalReward.add(reward);
            accPerShare = accPerShare.add(reward.mul(1e12).div(totalSupply));
        }
        lastUpdateTime = Math.min(block.timestamp, endTime);

        uint256 pending = user.amount.mul(accPerShare).div(1e12).sub(
            user.rewardDebt
        );
        user.pendingRewards = user.pendingRewards.add(pending);

        _;

        user.rewardDebt = user.amount.mul(accPerShare).div(1e12);
        if (user.claimedAt == 0) user.claimedAt = block.timestamp;
    }

    constructor(IERC20 _rewardsToken, IDigitalaxNFT _parentNFT) {
        rewardToken = _rewardsToken;
        feeRecipient = msg.sender;
        parentNFT = _parentNFT;

        lastUpdateTime = block.timestamp;
        endTime = block.timestamp.add(180 days);
    }

    function setLockupDuration(uint256 _lockupDuration) external onlyOwner {
        lockupDuration = _lockupDuration;
    }

    function setClaimLockDuration(uint256 _claimLockDuration) external onlyOwner {
        claimLockDuration = _claimLockDuration;
    }

    function setMaxCap(uint256 _cap) external onlyOwner {
        maxCap = _cap;
    }

    function isLocked(address _user) public view returns (bool) {
        UserInfo storage user = userInfo[_user];
        return block.timestamp < user.depositedAt.add(lockupDuration);
    }

    function setOwnToken(uint256[] memory _tokenIds, bool allowed) external onlyOwner {
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            allowedTokens[_tokenIds[i]] = allowed;
        }
    }

    function setEndTime(uint256 _time) external onlyOwner {
        require(block.timestamp < _time, "!available");
        endTime = _time;
    }

    function setRewardCycle(uint256 _cycle) external onlyOwner {
        rewardCycle = _cycle;
    }

    function isAllowed(uint256 _id) public view returns (bool) {
        return allowedTokens[_id];
    }

    function stakeAll()
        external
        nonReentrant
        whenNotPaused
        updateReward
        updateUserList
    {
        uint256 balance = parentNFT.balanceOf(msg.sender);
        uint256[] memory _tokenIds = new uint256[](balance);

        for (uint256 i = 0; i < balance; i++) {
            uint256 _tokenId = parentNFT.tokenOfOwnerByIndex(msg.sender, i);
            if (isAllowed(_tokenId)) _tokenIds[i] = _tokenId;
        }

        for (uint256 i = 0; i < balance; i++) {
            if (_tokenIds[i] > 0) _stake(_tokenIds[i]);
        }
    }

    function stake(uint256[] memory _tokenIds)
        external
        nonReentrant
        whenNotPaused
        updateReward
        updateUserList
    {
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            uint256 _tokenId = _tokenIds[i];
            require(
                parentNFT.ownerOf(_tokenId) == msg.sender,
                "error: only owner can stake!"
            );
            require(isAllowed(_tokenId), "error: not allowed NFT!");
            _stake(_tokenId);
        }
    }

    function _stake(uint256 _tokenId) internal {
        require(block.timestamp < endTime, "expired");

        UserInfo storage user = userInfo[msg.sender];

        if(user.amount == 0) user.depositedAt = block.timestamp;

        user.tokenIds.push(_tokenId);
        user.tokenIndex[_tokenId] = user.tokenIds.length.sub(1);
        user.amount = user.amount.add(1);
        totalSupply = totalSupply.add(1);

        parentNFT.transferFrom(msg.sender, address(this), _tokenId);

        emit Stake(msg.sender, _tokenId);
    }

    function unstakeAll() external updateReward updateUserList nonReentrant {
        UserInfo storage user = userInfo[msg.sender];
        uint256[] memory _tokenIds = user.tokenIds;

        for (uint256 i = 0; i < _tokenIds.length; i++) {
            _unstake(_tokenIds[i]);
        }
    }

    function isExistTokenId(uint256 _tokenId) internal view returns (bool) {
        UserInfo storage user = userInfo[msg.sender];
        uint256[] memory _tokenIds = user.tokenIds;

        for (uint256 i = 0; i < _tokenIds.length; i++) {
            if (_tokenIds[i] == _tokenId) return true;
        }
        return false;
    }

    function unstake(uint256[] memory _tokenIds)
        external
        updateReward
        updateUserList
        nonReentrant
    {
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            uint256 _tokenId = _tokenIds[i];
            require(
                isExistTokenId(_tokenId) == true,
                "only owner can unstake!"
            );
            _unstake(_tokenId);
        }
    }

    function _unstake(uint256 _tokenId) internal {
        UserInfo storage user = userInfo[msg.sender];

        require(block.timestamp > user.depositedAt.add(lockupDuration), "You can't unstake yet!");

        user.amount = user.amount.sub(1);
        totalSupply = totalSupply.sub(1);


        uint256 lastIndex = user.tokenIds.length.sub(1);
        uint256 lastIndexKey = user.tokenIds[lastIndex];
        uint256 tokenIdIndex = user.tokenIndex[_tokenId];

        user.tokenIds[tokenIdIndex] = lastIndexKey;
        user.tokenIndex[lastIndexKey] = tokenIdIndex;
        if (user.tokenIds.length > 0) {
            user.tokenIds.pop();
            delete user.tokenIndex[_tokenId];
        }

        parentNFT.transferFrom(address(this), msg.sender, _tokenId);

        emit UnStake(msg.sender, _tokenId);
    }

    function claim() public nonReentrant updateReward updateUserList {
        UserInfo storage user = userInfo[msg.sender];

        require(
            block.timestamp >= user.depositedAt + claimLockDuration,
            "You cannot claim yet!"
        );

        require(
            block.timestamp.sub(user.claimedAt) >= rewardCycle,
            "!available still"
        );

        uint256 claimedAmount = _safeTransferRewards(
            msg.sender,
            user.pendingRewards
        );
        user.pendingRewards = user.pendingRewards.sub(claimedAmount);
        user.claimedAt = block.timestamp;
        totalReward = totalReward.sub(claimedAmount);

        emit Claim(msg.sender, claimedAmount);
    }

    function claimable(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        if (user.amount == 0) return user.pendingRewards;
        uint256 curAccPerShare = accPerShare;

        if (totalSupply > 0) {
            uint256 multiplier = Math.min(block.timestamp, endTime).sub(
                lastUpdateTime
            );
            uint256 reward = multiplier.mul(rewardRate);
            curAccPerShare = accPerShare.add(reward.mul(1e12).div(totalSupply));
        }

        uint256 amount = user.amount;
        return
            amount.mul(curAccPerShare).div(1e12).sub(user.rewardDebt).add(
                user.pendingRewards
            );
    }

    function _safeTransferRewards(address to, uint256 amount)
        internal
        returns (uint256)
    {
        uint256 _bal = rewardToken.balanceOf(address(this));
        require(_bal > 0, "!balance");
        if (amount > totalReward) amount = totalReward;
        if (amount > _bal) amount = _bal;
        uint256 feeAmount = 0;
        feeAmount = amount.mul(claimFee).div(MAX_FEE);
        if (feeAmount > 0) rewardToken.safeTransfer(feeRecipient, feeAmount);
        rewardToken.safeTransfer(to, amount.sub(feeAmount));
        return amount;
    }

    function setRewardRate(uint256 _rewardRate) external onlyOwner {
        require(endTime > block.timestamp, "expired");
        require(_rewardRate > 0, "Rewards per block should be greater than 0!");

        // Update pool infos with old reward rate before setting new one first
        if (totalSupply > 0) {
            uint256 multiplier = block.timestamp.sub(lastUpdateTime);
            uint256 reward = multiplier.mul(rewardRate);
            totalReward = totalReward.add(reward);
            accPerShare = accPerShare.add(reward.mul(1e12).div(totalSupply));
        }
        lastUpdateTime = block.timestamp;
        rewardRate = _rewardRate;
    }

    function setClaimFee(uint256 _fee) external onlyOwner {
        require(_fee < FEE_LIMIT, "invalid fee");

        claimFee = _fee;
    }

    function setFeeRecipient(address _recipient) external onlyOwner {
        feeRecipient = _recipient;
    }

    function _removeUser(address _user) internal {
        if (users.contains(_user) == true) {
            users.remove(_user);
        }
    }

    function _checkOrAddUser(address _user) internal {
        if (users.contains(_user) == false) {
            users.add(_user);
        }
    }

    function userCount() external view returns (uint256) {
        return users.length();
    }

    function userTokenBalanceOf(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        return user.tokenIds.length;
    }

    function tokenOfOwnerByIndex(address owner, uint256 index)
        external
        view
        returns (uint256)
    {
        UserInfo storage user = userInfo[owner];
        return user.tokenIds[index];
    }

    function userList() external view onlyOwner returns (address[] memory) {
        address[] memory list = new address[](users.length());

        for (uint256 i = 0; i < users.length(); i++) {
            list[i] = users.at(i);
        }

        return list;
    }

    function clearUser(address _user) external onlyOwner {
        UserInfo storage user = userInfo[_user];

        uint256[] memory empty;
        totalSupply = totalSupply.sub(user.amount);
        user.amount = 0;
        user.tokenIds = empty;
        user.pendingRewards = 0;
        user.rewardDebt = 0;
        _removeUser(_user);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function emergencyWithdrawToken(address _token, uint256 _amount)
        external
        onlyOwner
    {
        uint256 _bal = IERC20(_token).balanceOf(address(this));
        if (_amount > _bal) _amount = _bal;

        IERC20(_token).safeTransfer(_msgSender(), _amount);
    }

    function emergencyWithdrawNFT(address addr, uint256 _tokenId)
        external
        onlyOwner
    {
        parentNFT.transferFrom(address(this), addr, _tokenId);
    }
}
