contract BoundlessERC20 is ERC20Permit, VaultOwned {

    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    mapping (address => IUniswapV2Router02) public routerForPair; //router address corresponding to pair address
    address[] public pairs;
    address public taxProcessor;
    mapping (address => bool) private _isExcludedFromFee;
    bool transfersEnabled = true;

    constructor(string memory _name, string memory _symbol) ERC20(_name, _symbol, 9) {
      _isExcludedFromFee[msg.sender] = true;
      _isExcludedFromFee[address(this)] = true;
    }

    function setPairs(address[] memory _pairs, address[] memory _routers) external onlyOwner() {
        pairs = _pairs;
        for (uint i = 0; i < pairs.length; i++) {
          routerForPair[pairs[i]] = IUniswapV2Router02(_routers[i]);
        }
    }

    function mint(address account_, uint256 amount_) external onlyVault() {
        _mint(account_, amount_);
    }

    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }

    function burnFrom(address account_, uint256 amount_) public virtual {
        _burnFrom(account_, amount_);
    }

    function _burnFrom(address account_, uint256 amount_) public virtual {
        uint256 decreasedAllowance_ =
            allowance(account_, msg.sender).sub(
                amount_,
                "ERC20: burn amount exceeds allowance"
            );

        _approve(account_, msg.sender, decreasedAllowance_);
        _burn(account_, amount_);
    }

    function excludeFromFee(address account) public onlyOwner() {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner() {
        _isExcludedFromFee[account] = false;
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function _transfer(address sender, address recipient, uint256 amount) internal virtual override {
      require(transfersEnabled, "Transfers are disabled");
      require(sender != address(0), "ERC20: transfer from the zero address");
      require(amount > 0, "ERC20: transfer amount cannot be 0");

      uint256 taxAmount;

      if(!_isExcludedFromFee[sender] && !_isExcludedFromFee[recipient]){
        uint256 transferType = 3;
        if (isDEXPair(sender)) transferType = 1;
        if (isDEXPair(recipient)) transferType = 2;
        taxAmount = ITaxProcessor(taxProcessor).processTaxes(transferType, amount, sender, recipient);
      }

      _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
      _balances[recipient] = _balances[recipient].add(amount.sub(taxAmount));
      _balances[taxProcessor] = _balances[taxProcessor].add(taxAmount);

      if (taxAmount > 0) ITaxProcessor(taxProcessor).distributeTaxes(taxAmount, amount);

      emit Transfer(sender, recipient, amount);
    }

    function setTaxProcessor( address _taxProcessor ) external onlyOwner() {
      taxProcessor = _taxProcessor;
    }

    function setTransfersEnabled( bool _enabled ) external onlyOwner() {
      transfersEnabled = _enabled;
    }

    function isDEXPair(address pair) private returns (bool) {
      for (uint i = 0; i < pairs.length; i++) {
        if (pairs[i] == pair) return true;
      }
      return false;
    }
}
